      const now = new Date();
      const diffMs = now.getTime() - startDate.getTime();
      
      if (diffMs < 0) return '00:00';
      
      const totalMinutes = Math.floor(diffMs / (1000 * 60));
      const hours = Math.floor(totalMinutes / 60);
      const minutes = totalMinutes % 60;
      
      return `${hours}:${minutes.toString().padStart(2, '0')}`;
    } catch (error) {
      console.error('❌ خطأ في حساب المدة:', error);
      return '00:00';
    }
  };
  const formatTime = (dateString) => {
    try {
      if (!dateString) return '--:--';
      const date = new Date(dateString);
      const egyptDate = new Date(date.toLocaleString("en-US", {timeZone: "Africa/Cairo"}));
      const hours = egyptDate.getHours();
      const minutes = egyptDate.getMinutes();
      
      return `${hours}:${minutes.toString().padStart(2, '0')}`;
    } catch {
      return '--:--';
    }
  };
  const formatDate = (dateString) => {
    try {
      if (!dateString) return '--/--/----';
      const date = new Date(dateString);
      const egyptDate = new Date(date.toLocaleString("en-US", {timeZone: "Africa/Cairo"}));
      
      return egyptDate.toLocaleDateString('ar-EG', {
        day: '2-digit',
        month: '2-digit',
        year: 'numeric'
      });
    } catch {
      return '--/--/----';
    }
  };
  const formatDuration = (minutes) => {
    if (!minutes || minutes === 0) return 'وقت مفتوح';
    if (minutes < 60) return `${minutes} دقيقة`;

    const hours = Math.floor(minutes / 60);
    const remainingMinutes = minutes % 60;

    return `${hours} ساعة${
      remainingMinutes > 0 ? ` ${remainingMinutes} دقيقة` : ''
    }`;
  };
