  if (!games || !Array.isArray(games)) return [];
  
  // ⚡ تحديث حالة الألعاب بناءً على activeRentals
  const gamesWithUpdatedStatus = games.map(game => {
    const isRented = activeRentals.some(rental => 
      rental.game_id === game.id || rental.game_name === game.name
    );
    
    return {
      ...game,
      status: isRented ? 'مؤجرة' : (game.status || 'متاح'),
      is_rented_realtime: isRented
    };
  });
  
  let filtered = gamesWithUpdatedStatus.filter(game => {
    if (!game) return false;
    
    // ⚡ التحقق من أن اللعبة نشطة
    const isActive = game.is_active === 1 || game.is_active === true;
    if (!isActive) return false;
    
    // ⚡ التحقق من أن اللعبة متاحة (ليست مؤجرة)
    const isAvailable = game.status === 'متاح';
    if (!isAvailable) return false;
    
    // البحث حسب الاسم
    if (searchTerm && searchTerm.trim() !== '') {
      const gameName = (game.name || '').toLowerCase();
      const searchTermLower = searchTerm.toLowerCase();
      if (!gameName.includes(searchTermLower)) {
        return false;
      }
    }
    
    // البحث حسب الفئة
    if (selectedCategory && selectedCategory.trim() !== '') {
      const gameCategory = game.category || '';
      if (gameCategory !== selectedCategory) {
        return false;
      }
    }
    
    return true;
  });
  
  console.log(`✅ الألعاب المصفاة: ${filtered.length} من ${games.length}`);
  console.log(`📊 الألعاب المؤجرة: ${gamesWithUpdatedStatus.filter(g => g.status === 'مؤجرة').length}`);
  
  return filtered;
}, [games, activeRentals, searchTerm, selectedCategory]);
