          } else {
            showNotification('error', '❌ فشل حتى بعد الإصلاح');
          }
        } else {
          showNotification('error', '❌ فشل إصلاح الجدول');
        }
      } catch (fixError) {
        console.error('❌ خطأ في الإصلاح:', fixError);
