      // إكمال جميع التأجيرات المنتهية
      rentalsToComplete.forEach(async (rental) => {
        await handleAutoCompleteRental(rental);
      });
      
      return true;
    }
    
    return false;
  } catch (error) {
    console.error('❌ خطأ في الإكمال التلقائي:', error);
