const handleAutoCompleteRental = async (rental) => {
  try {
    console.log(`🔄 إكمال التأجير ${rental.rental_number} تلقائياً`);
    
    // تحديث محلي أولاً
    setActiveRentals(prev => prev.filter(r => r.id !== rental.id));
    
    // إضافة إلى المكتملة
    const completedRental = {
      ...rental,
      status: 'مكتمل',
      actual_end_time: new Date().toISOString(),
      actual_duration_minutes: rental.duration_minutes,
      total_amount: rental.total_amount,
      payment_status: 'مدفوع',
      formatted_start_time: formatEgyptDateTime(rental.start_time),
      formatted_end_time: formatEgyptDateTime(new Date().toISOString())
    };
    
    setCompletedRentals(prev => [completedRental, ...prev]);
    
    // تحديث الإحصائيات فوراً
    setStatistics(prev => ({
      ...prev,
      activeRentals: prev.activeRentals - 1,
      todayRentals: prev.todayRentals + 1,
      todayRevenue: prev.todayRevenue + (Number(rental.total_amount) || 0),
      shiftRevenue: prev.shiftRevenue + (Number(rental.total_amount) || 0)
    }));
    
    console.log(`✅ تم إكمال التأجير ${rental.rental_number} محلياً`);
    
    // محاولة التحديث في السيرفر
    try {
      await api.put(`/rentals/${rental.id}/complete-expired`);
      console.log(`✅ تم إكمال التأجير ${rental.rental_number} في السيرفر`);
    } catch (apiError) {
      console.log('⚠️ لا يمكن تحديث السيرفر، تم الإكمال محلياً فقط');
    }
    
  } catch (error) {
    console.error(`🔥 خطأ في إكمال التأجير:`, error);
  }
};

