import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { 
  FaUsers, 
  FaGamepad, 
  FaStore, 
  FaClock, 
  FaMoneyBillWave, 
  FaChartLine,
  FaSync,
  FaPlusCircle,
  FaEye,
  FaEdit,
  FaCheckCircle,
  FaTimesCircle
} from 'react-icons/fa';

import api from '../../services/api';
import authService from '../../services/authService';
import './AdminDashboard.css';

const AdminDashboard = () => {
  const [stats, setStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    activeRentals: 0,
    availableGames: 0,
    totalGames: 0,
    todayRevenue: 0,
    todayRentals: 0,
    totalUsers: 0,
    totalBranches: 0,
    avgRentalValue: 0,
    occupancyRate: 0
  });
  
  const [recentRentals, setRecentRentals] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [topGames, setTopGames] = useState([]);
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [lastUpdate, setLastUpdate] = useState('');

  useEffect(() => {
    const currentUser = authService.getCurrentUser();
    setUser(currentUser);
    loadDashboardData();
    
    // تحديث تلقائي كل 30 ثانية
    const interval = setInterval(loadDashboardData, 30000);
    return () => clearInterval(interval);
  }, []);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      
      // 1. تحميل إحصائيات التأجيرات
      const rentalsStatsResponse = await api.get('/rentals/stats');
      if (rentalsStatsResponse.data.success) {
        const rentalStats = rentalsStatsResponse.data.data;
        setStats(prev => ({
          ...prev,
          totalRevenue: rentalStats.total_revenue || 0,
          totalRentals: rentalStats.total_rentals || 0,
          activeRentals: rentalStats.active_rentals || 0,
          todayRevenue: rentalStats.today_revenue || 0,
          todayRentals: rentalStats.today_rentals || 0,
          avgRentalValue: rentalStats.avg_rental_value || 0
        }));
      }

      // 2. تحميل إحصائيات الألعاب
      const gamesStatsResponse = await api.get('/games/stats');
      if (gamesStatsResponse.data.success) {
        const gameStats = gamesStatsResponse.data.data;
        setStats(prev => ({
          ...prev,
          totalGames: gameStats.total_games || 0,
          availableGames: gameStats.available_games || 0,
          occupancyRate: gameStats.total_games > 0 ? 
            ((gameStats.total_games - gameStats.available_games) / gameStats.total_games * 100) : 0
        }));
      }

      // 3. تحميل إحصائيات النظام
      const systemStatsResponse = await api.get('/admin/stats');
      if (systemStatsResponse.data.success) {
        const systemStats = systemStatsResponse.data.data;
        setStats(prev => ({
          ...prev,
          totalUsers: systemStats.total_users || 0,
          totalBranches: systemStats.total_branches || 0
        }));
      }

      // 4. تحميل آخر التأجيرات
      const recentRentalsResponse = await api.get('/rentals/recent?limit=5');
      if (recentRentalsResponse.data.success) {
        setRecentRentals(recentRentalsResponse.data.data);
      }

      // 5. تحميل التأجيرات النشطة
      const activeRentalsResponse = await api.get('/rentals/active');
      if (activeRentalsResponse.data.success) {
        setActiveRentals(activeRentalsResponse.data.data);
      }

      // 6. تحميل الألعاب الأكثر تأجيراً
      const topGamesResponse = await api.get('/games/top?limit=5');
      if (topGamesResponse.data.success) {
        setTopGames(topGamesResponse.data.data);
      }

      setLastUpdate(new Date().toLocaleTimeString('ar-EG'));
    } catch (error) {
      console.error('Error loading dashboard data:', error);
    } finally {
      setLoading(false);
    }
  };

  const formatCurrency = (amount) => {
    if (typeof amount !== 'number' || isNaN(amount)) return '0.00 ج';
    return amount.toLocaleString('ar-EG', {
      minimumFractionDigits: 2,
      maximumFractionDigits: 2
    }) + ' ج';
  };

  const formatDate = (dateString) => {
    if (!dateString) return '';
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('ar-EG', {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      return dateString;
    }
  };

  const handleRentalAction = async (rentalId, action) => {
    try {
      let response;
      
      switch(action) {
        case 'view':
          window.open(`/admin/rental/${rentalId}`, '_blank');
          return;
          
        case 'end':
          if (window.confirm('هل تريد إنهاء هذا التأجير؟')) {
            response = await api.post(`/rentals/${rentalId}/end`);
          }
          break;
          
        case 'extend':
          const hours = prompt('أدخل عدد الساعات الإضافية:', '1');
          if (hours && !isNaN(hours)) {
            response = await api.post(`/rentals/${rentalId}/extend`, { hours: parseInt(hours) });
          }
          break;
          
        case 'cancel':
          if (window.confirm('هل تريد إلغاء هذا التأجير؟')) {
            const reason = prompt('أدخل سبب الإلغاء:', '');
            if (reason) {
              response = await api.post(`/rentals/${rentalId}/cancel`, { reason });
            }
          }
          break;
      }
      
      if (response?.data.success) {
        alert('تم تنفيذ العملية بنجاح');
        loadDashboardData();
      }
    } catch (error) {
      alert('حدث خطأ أثناء تنفيذ العملية');
      console.error('Error performing rental action:', error);
    }
  };

  if (loading && recentRentals.length === 0) {
    return (
      <div className="dashboard-loading">
        <div className="spinner"></div>
        <p>جاري تحميل بيانات لوحة التحكم...</p>
      </div>
    );
  }

  return (
    <div className="admin-dashboard">
      {/* رأس الصفحة */}
      <div className="dashboard-header">
        <div>
          <h1><FaChartLine /> لوحة تحكم المدير العام</h1>
          <div className="admin-info">
            <span className="admin-name">
              <FaUsers /> مرحباً {user?.name}
            </span>
            <span className="admin-role">({user?.role})</span>
            <span className="admin-branch">الفرع: {user?.branch_name}</span>
            <span className="last-update">آخر تحديث: {lastUpdate}</span>
          </div>
        </div>
        <div className="header-actions">
          <button onClick={loadDashboardData} className="btn-refresh">
            <FaSync /> تحديث البيانات
          </button>
          <Link to="/employee/rentals/new" className="btn-primary">
            <FaPlusCircle /> بدء تأجير جديد
          </Link>
        </div>
      </div>

      {/* الإحصائيات الرئيسية */}
      <div className="stats-grid">
        <div className="stat-card stat-revenue">
          <div className="stat-icon"><FaMoneyBillWave /></div>
          <div className="stat-content">
            <h3>إجمالي الإيرادات</h3>
            <h2>{formatCurrency(stats.totalRevenue)}</h2>
            <div className="stat-trend">
              <span>+{formatCurrency(stats.todayRevenue)} اليوم</span>
            </div>
          </div>
        </div>

        <div className="stat-card stat-rentals">
          <div className="stat-icon"><FaClock /></div>
          <div className="stat-content">
            <h3>إجمالي التأجيرات</h3>
            <h2>{stats.totalRentals.toLocaleString('ar-EG')}</h2>
            <div className="stat-trend">
              <span>+{stats.todayRentals} تأجير اليوم</span>
            </div>
          </div>
        </div>

        <div className="stat-card stat-active">
          <div className="stat-icon"><FaClock /></div>
          <div className="stat-content">
            <h3>التأجيرات النشطة</h3>
            <h2>{stats.activeRentals}</h2>
            <div className="stat-info">
              <span>من أصل {stats.totalRentals}</span>
            </div>
          </div>
        </div>

        <div className="stat-card stat-games">
          <div className="stat-icon"><FaGamepad /></div>
          <div className="stat-content">
            <h3>الألعاب المتاحة</h3>
            <h2>{stats.availableGames}</h2>
            <div className="stat-info">
              <span>من أصل {stats.totalGames}</span>
            </div>
          </div>
        </div>
      </div>

      {/* الصف الثاني: إجراءات سريعة + تأجيرات نشطة */}
      <div className="dashboard-row">
        {/* إجراءات سريعة */}
        <div className="quick-actions-section">
          <div className="section-header">
            <h2><FaBolt /> إجراءات سريعة</h2>
          </div>
          <div className="actions-grid">
            <Link to="/admin/users" className="action-card admin-action">
              <div className="action-icon"><FaUsers /></div>
              <div className="action-content">
                <h3>إدارة المستخدمين</h3>
                <p>إضافة وتعديل المستخدمين</p>
              </div>
            </Link>
            
            <Link to="/admin/games" className="action-card">
              <div className="action-icon"><FaGamepad /></div>
              <div className="action-content">
                <h3>إدارة الألعاب</h3>
                <p>إضافة وتعديل الألعاب</p>
              </div>
            </Link>
            
            <Link to="/admin/branches" className="action-card">
              <div className="action-icon"><FaStore /></div>
              <div className="action-content">
                <h3>إدارة الفروع</h3>
                <p>تعديل بيانات الفروع</p>
              </div>
            </Link>
            
            <Link to="/admin/activities" className="action-card">
              <div className="action-icon"><FaHistory /></div>
              <div className="action-content">
                <h3>سجل الأنشطة</h3>
                <p>عرض سجل أنشطة النظام</p>
              </div>
            </Link>
          </div>
        </div>

        {/* التأجيرات النشطة */}
        <div className="active-rentals-section">
          <div className="section-header">
            <h2><FaClock /> التأجيرات النشطة حالياً</h2>
            <Link to="/employee/rentals" className="btn-view-all">
              عرض الكل →
            </Link>
          </div>
          
          {activeRentals.length === 0 ? (
            <div className="empty-state">
              <p>لا توجد تأجيرات نشطة حالياً</p>
              <Link to="/employee/rentals/new" className="btn-primary">
                بدء أول تأجير
              </Link>
            </div>
          ) : (
            <div className="active-rentals-list">
              {activeRentals.map(rental => (
                <div key={rental.id} className="active-rental-card">
                  <div className="rental-header">
                    <div className="rental-info">
                      <h4>تأجير #{rental.rental_number}</h4>
                      <p className="customer-name">{rental.customer_name}</p>
                      <p className="game-name">{rental.game_name}</p>
                    </div>
                    <div className="rental-time">
                      <div className="time-elapsed">
                        <span className="hours">{rental.hours_elapsed || 0}</span>
                        <span>ساعة</span>
                      </div>
                      <div className="time-remaining">
                        متبقي: {Math.max(0, (rental.duration_hours || 0) - (rental.hours_elapsed || 0))} ساعة
                      </div>
                    </div>
                  </div>
                  
                  <div className="rental-details">
                    <div className="detail-item">
                      <span>الفرع:</span>
                      <span>{rental.branch_name}</span>
                    </div>
                    <div className="detail-item">
                      <span>المبلغ:</span>
                      <span className="amount">{formatCurrency(rental.total_amount)}</span>
                    </div>
                    <div className="detail-item">
                      <span>بدأ في:</span>
                      <span>{formatDate(rental.start_time)}</span>
                    </div>
                  </div>
                  
                  <div className="rental-actions">
                    <button
                      className="btn-action btn-success"
                      onClick={() => handleRentalAction(rental.id, 'end')}
                      title="إنهاء التأجير"
                    >
                      <FaCheckCircle /> إنهاء
                    </button>
                    <button
                      className="btn-action btn-warning"
                      onClick={() => handleRentalAction(rental.id, 'extend')}
                      title="تمديد التأجير"
                    >
                      <FaClock /> تمديد
                    </button>
                    <button
                      className="btn-action btn-danger"
                      onClick={() => handleRentalAction(rental.id, 'cancel')}
                      title="إلغاء التأجير"
                    >
                      <FaTimesCircle /> إلغاء
                    </button>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* الصف الثالث: آخر التأجيرات + الألعاب الأكثر تأجيراً */}
      <div className="dashboard-row">
        {/* آخر التأجيرات */}
        <div className="recent-rentals-section">
          <div className="section-header">
            <h2><FaHistory /> آخر التأجيرات</h2>
            <Link to="/admin/rentals" className="btn-view-all">
              عرض الكل →
            </Link>
          </div>
          
          {recentRentals.length === 0 ? (
            <div className="empty-state">
              <p>لا توجد تأجيرات حديثة</p>
            </div>
          ) : (
            <div className="rentals-table">
              <table>
                <thead>
                  <tr>
                    <th>رقم التأجير</th>
                    <th>العميل</th>
                    <th>اللعبة</th>
                    <th>المبلغ</th>
                    <th>الحالة</th>
                    <th>التاريخ</th>
                    <th>الإجراءات</th>
                  </tr>
                </thead>
                <tbody>
                  {recentRentals.map(rental => (
                    <tr key={rental.id}>
                      <td>
                        <strong>#{rental.rental_number}</strong>
                      </td>
                      <td>
                        <div className="customer-cell">
                          <div>{rental.customer_name}</div>
                          <small>{rental.customer_phone}</small>
                        </div>
                      </td>
                      <td>{rental.game_name}</td>
                      <td className="amount">{formatCurrency(rental.total_amount)}</td>
                      <td>
                        <span className={`status-badge ${rental.status === 'نشط' ? 'active' : 
                                          rental.status === 'مكتمل' ? 'completed' : 'cancelled'}`}>
                          {rental.status}
                        </span>
                      </td>
                      <td>
                        <div className="datetime-cell">
                          {formatDate(rental.created_at)}
                        </div>
                      </td>
                      <td>
                        <div className="action-buttons">
                          <button
                            className="btn-action btn-view"
                            onClick={() => handleRentalAction(rental.id, 'view')}
                            title="عرض التفاصيل"
                          >
                            <FaEye />
                          </button>
                          {rental.status === 'نشط' && (
                            <>
                              <button
                                className="btn-action btn-end"
                                onClick={() => handleRentalAction(rental.id, 'end')}
                                title="إنهاء التأجير"
                              >
                                <FaCheckCircle />
                              </button>
                            </>
                          )}
                        </div>
                      </td>
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          )}
        </div>

        {/* الألعاب الأكثر تأجيراً */}
        <div className="top-games-section">
          <div className="section-header">
            <h2><FaGamepad /> الألعاب الأكثر تأجيراً</h2>
            <Link to="/admin/games" className="btn-view-all">
              إدارة الألعاب →
            </Link>
          </div>
          
          {topGames.length === 0 ? (
            <div className="empty-state">
              <p>لا توجد بيانات عن الألعاب</p>
            </div>
          ) : (
            <div className="top-games-list">
              {topGames.map(game => (
                <div key={game.id} className="top-game-card">
                  <div className="game-rank">#{game.rank}</div>
                  <div className="game-image">
                    <img 
                      src={`/images/${game.image_url || 'default-game.jpg'}`}
                      alt={game.name}
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = "/images/default-game.jpg";
                      }}
                    />
                  </div>
                  <div className="game-info">
                    <h4>{game.name}</h4>
                    <div className="game-meta">
                      <span className="game-category">{game.category}</span>
                      <span className="game-price">{game.price_per_hour} ج/ساعة</span>
                    </div>
                    <div className="game-stats">
                      <div className="stat">
                        <span className="stat-label">التأجيرات:</span>
                        <span className="stat-value">{game.rental_count}</span>
                      </div>
                      <div className="stat">
                        <span className="stat-label">الإيرادات:</span>
                        <span className="stat-value">{formatCurrency(game.total_revenue)}</span>
                      </div>
                    </div>
                    <div className="game-branch">
                      <small>الفرع: {game.branch_name}</small>
                    </div>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </div>

      {/* إحصائيات متقدمة */}
      <div className="advanced-stats">
        <div className="section-header">
          <h2><FaChartLine /> إحصائيات متقدمة</h2>
        </div>
        <div className="advanced-stats-grid">
          <div className="stat-item">
            <div className="stat-label">متوسط قيمة التأجير</div>
            <div className="stat-value">{formatCurrency(stats.avgRentalValue)}</div>
          </div>
          <div className="stat-item">
            <div className="stat-label">نسبة الإشغال</div>
            <div className="stat-value">{Math.round(stats.occupancyRate)}%</div>
          </div>
          <div className="stat-item">
            <div className="stat-label">عدد المستخدمين</div>
            <div className="stat-value">{stats.totalUsers}</div>
          </div>
          <div className="stat-item">
            <div className="stat-label">عدد الفروع</div>
            <div className="stat-value">{stats.totalBranches}</div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
