// frontend/src/pages/employee/Dashboard.jsx
import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import api from '../../services/api';
import authService from '../../services/authService';
import './EmployeeDashboard.css';
import {
  Gamepad2, Zap, DollarSign, Clock, Users,
  CheckCircle, AlertCircle, RefreshCw, Building,
  Loader2, Calendar, TrendingUp, User,
  PowerOff, LogOut, Bell, History,
  Package, BarChart3, Activity
} from 'lucide-react';

const EmployeeDashboard = () => {
  const [dashboardData, setDashboardData] = useState({
    // ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ©
    availableGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0,
    completedRentals: 0,
    
    // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™
    currentShift: null,
    shiftEmployees: [],
    
    // ÿ®ŸäÿßŸÜÿßÿ™ ÿ•ÿ∂ÿßŸÅŸäÿ©
    branchInfo: null,
    rentalStats: {
      fixedTime: 0,
      openTime: 0,
      totalAmount: 0
    }
  });
  
  const [availableGames, setAvailableGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [user, setUser] = useState(null);
  const [branch, setBranch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [refreshInterval, setRefreshInterval] = useState(null);
  const [shiftEnded, setShiftEnded] = useState(false);
  const [lastShiftInfo, setLastShiftInfo] = useState(null);
  
  // ÿØÿßŸÑÿ© ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑÿπŸÖŸÑÿ©
  const formatCurrency = (amount) => {
    if (!amount && amount !== 0) return '0 ÿ¨.ŸÖ';
    const num = parseFloat(amount) || 0;
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0,
      maximumFractionDigits: 2
    }).format(num);
  };
  
  // ÿØÿßŸÑÿ© ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑŸàŸÇÿ™
  const formatTime = (dateString) => {
    if (!dateString) return '--:--';
    try {
      const date = new Date(dateString);
      return date.toLocaleTimeString('ar-EG', {
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '--:--';
    }
  };
  
  // ÿØÿßŸÑÿ© ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑÿ™ÿßÿ±ŸäÿÆ ŸàÿßŸÑŸàŸÇÿ™
  const formatDateTime = (dateString) => {
    if (!dateString) return '--/--/---- --:--';
    try {
      const date = new Date(dateString);
      return date.toLocaleDateString('ar-EG', {
        year: 'numeric',
        month: '2-digit',
        day: '2-digit',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch {
      return '--/--/---- --:--';
    }
  };
  
// ŸÅŸä Dashboard.jsxÿå ŸÇŸÖ ÿ®ÿ™ÿ≠ÿØŸäÿ´ Ÿáÿ∞Ÿá ÿßŸÑÿØŸàÿßŸÑ:

// 1. ÿ™ÿ≠ÿØŸäÿ´ ÿØÿßŸÑÿ© ÿ¨ŸÑÿ® ŸÖŸàÿ∏ŸÅŸä ÿßŸÑÿ¥ŸäŸÅÿ™
const loadShiftEmployees = async (shiftId) => {
  try {
    if (!shiftId) return [];
    
    console.log(`üë• ÿ¨ŸÑÿ® ŸÖŸàÿ∏ŸÅŸä ÿßŸÑÿ¥ŸäŸÅÿ™ ${shiftId}`);
    
    // ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© 1: ÿßŸÑŸÜŸÇÿ∑ÿ© ÿßŸÑÿ¨ÿØŸäÿØÿ©
    try {
      const response = await api.get(`/shifts/${shiftId}/employees`);
      if (response.success) {
        return response.data || [];
      }
    } catch (error) {
      console.warn('‚ö†Ô∏è ŸÅÿ¥ŸÑÿ™ ÿßŸÑŸÜŸÇÿ∑ÿ© ÿßŸÑÿ£ŸàŸÑŸâ:', error.message);
    }
    
    // ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© 2: ÿßŸÑŸÜŸÇÿ∑ÿ© ÿßŸÑÿ®ÿØŸäŸÑÿ©
    try {
      const response = await api.get('/shifts/current-employees', {
        params: { shift_id: shiftId }
      });
      if (response.success) {
        return response.data || [];
      }
    } catch (error) {
      console.warn('‚ö†Ô∏è ŸÅÿ¥ŸÑÿ™ ÿßŸÑŸÜŸÇÿ∑ÿ© ÿßŸÑÿ´ÿßŸÜŸäÿ©:', error.message);
    }
    
    // ÿ•ÿ∞ÿß ŸÅÿ¥ŸÑ ŸÉŸÑ ÿ¥Ÿäÿ°ÿå ÿ•ÿ±ÿ¨ÿßÿπ ŸÇÿßÿ¶ŸÖÿ© ÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©
    return currentShift ? [
      {
        id: currentShift.employee_id || currentShift.user_id,
        name: currentShift.employee_name || user?.name,
        role: user?.role || 'employee',
        total_rentals: 0
      }
    ] : [];
    
  } catch (error) {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ŸÖŸàÿ∏ŸÅŸä ÿßŸÑÿ¥ŸäŸÅÿ™:', error);
    return [];
  }
};

// 2. ÿ™ÿ≠ÿØŸäÿ´ ÿØÿßŸÑÿ© ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©
const loadCompletedRentals = async () => {
  try {
    if (!currentShift?.id) return [];
    
    console.log('üìã ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© ŸÑŸÑÿ¥ŸäŸÅÿ™', currentShift.id);
    
    // ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© 1: ÿßŸÑŸÜŸÇÿ∑ÿ© ÿßŸÑÿ¨ÿØŸäÿØÿ©
    try {
      const response = await api.get('/rentals/completed-only', {
        params: {
          shift_id: currentShift.id,
          branch_id: user?.branch_id
        }
      });
      
      if (response.success) {
        return response.data || [];
      }
    } catch (error) {
      console.warn('‚ö†Ô∏è ŸÅÿ¥ŸÑ /rentals/completed-only:', error.message);
    }
    
    // ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© 2: ÿßŸÑŸÜŸÇÿ∑ÿ© ÿßŸÑÿ®ÿØŸäŸÑÿ©
    try {
      const response = await api.get('/rentals/completed', {
        params: {
          shift_id: currentShift.id,
          status: 'ŸÖŸÉÿ™ŸÖŸÑ'
        }
      });
      
      if (response.success) {
        return response.data || [];
      }
    } catch (error) {
      console.warn('‚ö†Ô∏è ŸÅÿ¥ŸÑ /rentals/completed:', error.message);
    }
    
    // ÿßŸÑŸÖÿ≠ÿßŸàŸÑÿ© 3: ÿßŸÑÿ™ÿµŸÅŸäÿ© ŸÖŸÜ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™
    try {
      const response = await api.get('/rentals', {
        params: {
          shift_id: currentShift.id,
          branch_id: user?.branch_id,
          status: 'ŸÖŸÉÿ™ŸÖŸÑ'
        }
      });
      
      if (response.success) {
        return response.data || [];
      }
    } catch (error) {
      console.warn('‚ö†Ô∏è ŸÅÿ¥ŸÑ /rentals:', error.message);
    }
    
    return [];
    
  } catch (error) {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©:', error);
    return [];
  }
};

  const loadDashboardData = useCallback(async () => {
    try {
      const currentUser = authService.getCurrentUser();
      setUser(currentUser);
      
      if (!currentUser?.branch_id) {
        console.warn('‚ö†Ô∏è ŸÑŸÖ Ÿäÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿØ ŸÅÿ±ÿπ ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ');
        setLoading(false);
        return;
      }
      
      setLoading(true);
      
      // 1. ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÅÿ±ÿπ
      try {
        const branchResponse = await api.get('/branches');
        if (branchResponse.data.success && currentUser.branch_id) {
          const userBranch = branchResponse.data.data.find(b => b.id === currentUser.branch_id);
          setBranch(userBranch);
          setDashboardData(prev => ({
            ...prev,
            branchInfo: userBranch
          }));
        }
      } catch (error) {
        console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÅÿ±ÿπ:', error);
      }
      
      // 2. ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸä (ŸÖÿ®ÿßÿ¥ÿ±ÿ© ŸÖŸÜ API)
      try {
        const shiftResponse = await api.get('/shifts/simple');
        if (shiftResponse.success && shiftResponse.data) {
          // ÿ™ÿ≠ŸÇŸÇ ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑÿ¥ŸäŸÅÿ™ ŸÖÿÆÿ™ŸÑŸÅÿßŸã ÿπŸÜ ÿßŸÑÿ≠ÿßŸÑŸä
          const currentShiftId = dashboardData.currentShift?.id;
          const newShiftId = shiftResponse.data.id;
          
          if (currentShiftId && !newShiftId) {
            // ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ŸÅŸä ÿµŸÅÿ≠ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±
            handleShiftEndInRentals();
          }
          
          setDashboardData(prev => ({
            ...prev,
            currentShift: shiftResponse.data
          }));
          
          // 3. ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖŸàÿ∏ŸÅŸäŸÜ ŸÅŸä ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸä
          try {
            const employeesResponse = await api.get('/shifts/current-employees', {
              params: { shift_id: shiftResponse.data.id }
            });
            if (employeesResponse.success) {
              setDashboardData(prev => ({
                ...prev,
                shiftEmployees: employeesResponse.data || []
              }));
            }
          } catch (empError) {
            console.warn('‚ö†Ô∏è ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖŸàÿ∏ŸÅŸäŸÜ:', empError);
          }
        } else {
          // ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑
          setDashboardData(prev => ({
            ...prev,
            currentShift: null,
            shiftEmployees: []
          }));
        }
      } catch (shiftError) {
        console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™:', shiftError);
        setDashboardData(prev => ({
          ...prev,
          currentShift: null,
          shiftEmployees: []
        }));
      }
      
      // 4. ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© (ŸÑŸÑŸÅÿ±ÿπ ÿßŸÑÿ≠ÿßŸÑŸä ŸÅŸÇÿ∑)
      try {
        const gamesResponse = await api.loadGamesSmart(currentUser.branch_id);
        if (gamesResponse.success) {
          const availableGames = gamesResponse.data.filter(game => 
            game.status === 'ŸÖÿ™ÿßÿ≠' && game.branch_id === currentUser.branch_id
          );
          setAvailableGames(availableGames);
          setDashboardData(prev => ({
            ...prev,
            availableGames: availableGames.length
          }));
        }
      } catch (gamesError) {
        console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®:', gamesError);
      }
      
      // 5. ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ© (ŸÑŸÑŸÅÿ±ÿπ ÿßŸÑÿ≠ÿßŸÑŸä)
      try {
        const activeRentalsResponse = await api.get('/rentals/active', {
          params: { branch_id: currentUser.branch_id }
        });
        
        let activeRentals = [];
        if (activeRentalsResponse.success) {
          activeRentals = activeRentalsResponse.data || [];
        } else {
          // ŸÖÿ≠ÿßŸàŸÑÿ© ÿ®ÿØŸäŸÑÿ©
          const allRentalsResponse = await api.get('/rentals', {
            params: { branch_id: currentUser.branch_id, limit: 100 }
          });
          if (allRentalsResponse.success) {
            activeRentals = allRentalsResponse.data.filter(rental => 
              rental.status === 'ŸÜÿ¥ÿ∑' || rental.status === 'active'
            );
          }
        }
        
        setActiveRentals(activeRentals);
        
        // ÿ™ÿ≠ŸÑŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
        const rentalStats = {
          fixedTime: 0,
          openTime: 0,
          totalAmount: 0
        };
        
        activeRentals.forEach(rental => {
          const isOpenTime = rental.rental_type === 'open' || rental.is_open_time === 1;
          if (isOpenTime) {
            rentalStats.openTime++;
          } else {
            rentalStats.fixedTime++;
          }
          
          // ÿ≠ÿ≥ÿßÿ® ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÖÿ≥ÿ™ÿ≠ŸÇ
          if (isOpenTime) {
            if (rental.start_time) {
              const startTime = new Date(rental.start_time);
              const now = new Date();
              const elapsedMinutes = Math.floor((now - startTime) / 60000);
              const minMinutes = Math.max(elapsedMinutes, 15);
              const units = Math.ceil(minMinutes / 15);
              const pricePer15Min = rental.price_per_15min || 100;
              rentalStats.totalAmount += units * pricePer15Min;
            }
          } else {
            rentalStats.totalAmount += parseFloat(rental.paid_amount || rental.total_amount || 0);
          }
        });
        
        setDashboardData(prev => ({
          ...prev,
          activeRentals: activeRentals.length,
          rentalStats: rentalStats
        }));
      } catch (rentalsError) {
        console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©:', rentalsError);
      }
      
      // 6. ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© ÿßŸÑŸäŸàŸÖ (ŸÑŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸä)
      try {
        const shiftId = dashboardData.currentShift?.id;
        if (shiftId) {
          const completedResponse = await api.get('/rentals/completed-only', {
            params: { 
              shift_id: shiftId,
              branch_id: currentUser.branch_id 
            }
          });
          
          let completedRentalsToday = [];
          let todayRevenue = 0;
          
          if (completedResponse.success && completedResponse.data) {
            completedRentalsToday = Array.isArray(completedResponse.data) ? completedResponse.data : [];
            
            // ÿ≠ÿ≥ÿßÿ® ÿ•Ÿäÿ±ÿßÿØ ÿßŸÑŸäŸàŸÖ ŸÖŸÜ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©
            completedRentalsToday.forEach(rental => {
              if (rental.rental_type === 'open' || rental.is_open_time === 1) {
                todayRevenue += parseFloat(rental.final_amount || 0);
              } else {
                todayRevenue += parseFloat(rental.paid_amount || rental.total_amount || 0);
              }
            });
          }
          
          setCompletedRentals(completedRentalsToday);
          setDashboardData(prev => ({
            ...prev,
            completedRentals: completedRentalsToday.length,
            todayRevenue: todayRevenue,
            todayRentals: completedRentalsToday.length
          }));
        } else {
          // ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ÿå ÿ•ÿπÿßÿØÿ© ÿ™ÿπŸäŸäŸÜ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
          setDashboardData(prev => ({
            ...prev,
            completedRentals: 0,
            todayRevenue: 0,
            todayRentals: 0
          }));
        }
      } catch (completedError) {
