// frontend/src/pages/employee/Rentals.jsx
import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  const { user } = useAuth();
  const [loading, setLoading] = useState(true);
  const [currentShift, setCurrentShift] = useState(null);
  const [shiftStats, setShiftStats] = useState({
    total_rentals: 0,
    total_revenue: 0,
    unique_customers: 0,
    active_rentals: 0
  });
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ŸÑÿπÿßÿ®
  const [availableGames, setAvailableGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  
  // ÿ™ÿ£ÿ¨Ÿäÿ± ŸÖÿ™ÿπÿØÿØ
  const [selectedGames, setSelectedGames] = useState([]);
  const [quantities, setQuantities] = useState({});
  const [showRentalForm, setShowRentalForm] = useState(false);
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÜŸÖŸàÿ∞ÿ¨
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(60);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [discount, setDiscount] = useState(0);
  
  // ÿ≠ÿßŸÑÿ© ŸÑÿπÿ±ÿ∂ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™ ŸÅŸä modal
  const [showShiftStatsModal, setShowShiftStatsModal] = useState(false);
  const [detailedShiftStats, setDetailedShiftStats] = useState(null);
  
  // ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿ™ŸàŸÇŸäÿ™ ÿßŸÑŸÅÿ±ÿπ
  const [branchTiming, setBranchTiming] = useState(null);
  const [isBranchOpen, setIsBranchOpen] = useState(true);
  const [timeUntilClosing, setTimeUntilClosing] = useState('');
  
  const timersRef = useRef({});
  const branchTimingIntervalRef = useRef(null);

  // ÿ™ÿ£ÿ´Ÿäÿ±ÿßÿ™
  useEffect(() => {
    if (user) {
      initializeShift();
      loadBranchTiming();
      
      const interval = setInterval(() => {
        if (currentShift) {
          loadData();
        }
      }, 30000);
      
      // ÿ™ÿ≠ÿØŸäÿ´ ÿ™ŸàŸÇŸäÿ™ ÿßŸÑŸÅÿ±ÿπ ŸÉŸÑ ÿØŸÇŸäŸÇÿ©
      branchTimingIntervalRef.current = setInterval(() => {
        loadBranchTiming();
      }, 60000);
      
      return () => {
        clearInterval(interval);
        clearInterval(branchTimingIntervalRef.current);
        Object.values(timersRef.current).forEach(timer => clearInterval(timer));
      };
    }
  }, [user, currentShift]);

  // ==================== BRANCH TIMING ====================

  const loadBranchTiming = async () => {
    try {
      if (!user?.branch_id) return;
      
      const response = await api.get('/api/branches/current/timing');
      
      if (response.data && response.data.success) {
        const timingData = response.data.data;
        setBranchTiming(timingData);
        setIsBranchOpen(timingData.is_open);
        
        // ÿ≠ÿ≥ÿßÿ® ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖÿ™ÿ®ŸÇŸä ŸÑŸÑÿ•ÿ∫ŸÑÿßŸÇ
        if (timingData.is_open) {
          const currentTime = new Date();
          const [closingHour, closingMinute] = timingData.closing_time_formatted.split(':').map(Number);
          const closingTime = new Date(currentTime);
          closingTime.setHours(closingHour, closingMinute, 0, 0);
          
          const diffMs = closingTime - currentTime;
          const diffMinutes = Math.floor(diffMs / 60000);
          
          if (diffMinutes > 0) {
            const hours = Math.floor(diffMinutes / 60);
            const minutes = diffMinutes % 60;
            
            if (hours > 0) {
              setTimeUntilClosing(`ŸÖÿ™ÿ®ŸÇŸä ${hours} ÿ≥ÿßÿπÿ©${minutes > 0 ? ` Ÿà${minutes} ÿØŸÇŸäŸÇÿ©` : ''} ŸÑŸÑÿ•ÿ∫ŸÑÿßŸÇ`);
            } else {
              setTimeUntilClosing(`ŸÖÿ™ÿ®ŸÇŸä ${minutes} ÿØŸÇŸäŸÇÿ© ŸÑŸÑÿ•ÿ∫ŸÑÿßŸÇ`);
            }
          } else {
            setTimeUntilClosing('ÿßŸÑŸÅÿ±ÿπ ÿ≥Ÿäÿ∫ŸÑŸÇ ŸÇÿ±Ÿäÿ®ÿßŸã');
          }
        } else {
          setTimeUntilClosing('ÿßŸÑŸÅÿ±ÿπ ŸÖÿ∫ŸÑŸÇ ÿ≠ÿßŸÑŸäÿßŸã');
        }
      }
    } catch (error) {
      console.error('Error loading branch timing:', error);
    }
  };

  // ==================== SHIFT MANAGEMENT ====================

