      return false;
    }
    
    return true;
  };

  const resetForm = () => {
    setCustomerName('');
    setCustomerPhone('');
    setCustomerNotes('');
    setSelectedGames([]);
    setQuantities({});
    setDurationType('fixed');
    setSelectedDuration(60);
    setPaymentMethod('cash');
    setDiscount(0);
  };

  const calculateTotalPrice = () => {
    let total = 0;
    
    selectedGames.forEach(game => {
      const quantity = quantities[game.id] || 1;
      let gamePrice = 0;
      
      if (selectedDuration >= 60) {
        gamePrice = (game.price_per_hour || 0) * (selectedDuration / 60);
      } else {
        const quarters = Math.ceil(selectedDuration / 15);
        gamePrice = (game.price_per_15min || 0) * quarters;
      }
      
      total += gamePrice * quantity;
    });
    
    return total * (1 - discount / 100);
  };

  const showNotification = (type, message) => {
    // ÿ™ŸÜŸÅŸäÿ∞ ÿßŸÑÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ŸÉŸÖÿß ŸáŸä ŸÖŸàÿ¨ŸàÿØÿ©
    console.log(`${type === 'error' ? '‚ùå' : '‚úÖ'} ${message}`);
    alert(message); // ŸäŸÖŸÉŸÜ ÿßÿ≥ÿ™ÿ®ÿØÿßŸÑŸáÿß ÿ®ŸÜÿ∏ÿßŸÖ ÿ•ÿ¥ÿπÿßÿ±ÿßÿ™ ÿ£ŸÅÿ∂ŸÑ
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0
    }).format(amount || 0);
  };

  const formatDuration = (minutes) => {
    if (!minutes || minutes === 0) return 'ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠';
    if (minutes < 60) return `${minutes} ÿØŸÇŸäŸÇÿ©`;
    
    const hours = Math.floor(minutes / 60);
    const remaining = minutes % 60;
    return `${hours} ÿ≥ÿßÿπÿ© ${remaining > 0 ? remaining + ' ÿØŸÇŸäŸÇÿ©' : ''}`;
  };

  const formatDateTime = (dateString) => {
    try {
      const date = new Date(dateString);
      return date.toLocaleString('ar-EG', {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      return 'ÿ™ÿßÿ±ŸäÿÆ ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ';
    }
  };

  const handleApiError = (error, context) => {
    console.error(`üî• ÿÆÿ∑ÿ£ ŸÅŸä ${context}:`, error);
    
    if (error.response) {
      switch (error.response.status) {
        case 400:
          return 'ÿ∑ŸÑÿ® ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ - ÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™';
        case 401:
          return 'ÿßŸÜÿ™Ÿáÿ™ ÿßŸÑÿ¨ŸÑÿ≥ÿ© - Ÿäÿ±ÿ¨Ÿâ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ';
        case 403:
          return 'ÿ∫Ÿäÿ± ŸÖÿµÿ±ÿ≠ ŸÑŸÉ ÿ®Ÿáÿ∞ÿß ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°';
        case 404:
          return 'ÿßŸÑŸÖŸàÿ±ÿØ ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ';
        case 500:
          return 'ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿÆÿßÿØŸÖ - ÿ™ŸàÿßÿµŸÑ ŸÖÿπ ÿßŸÑÿØÿπŸÖ ÿßŸÑŸÅŸÜŸä';
        default:
          return `ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ∫Ÿäÿ± ŸÖÿ™ŸàŸÇÿπ (${error.response.status})`;
      }
    } else if (error.request) {
      return 'ŸÅÿ¥ŸÑ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ - ÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßÿ™ÿµÿßŸÑŸÉ ÿ®ÿßŸÑÿ•ŸÜÿ™ÿ±ŸÜÿ™';
    } else {
