    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...</p>
      </div>
    );
  }

  return (
    <div className="rentals-page">
      {/* üîß ŸÇÿ≥ŸÖ ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑŸÅÿ±ÿπ */}
      <div className="branch-info-section">
        <div className="branch-timing-card">
          <div className="timing-header">
            <h3>üè¨ ŸÖÿπŸÑŸàŸÖÿßÿ™ ÿßŸÑŸÅÿ±ÿπ</h3>
            <span className={`status-badge ${isBranchOpen ? 'open' : 'closed'}`}>
              {isBranchOpen ? 'üü¢ ŸÖŸÅÿ™Ÿàÿ≠' : 'üî¥ ŸÖÿ∫ŸÑŸÇ'}
