                            <button 
                              className="quantity-btn"
                              onClick={() => removeGameFromRental(game.id)}
                              disabled={(quantities[game.id] || 1) <= 1}
                            >
                              -
                            </button>
                            <span className="quantity">{quantities[game.id] || 1}</span>
                            <button 
                              className="quantity-btn"
                              onClick={() => addGameToRental(game)}
                              disabled={(quantities[game.id] || 1) >= Math.min(game.quantity || 1, 5)}
                            >
                              +
                            </button>
                          </div>
                        </div>
                      ))}
                    </div>
                  </div>
                  
                  {/* üîò ÿ£ÿ≤ÿ±ÿßÿ± ÿßŸÑÿ•ÿ¨ÿ±ÿßÿ°ÿßÿ™ */}
                  <div className="modal-actions">
                    <button 
                      className="btn-start"
                      onClick={handleStartRental}
                      disabled={!currentShift || selectedGames.length === 0}
                    >
                      {!currentShift ? 'ÿßÿ®ÿØÿ£ ÿ¥ŸäŸÅÿ™ ÿ£ŸàŸÑÿßŸã' : 'üöÄ ÿ®ÿØÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±'}
                    </button>
                    <button 
                      className="btn-cancel"
                      onClick={() => {
                        setShowRentalForm(false);
                        resetForm();
                      }}
                    >
                      ÿ•ŸÑÿ∫ÿßÿ°
                    </button>
                  </div>
                </>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Rentals;
