import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import authService from '../../services/authService';
import './AdminPages.css';

const AdminDashboard = () => {
  const { user } = useAuth();
  const [stats, setStats] = useState({
    totalBranches: 0,
    totalUsers: 0,
    totalGames: 0,
    availableGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0,
    todayCompletedRentals: 0
  });
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [recentRentals, setRecentRentals] = useState([]);
  const [branchStats, setBranchStats] = useState([]);
  const [refreshKey, setRefreshKey] = useState(0);

  useEffect(() => {
    loadDashboardData();
  }, [refreshKey]);

  const loadDashboardData = async () => {
    try {
      setLoading(true);
      setError('');
      
      // جلب جميع البيانات بشكل متوازٍ
      const [
        statsResponse,
        rentalsResponse,
        branchesResponse,
        usersResponse,
        gamesResponse
      ] = await Promise.allSettled([
        authService.getSimpleStats(),
        authService.getRentals({ limit: 10 }),
        authService.getBranches(),
        authService.getUsers(),
        authService.getGames()
      ]);

      let newStats = {
        totalBranches: 0,
        totalUsers: 0,
        totalGames: 0,
        availableGames: 0,
        activeRentals: 0,
        todayRevenue: 0,
        todayRentals: 0,
        todayCompletedRentals: 0
      };

      // معالجة إحصائيات لوحة التحكم
      if (statsResponse.status === 'fulfilled' && statsResponse.value.success) {
        const statsData = statsResponse.value.data;
        newStats = {
          ...newStats,
          availableGames: statsData.available_games || 0,
          totalGames: statsData.total_games || 0,
          todayRevenue: statsData.today_revenue || 0,
          activeRentals: statsData.active_rentals || 0,
          todayRentals: statsData.today_rentals || 0
        };
      }

      // معالجة الفروع
      if (branchesResponse.status === 'fulfilled' && branchesResponse.value.success) {
        newStats.totalBranches = branchesResponse.value.data.length || 0;
        
        // حساب إحصائيات الفروع
        const branchStatsPromises = branchesResponse.value.data.map(async (branch) => {
          try {
            const [rentalsRes, gamesRes] = await Promise.allSettled([
              authService.getRentals({ 
                branch_id: branch.id, 
                status: 'مكتمل',
                limit: 1 
              }),
              authService.getBranchGames(branch.id)
            ]);
            
            let branchRevenue = 0;
            let branchRentals = 0;
            let branchGames = 0;
            
            if (rentalsRes.status === 'fulfilled' && rentalsRes.value.success) {
              const rentals = rentalsRes.value.data || [];
              branchRentals = rentals.length;
              branchRevenue = rentals.reduce((sum, rental) => sum + (rental.total_amount || 0), 0);
            }
            
            if (gamesRes.status === 'fulfilled' && gamesRes.value.success) {
              branchGames = gamesRes.value.data.length || 0;
            }
            
            return {
              id: branch.id,
              name: branch.name,
              location: branch.location,
              revenue: branchRevenue,
              rentals: branchRentals,
              games: branchGames,
              status: 'active'
            };
          } catch (error) {
            console.error(`Error loading stats for branch ${branch.id}:`, error);
            return null;
          }
        });
        
        const branchStatsResults = await Promise.all(branchStatsPromises);
        setBranchStats(branchStatsResults.filter(Boolean));
      }

      // معالجة المستخدمين
      if (usersResponse.status === 'fulfilled' && usersResponse.value.success) {
        newStats.totalUsers = usersResponse.value.data.length || 0;
      }

      // معالجة الألعاب (إذا لم تكن محملة بالفعل)
      if (gamesResponse.status === 'fulfilled' && gamesResponse.value.success) {
        const games = gamesResponse.value.data || [];
        if (newStats.totalGames === 0) {
          newStats.totalGames = games.length;
          newStats.availableGames = games.filter(game => game.status === 'متاح').length;
        }
      }

      // معالجة التأجيرات الأخيرة
      if (rentalsResponse.status === 'fulfilled' && rentalsResponse.value.success) {
        const rentals = rentalsResponse.value.data || [];
        setRecentRentals(rentals.slice(0, 5));
        
        // حساب التأجيرات المكتملة اليوم
        const today = new Date().toISOString().split('T')[0];
        const todayCompleted = rentals.filter(rental => 
          rental.status === 'مكتمل' && 
          rental.created_at?.startsWith(today)
        );
        newStats.todayCompletedRentals = todayCompleted.length;
      }

      setStats(newStats);

    } catch (error) {
      console.error('❌ خطأ في تحميل بيانات لوحة التحكم:', error);
      setError('تعذر تحميل بيانات لوحة التحكم. يرجى المحاولة مرة أخرى.');
    } finally {
      setLoading(false);
    }
  };

  const handleRefresh = () => {
    setRefreshKey(prev => prev + 1);
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0
    }).format(amount || 0);
  };

  const formatTime = (timeString) => {
    if (!timeString) return '';
    const date = new Date(timeString);
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>جاري تحميل لوحة التحكم...</p>
      </div>
    );
  }

  return (
    <div className="admin-page">
      {/* رأس الصفحة مع أزرار التحكم */}
      <div className="page-header-with-controls">
        <div className="header-left">
          <h1>📊 لوحة تحكم المدير</h1>
          <p className="welcome-message">
            <span className="greeting">مرحباً بك،</span>
            <span className="user-name">{user?.name}</span>
            <span className="user-role">({user?.role === 'admin' ? 'مدير النظام' : user?.role === 'branch_manager' ? 'مدير فرع' : 'موظف'})</span>
          </p>
          <div className="date-time-display">
            <span className="date">{new Date().toLocaleDateString('ar-EG', { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' })}</span>
            <span className="time">{new Date().toLocaleTimeString('ar-EG', { hour: '2-digit', minute: '2-digit' })}</span>
          </div>
        </div>
        <div className="header-right">
          <button className="btn-refresh" onClick={handleRefresh}>
            🔄 تحديث البيانات
          </button>
          <button 
            className="btn-export"
            onClick={() => window.print()}
          >
            📄 تصدير تقرير
          </button>
        </div>
      </div>

      {error && (
        <div className="alert alert-error">
          <span>⚠️ {error}</span>
          <button onClick={handleRefresh}>إعادة المحاولة</button>
        </div>
      )}

      {/* ملخص اليوم السريع */}
      <div className="daily-summary">
        <h2>
          📈 ملخص اليوم 
          <span className="today-date">
            {new Date().toLocaleDateString('ar-EG', { day: 'numeric', month: 'long' })}
          </span>
        </h2>
        <div className="summary-grid">
          <div className="summary-item revenue">
            <div className="summary-icon">💰</div>
            <div className="summary-content">
              <span className="summary-label">إيرادات اليوم</span>
              <span className="summary-value">{formatCurrency(stats.todayRevenue)}</span>
            </div>
          </div>
          <div className="summary-item rentals">
            <div className="summary-icon">📋</div>
            <div className="summary-content">
              <span className="summary-label">التأجيرات اليومية</span>
              <span className="summary-value">{stats.todayRentals}</span>
            </div>
          </div>
          <div className="summary-item active">
            <div className="summary-icon">⏱️</div>
            <div className="summary-content">
              <span className="summary-label">التأجيرات النشطة</span>
              <span className="summary-value">{stats.activeRentals}</span>
            </div>
          </div>
          <div className="summary-item completed">
            <div className="summary-icon">✅</div>
            <div className="summary-content">
              <span className="summary-label">المكتملة اليوم</span>
              <span className="summary-value">{stats.todayCompletedRentals}</span>
            </div>
          </div>
        </div>
      </div>

      {/* إحصائيات سريعة */}
      <div className="stats-grid">
        <div className="stat-card primary">
          <div className="stat-icon">🏬</div>
          <div className="stat-content">
            <h3>الفروع</h3>
            <p className="stat-value">{stats.totalBranches}</p>
            <p className="stat-subtext">فرع نشط</p>
          </div>
        </div>

        <div className="stat-card success">
          <div className="stat-icon">👥</div>
          <div className="stat-content">
            <h3>المستخدمون</h3>
            <p className="stat-value">{stats.totalUsers}</p>
            <p className="stat-subtext">مستخدم نشط</p>
          </div>
        </div>

        <div className="stat-card info">
          <div className="stat-icon">🎮</div>
          <div className="stat-content">
            <h3>الألعاب</h3>
            <p className="stat-value">{stats.totalGames}</p>
            <p className="stat-subtext">
              <span className="available">{stats.availableGames} متاح</span>
              <span className="separator"> | </span>
              <span className="rented">{stats.totalGames - stats.availableGames} مؤجر</span>
            </p>
          </div>
        </div>

        <div className="stat-card warning">
          <div className="stat-icon">⏱️</div>
          <div className="stat-content">
            <h3>التأجيرات النشطة</h3>
            <p className="stat-value">{stats.activeRentals}</p>
            <p className="stat-subtext">تحت التشغيل الآن</p>
          </div>
        </div>
      </div>

      {/* قسمين متجاورين */}
      <div className="dashboard-sections">
        {/* إجراءات سريعة */}
        <div className="section quick-actions">
          <div className="section-header">
            <h2>🔄 إجراءات سريعة</h2>
            <span className="section-badge">الوصول السريع</span>
          </div>
          <div className="actions-grid">
            <button 
              className="action-btn primary"
              onClick={() => window.location.href = '/admin/users'}
            >
              <span className="action-icon">👥</span>
              <span className="action-content">
                <span className="action-label">إدارة المستخدمين</span>
                <span className="action-desc">إضافة/تعديل/حذف المستخدمين</span>
              </span>
            </button>
            
            <button 
              className="action-btn success"
              onClick={() => window.location.href = '/admin/branches'}
            >
              <span className="action-icon">🏬</span>
              <span className="action-content">
                <span className="action-label">إدارة الفروع</span>
                <span className="action-desc">التحكم في جميع الفروع</span>
              </span>
            </button>
            
            <button 
              className="action-btn info"
              onClick={() => window.location.href = '/admin/rentals'}
            >
              <span className="action-icon">📋</span>
              <span className="action-content">
                <span className="action-label">عرض التأجيرات</span>
                <span className="action-desc">جميع التأجيرات النشطة والسابقة</span>
              </span>
            </button>
            
            <button 
              className="action-btn warning"
              onClick={() => window.location.href = '/admin/games'}
            >
              <span className="action-icon">🎮</span>
              <span className="action-content">
                <span className="action-label">الألعاب</span>
                <span className="action-desc">إدارة المخزون والأسعار</span>
              </span>
            </button>
            
            <button 
              className="action-btn danger"
              onClick={() => window.location.href = '/admin/revenue-analysis'}
            >
              <span className="action-icon">💰</span>
              <span className="action-content">
                <span className="action-label">تحليل الإيرادات</span>
                <span className="action-desc">تقارير وتحليلات مالية</span>
              </span>
            </button>
            
            <button 
              className="action-btn secondary"
              onClick={() => window.location.href = '/admin/settings'}
            >
              <span className="action-icon">⚙️</span>
              <span className="action-content">
                <span className="action-label">الإعدادات</span>
                <span className="action-desc">تخصيص النظام</span>
              </span>
            </button>
          </div>
        </div>

        {/* التأجيرات الأخيرة */}
        <div className="section recent-activity">
          <div className="section-header">
            <h2>📋 آخر التأجيرات</h2>
            <span className="section-badge">آخر 5</span>
          </div>
          <div className="recent-list">
            {recentRentals.length > 0 ? (
              recentRentals.map((rental) => (
                <div key={rental.id} className="recent-item">
                  <div className="rental-info">
                    <span className="rental-game">{rental.game_name || 'لعبة'}</span>
                    <span className="rental-customer">{rental.customer_name}</span>
                    <span className="rental-time">{formatTime(rental.start_time)}</span>
                  </div>
                  <div className="rental-status">
                    <span className={`status-badge ${rental.status === 'نشط' ? 'active' : rental.status === 'مكتمل' ? 'completed' : 'cancelled'}`}>
                      {rental.status}
                    </span>
                    {rental.total_amount > 0 && (
                      <span className="rental-amount">{formatCurrency(rental.total_amount)}</span>
                    )}
                  </div>
                </div>
              ))
            ) : (
              <div className="empty-state">
                <span>📭 لا توجد تأجيرات حديثة</span>
              </div>
            )}
          </div>
          <button 
            className="btn-view-all"
            onClick={() => window.location.href = '/admin/rentals'}
          >
            عرض جميع التأجيرات →
          </button>
        </div>
      </div>

      {/* إحصائيات الفروع */}
      {branchStats.length > 0 && (
        <div className="section branch-stats">
          <div className="section-header">
            <h2>🏬 أداء الفروع</h2>
            <span className="section-badge">{branchStats.length} فرع</span>
          </div>
          <div className="branches-grid">
            {branchStats.map((branch) => (
              <div key={branch.id} className="branch-card">
                <div className="branch-header">
                  <h3 className="branch-name">{branch.name}</h3>
                  <span className="branch-location">{branch.location}</span>
                </div>
                <div className="branch-stats">
                  <div className="branch-stat">
                    <span className="stat-label">الإيرادات</span>
                    <span className="stat-value">{formatCurrency(branch.revenue)}</span>
                  </div>
                  <div className="branch-stat">
                    <span className="stat-label">التأجيرات</span>
                    <span className="stat-value">{branch.rentals}</span>
                  </div>
                  <div className="branch-stat">
                    <span className="stat-label">الألعاب</span>
                    <span className="stat-value">{branch.games}</span>
                  </div>
                </div>
                <button 
                  className="btn-branch-details"
                  onClick={() => window.location.href = `/admin/branches/${branch.id}`}
                >
                  تفاصيل الفرع
                </button>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* ملاحظات وتنبيهات */}
      <div className="alerts-section">
        <h2>⚠️ تنبيهات النظام</h2>
        <div className="alerts-grid">
          {stats.activeRentals === 0 && (
            <div className="alert-item info">
              <span className="alert-icon">ℹ️</span>
              <span>لا توجد تأجيرات نشطة حالياً</span>
            </div>
          )}
          
          {stats.availableGames === 0 && stats.totalGames > 0 && (
            <div className="alert-item warning">
              <span className="alert-icon">⚠️</span>
              <span>جميع الألعاب مؤجرة حالياً</span>
            </div>
          )}
          
          {stats.todayRevenue === 0 && new Date().getHours() >= 12 && (
            <div className="alert-item warning">
              <span className="alert-icon">💰</span>
              <span>لم يتم تسجيل إيرادات اليوم حتى الآن</span>
            </div>
          )}
          
          <div className="alert-item success">
            <span className="alert-icon">✅</span>
            <span>النظام يعمل بشكل طبيعي</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
