import React, { useState, useEffect, useRef } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { 
  Home, Building, Users, Clock, DollarSign, 
  BarChart3, Settings, LogOut, ChevronLeft, 
  ChevronRight, Gamepad2, Calendar, Package,
  CreditCard, FileText, Bell, HelpCircle, 
  Shield, TrendingUp, Activity, Menu
} from 'lucide-react';
import './Layout.css';

// Custom hook for click outside detection
const useClickOutside = (ref, handler) => {
  useEffect(() => {
    const listener = (event) => {
      if (!ref.current || ref.current.contains(event.target)) {
        return;
      }
      handler(event);
    };

    document.addEventListener('mousedown', listener);
    document.addEventListener('touchstart', listener);

    return () => {
      document.removeEventListener('mousedown', listener);
      document.removeEventListener('touchstart', listener);
    };
  }, [ref, handler]);
};

const Layout = ({ children }) => {
  const { user, logout, isAuthenticated } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const [isSidebarCollapsed, setIsSidebarCollapsed] = useState(false);
  const [isUserDropdownOpen, setIsUserDropdownOpen] = useState(false);
  const [notifications, setNotifications] = useState([]);
  const [isNotificationsOpen, setIsNotificationsOpen] = useState(false);
  const [isShiftActive, setIsShiftActive] = useState(false);
  const [shiftInfo, setShiftInfo] = useState(null);
  
  // المراجع للقوائم المنسدلة
  const menuRef = useRef(null);
  const userDropdownRef = useRef(null);
  const notificationsRef = useRef(null);

  // إغلاق القوائم عند النقر خارجها
  useClickOutside(menuRef, () => setIsMenuOpen(false));
  useClickOutside(userDropdownRef, () => setIsUserDropdownOpen(false));
  useClickOutside(notificationsRef, () => setIsNotificationsOpen(false));

  // إغلاق القوائم عند تغيير المسار
  useEffect(() => {
    setIsMenuOpen(false);
    setIsUserDropdownOpen(false);
    setIsNotificationsOpen(false);
  }, [location]);

  // قائمة التنقل للشريط الجانبي
  const navItems = {
    admin: [
      {
        title: 'لوحة التحكم',
        icon: <Home size={20} />,
        path: '/admin',
        badge: null,
        exact: true
      },
      {
        title: 'المستخدمين',
        icon: <Users size={20} />,
        path: '/admin/users',
        badge: null
      },
      {
        title: 'الفروع',
        icon: <Building size={20} />,
        path: '/admin/branches',
        badge: null
      },
      {
        title: 'التأجيرات',
        icon: <Clock size={20} />,
        path: '/admin/rentals',
        badge: { count: 5, color: 'warning' }
      },
      {
        title: 'التحليل المالي',
        icon: <DollarSign size={20} />,
        path: '/admin/revenus',
        badge: null
      },
      {
        title: 'الإحصائيات',
        icon: <TrendingUp size={20} />,
        path: '/admin/statistics',
        badge: null
      },
      {
        title: 'الإعدادات',
        icon: <Settings size={20} />,
        path: '/admin/settings',
        badge: null
      }
    ],
    branch_manager: [
      {
        title: 'لوحة التحكم',
        icon: <Home size={20} />,
        path: '/employee',
        badge: null,
        exact: true
      },
      {
        title: 'التأجيرات',
        icon: <Clock size={20} />,
        path: '/employee/rentals',
        badge: null
      },
      {
        title: 'التقارير',
        icon: <FileText size={20} />,
        path: '/employee/reports',
        badge: null
      },
      {
        title: 'إعدادات الفرع',
        icon: <Settings size={20} />,
        path: '/employee/settings',
        badge: null
      }
    ],
    employee: [
      {
        title: 'لوحة التحكم',
        icon: <Home size={20} />,
        path: '/employee',
        badge: null,
        exact: true
      },
      {
        title: 'التأجيرات',
        icon: <Clock size={20} />,
        path: '/employee/rentals',
        badge: null
      }
    ]
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
  };

  const toggleSidebar = () => {
    setIsSidebarCollapsed(!isSidebarCollapsed);
  };

  const toggleUserDropdown = () => {
    setIsUserDropdownOpen(!isUserDropdownOpen);
  };

  const getRoleText = (role) => {
    switch(role) {
      case 'admin': return 'المدير العام';
      case 'branch_manager': return 'مدير فرع';
      case 'employee': return 'موظف';
      default: return 'مستخدم';
    }
  };

  const getRoleColor = (role) => {
    switch(role) {
      case 'admin': return 'role-admin';
      case 'branch_manager': return 'role-branch-manager';
      case 'employee': return 'role-employee';
      default: return 'role-default';
    }
  };

  if (!isAuthenticated) {
    return children;
  }

  const userNavItems = navItems[user?.role] || navItems.employee;
  const basePath = user?.role === 'admin' ? '/admin' : '/employee';
  const isRentalsPage = location.pathname.includes('/rentals');
  const isManager = user?.role === 'admin' || user?.role === 'branch_manager';

  return (
    <div className="app-container">
      {/* Sidebar ثابت */}
      <aside className={`sidebar-fixed ${isSidebarCollapsed ? 'collapsed' : ''}`}>
        {/* شعار التطبيق */}
        <div className="sidebar-logo">
          {!isSidebarCollapsed ? (
            <div className="logo-content">
              <div className="logo-icon">🏬</div>
              <div className="logo-text">
                <h2>L3BTY Store</h2>
                <p>نظام إدارة</p>
              </div>
            </div>
          ) : (
            <div className="logo-collapsed">
              <span className="logo-icon-small">🏬</span>
            </div>
          )}
          
          <button 
            className="collapse-btn"
            onClick={toggleSidebar}
            title={isSidebarCollapsed ? 'إظهار القائمة' : 'طي القائمة'}
          >
            {isSidebarCollapsed ? <ChevronRight size={18} /> : <ChevronLeft size={18} />}
          </button>
        </div>
        
        {/* قسم المستخدم */}
        <div className="user-section">
          {!isSidebarCollapsed && (
            <div className="user-info">
              <div className="user-avatar">
                <Users size={24} />
              </div>
              <div className="user-details">
                <h4>{user?.name || 'المستخدم'}</h4>
                <p>{getRoleText(user?.role)}</p>
              </div>
            </div>
          )}
        </div>
        
        {/* قائمة التنقل */}
        <nav className="sidebar-nav-fixed">
          <ul className="nav-menu-fixed">
            {userNavItems.map((item, index) => {
              const isActive = item.exact 
                ? location.pathname === item.path
                : location.pathname.startsWith(item.path);
              
              return (
                <li key={index}>
                  <Link 
                    to={item.path} 
                    className={`nav-item-fixed ${isActive ? 'active' : ''}`}
                    title={isSidebarCollapsed ? item.title : ''}
                  >
                    <span className="nav-icon-fixed">
                      {item.icon}
                      {item.badge && (
                        <span className={`nav-badge-fixed ${item.badge.color}`}>
                          {item.badge.count}
                        </span>
                      )}
                    </span>
                    {!isSidebarCollapsed && (
                      <>
                        <span className="nav-label-fixed">{item.title}</span>
                        {isActive && <span className="nav-indicator-fixed"></span>}
                      </>
                    )}
                  </Link>
                </li>
              );
            })}
          </ul>
        </nav>
        
        {/* قسم تسجيل الخروج */}
        <div className="sidebar-footer-fixed">
          <button 
            className="logout-btn-fixed"
            onClick={handleLogout}
            title={isSidebarCollapsed ? 'تسجيل الخروج' : ''}
          >
            <LogOut size={20} />
            {!isSidebarCollapsed && <span>تسجيل الخروج</span>}
          </button>
          
          {!isSidebarCollapsed && (
            <div className="system-info-fixed">
              <div className="system-status-fixed">
                <span className="status-dot-fixed online"></span>
                النظام يعمل
              </div>
              <div className="system-version-fixed">الإصدار 3.0.0</div>
            </div>
          )}
        </div>
      </aside>

      {/* المحتوى الرئيسي مع Navbar */}
      <div className={`main-content-wrapper ${isSidebarCollapsed ? 'sidebar-collapsed' : ''}`}>
        {/* Navbar */}
        <nav className="navbar">
          <div className="navbar-container">
            {/* القسم الأيسر: القائمة والشعار */}
            <div className="flex items-center gap-4">
              <button 
                className="menu-toggle"
                onClick={toggleMenu}
                aria-label="فتح/إغلاق القائمة المتنقلة"
              >
                <Menu size={24} />
              </button>
              
              <Link to={basePath} className="navbar-brand">
                <div className="logo-container">
                  <div className="logo">
                    <img 
                      src="/images/l3bty.png" 
                      alt="L3BTY Store Logo" 
                      className="logo-image"
                      onError={(e) => {
                        e.target.onerror = null;
                        e.target.src = "data:image/svg+xml,%3Csvg xmlns='http://www.w3.org/2000/svg' viewBox='0 0 100 100'%3E%3Crect width='100' height='100' fill='%234361ee'/%3E%3Ctext x='50' y='50' font-family='Arial' font-size='40' fill='white' text-anchor='middle' dy='.3em'%3EL%3C/text%3E%3C/svg%3E";
                      }}
                    />
                  </div>
                  <div className="brand-info">
                    <div className="brand-text">L3BTY Store</div>
                    <div className="brand-subtitle">نظام إدارة التأجيرات</div>
                  </div>
                </div>
              </Link>
            </div>

            {/* القسم الأيمن: الإشعارات والمستخدم */}
            <div className="navbar-right">
              {/* معلومات المستخدم */}
              <div className="user-container" ref={userDropdownRef}>
                <button
                  className="user-button"
                  onClick={toggleUserDropdown}
                >
                  <div className="user-info">
                    <div className="user-name">{user?.name || 'مستخدم'}</div>
                    <div className="user-role">{getRoleText(user?.role)}</div>
                  </div>
                  <div className={`user-avatar ${getRoleColor(user?.role)}`}>
                    {user?.name?.charAt(0) || 'U'}
                  </div>
                  <span className={`dropdown-arrow ${isUserDropdownOpen ? 'open' : ''}`}>
                    ▼
                  </span>
                </button>

                {/* Dropdown Menu */}
                {isUserDropdownOpen && (
                  <div className="user-dropdown">
                    <div className="user-dropdown-header">
                      <div className="user-dropdown-name">{user?.name}</div>
                      <div className="user-dropdown-email">{user?.email}</div>
                      <div className="user-dropdown-role">{getRoleText(user?.role)}</div>
                      {user?.branch_name && (
                        <div className="user-dropdown-branch">
                          <span className="branch-icon">🏬</span>
                          {user.branch_name}
                        </div>
                      )}
                    </div>
                    
                    <div className="dropdown-divider"></div>
                    
                    <Link
                      to={`${basePath}/profile`}
                      className="dropdown-item"
                      onClick={() => setIsUserDropdownOpen(false)}
                    >
                      <span className="dropdown-icon">👤</span>
                      الملف الشخصي
                    </Link>
                    
                    <Link
                      to={`${basePath}/settings`}
                      className="dropdown-item"
                      onClick={() => setIsUserDropdownOpen(false)}
                    >
                      <span className="dropdown-icon">⚙️</span>
                      الإعدادات
                    </Link>
                    
                    <div className="dropdown-divider"></div>
                    
                    <button
                      onClick={handleLogout}
                      className="dropdown-item logout"
                    >
                      <span className="dropdown-icon">🚪</span>
                      تسجيل الخروج
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* القائمة الجانبية المتنقلة (للأجهزة الصغيرة) */}
          <div className={`sidebar-mobile ${isMenuOpen ? 'open' : ''}`} ref={menuRef}>
            <div className="sidebar-header">
              <h3 className="sidebar-title">القائمة الرئيسية</h3>
              <button 
                className="sidebar-close"
                onClick={() => setIsMenuOpen(false)}
                aria-label="إغلاق القائمة"
              >
                ✕
              </button>
            </div>
            
            <nav className="sidebar-nav-mobile">
              {userNavItems.map((item) => {
                const isActive = item.exact 
                  ? location.pathname === item.path
                  : location.pathname.startsWith(item.path);
                
                return (
                  <Link
                    key={item.path}
                    to={item.path}
                    className={`nav-link-mobile ${isActive ? 'active' : ''}`}
                    onClick={() => setIsMenuOpen(false)}
                  >
                    <span className="nav-icon-mobile">{item.icon}</span>
                    <span className="nav-label-mobile">{item.title}</span>
                    {item.badge && (
                      <span className="nav-badge-mobile">{item.badge.count}</span>
                    )}
                    {isActive && <span className="nav-indicator-mobile"></span>}
                  </Link>
                );
              })}
            </nav>
            
            <div className="sidebar-footer-mobile">
              <div className="user-sidebar-info">
                <div className="sidebar-user-avatar">
                  {user?.name?.charAt(0) || 'U'}
                </div>
                <div className="sidebar-user-details">
                  <div className="sidebar-user-name">{user?.name}</div>
                  <div className="sidebar-user-role">{getRoleText(user?.role)}</div>
                </div>
              </div>
            </div>
          </div>

          {/* Overlay للقائمة الجانبية المتنقلة */}
          {isMenuOpen && (
            <div 
              className="sidebar-overlay"
              onClick={() => setIsMenuOpen(false)}
            />
          )}
        </nav>

        {/* المحتوى الرئيسي */}
        <main className="main-content">
          <div className="page-container">
            {children}
          </div>
        </main>

        {/* Footer */}
        <footer className="footer">
          <div className="footer-content">
            <div className="footer-section">
              <div className="footer-logo">
                <div className="footer-logo-icon">🎮</div>
                <div>
                  <h3 className="footer-title">L3BTY Store</h3>
                  <p className="footer-description">
                    نظام إدارة تأجير الألعاب المتكامل
                  </p>
                </div>
              </div>
              <p className="footer-about">
                نظام متكامل لإدارة تأجير الألعاب الإلكترونية، مصمم لتبسيط عمليات الإدارة، 
                المتابعة، والإحصائيات لتحقيق أفضل تجربة للمستخدمين.
              </p>
            </div>

            <div className="footer-section">
              <h3 className="footer-section-title">معلومات الاتصال</h3>
              <ul className="footer-contact">
                <li className="contact-item">
                  <span className="contact-icon">📍</span>
                  <span>القاهرة، مصر</span>
                </li>
                <li className="contact-item">
                  <span className="contact-icon">📞</span>
                  <span>01016904292</span>
                </li>
                <li className="contact-item">
                  <span className="contact-icon">✉️</span>
                  <span>support@l3bty.com</span>
                </li>
                <li className="contact-item">
                  <span className="contact-icon">🕒</span>
                  <span>10:00 ص - 10:00 م</span>
                </li>
              </ul>
            </div>
          </div>
          
          <div className="footer-bottom">
            <div className="footer-copyright">
              © {new Date().getFullYear()} L3BTY Store. جميع الحقوق محفوظة.
            </div>
            <div className="footer-meta">
              <span className="meta-item">
                <span className="meta-icon">🔄</span>
                آخر تحديث: {new Date().toLocaleDateString('ar-EG')}
              </span>
              <span className="meta-item">
                <span className="meta-icon">⚡</span>
                الإصدار: 3.0.0
              </span>
              <span className="meta-item">
                <span className="meta-icon">👥</span>
                مستخدمين نشطين: {Math.floor(Math.random() * 50) + 10}
              </span>
            </div>
          </div>
        </footer>
      </div>
    </div>
  );
};

export default Layout;
