import axios from 'axios';

// ==================== ÿ•ÿπÿØÿßÿØÿßÿ™ ÿ£ÿ≥ÿßÿ≥Ÿäÿ© ====================
// baseURL Ÿäÿ¨ÿ® ÿ£ŸÜ ŸäŸÜÿ™ŸáŸä ÿ®ŸÄ /api
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

console.log('üåê API Base URL:', API_BASE_URL);

const api = axios.create({
  baseURL: API_BASE_URL, // http://localhost:5000/api
  timeout: 30000,
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json',
    'Accept-Language': 'ar'
  }
});

// ==================== Interceptors ====================
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    
    // ‚ö° **ÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ£ŸÜ URL ŸÑÿß Ÿäÿ®ÿØÿ£ ÿ®ŸÄ /api**
    if (config.url && config.url.startsWith('/api/')) {
      console.warn('‚ö†Ô∏è ÿ™ÿ≠ÿ∞Ÿäÿ±: URL Ÿäÿ®ÿØÿ£ ÿ®ŸÄ /api/ ŸÖÿ±ÿ™ŸäŸÜ:', config.url);
      // ÿ•ÿµŸÑÿßÿ≠ ÿ™ŸÑŸÇÿßÿ¶Ÿä: ÿ•ÿ≤ÿßŸÑÿ© /api ÿßŸÑÿ£ŸàŸÑŸâ
      config.url = config.url.replace(/^\/api/, '');
      console.log('‚úÖ ÿ™ŸÖ ÿ•ÿµŸÑÿßÿ≠ URL ÿ•ŸÑŸâ:', config.url);
    }
    
    // DEBUG: ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿ∑ŸÑÿ®ÿßÿ™
    console.log('üåê API Request:', {
      method: config.method?.toUpperCase(),
      url: config.url,
      baseURL: config.baseURL,
      fullUrl: `${config.baseURL}${config.url}`
    });
    
    return config;
  },
  (error) => {
    console.error('‚ùå Request Interceptor Error:', error);
    return Promise.reject(error);
  }
);

api.interceptors.response.use(
  (response) => {
    console.log('‚úÖ API Response:', {
      url: response.config.url,
      status: response.status,
      success: true
    });
    
    // ÿ•ÿ∞ÿß ŸÉÿßŸÜÿ™ ÿßŸÑÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿ™ÿ≠ÿ™ŸàŸä ÿπŸÑŸâ data ŸÖÿ®ÿßÿ¥ÿ±ÿ©
    if (response.data && typeof response.data === 'object') {
      return response.data;
    }
    
    return {
      success: true,
      status: response.status,
      data: response.data,
      message: 'ÿ™ŸÖ ÿ™ŸÜŸÅŸäÿ∞ ÿßŸÑÿπŸÖŸÑŸäÿ© ÿ®ŸÜÿ¨ÿßÿ≠'
    };
  },
  (error) => {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä API:', {
      url: error.config?.url,
      status: error.response?.status,
      message: error.message
    });
    
    // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑÿÆÿ∑ÿ£ 500ÿå ŸÑÿß ÿ™ÿπÿ±ÿ∂ ÿ™ŸÅÿßÿµŸäŸÑ ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
    if (error.response?.status === 500) {
      return {
        success: false,
        status: 500,
        message: 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿÆÿßÿØŸÖ'
      };
    }
    
    // ŸÑŸÑÿ£ÿÆÿ∑ÿßÿ° ÿßŸÑÿ£ÿÆÿ±Ÿâ
    return {
      success: false,
      status: error.response?.status || 0,
      message: error.response?.data?.message || error.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£',
      data: error.response?.data
    };
  }
);

// ==================== ÿØŸàÿßŸÑ ÿßŸÑŸÖÿ≥ÿßÿπÿØÿ© ====================
api.setToken = (token) => {
  if (token) {
    api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
    localStorage.setItem('token', token);
  } else {
    delete api.defaults.headers.common['Authorization'];
    localStorage.removeItem('token');
  }
};

api.checkAuthStatus = () => {
  const token = localStorage.getItem('token');
  const user = localStorage.getItem('user');
  
  return {
    isAuthenticated: !!token,
    token: token,
    user: user ? JSON.parse(user) : null
  };
};

// ÿØÿßŸÑÿ© ŸÑÿ™ÿµÿ≠Ÿäÿ≠ ÿßŸÑŸÖÿ≥ÿßÿ±ÿßÿ™ ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã
api.fixUrl = (url) => {
  if (url && url.startsWith('/api/')) {
    console.warn(`‚ö†Ô∏è ÿ™ÿµÿ≠Ÿäÿ≠ URL: ${url} -> ${url.substring(4)}`);
    return url.substring(4); // ÿ•ÿ≤ÿßŸÑÿ© /api
  }
  return url;
};

// ==================== ÿØŸàÿßŸÑ API ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ© ====================

// 1. ÿßŸÑŸÖÿµÿßÿØŸÇÿ©
api.login = async (email, password) => {
  try {
    const response = await api.post('/auth/login', {
      email, 
      password
    });
    
    const data = response;
    
    if (data.success && data.token) {
      api.setToken(data.token);
      
      if (data.user) {
        localStorage.setItem('user', JSON.stringify(data.user));
        localStorage.setItem('lastLogin', new Date().toISOString());
      }
      
      return {
        success: true,
        data: data
      };
    } else {
      return {
        success: false,
        message: data.message || 'ŸÅÿ¥ŸÑ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ'
      };
    }
  } catch (error) {
    if (error.code === 'ECONNABORTED') {
      return {
        success: false,
        message: 'ÿßŸÜÿ™Ÿáÿ™ ÿßŸÑŸÖŸáŸÑÿ©. ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ™ÿ¥ÿ∫ŸäŸÑ ÿßŸÑÿÆÿßÿØŸÖ'
      };
    }
    
    if (error.status === 401) {
      return {
        success: false,
        message: 'ÿßŸÑÿ®ÿ±ŸäÿØ ÿßŸÑÿ•ŸÑŸÉÿ™ÿ±ŸàŸÜŸä ÿ£Ÿà ŸÉŸÑŸÖÿ© ÿßŸÑŸÖÿ±Ÿàÿ± ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠ÿ©'
      };
    }
    
    return {
      success: false,
      message: 'ÿ™ÿπÿ∞ÿ± ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ. ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ™ÿ¥ÿ∫ŸäŸÑ ÿßŸÑÿÆÿßÿØŸÖ'
    };
  }
};

api.logout = async (redirect = true) => {
  try {
    // ŸÖÿ≠ÿßŸàŸÑÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨ ŸÖŸÜ ÿßŸÑÿÆÿßÿØŸÖ
    await api.post('/auth/logout');
  } catch (error) {
    console.log('‚ö†Ô∏è ŸÑÿß ŸäŸÖŸÉŸÜ ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ ŸÑŸÑÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨');
  } finally {
    // ŸÖÿ≥ÿ≠ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≠ŸÑŸäÿ©
    api.setToken(null);
    localStorage.removeItem('user');
    localStorage.removeItem('lastLogin');
    localStorage.removeItem('refreshToken');
    
    // ŸÖÿ≥ÿ≠ ÿßŸÑŸÉÿßÿ¥
    api.clearCache();
    
    // ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™Ÿàÿ¨ŸäŸá
    if (redirect) {
      window.location.href = '/login';
    }
  }
};

