api.getActiveRentalsOnly = async (branchId = null) => {
  try {
    console.log('‚è±Ô∏è ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ© ŸÅŸÇÿ∑');
    
    const params = {};
    if (branchId) {
      params.branch_id = branchId;
    }
    
    try {
      const response = await api.get('/rentals/active-only', { params });
      console.log('‚úÖ ÿßÿ≥ÿ™ÿÆÿØŸÖÿ™ ŸÜŸÇÿ∑ÿ© active-only ÿßŸÑÿ¨ÿØŸäÿØÿ©');
      return response;
    } catch (error) {
      console.log('‚ö†Ô∏è ŸÜŸÇÿ∑ÿ© active-only ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØÿ©ÿå ÿßÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑŸÜŸÇÿ∑ÿ© ÿßŸÑÿπÿßÿØŸäÿ©');
      const regularResponse = await api.get('/rentals', { 
        params: { 
          ...params,
          status: 'ŸÜÿ¥ÿ∑',
          limit: 100,
          include_game: true
        }
      });
      
      if (regularResponse.success && Array.isArray(regularResponse.data)) {
        const now = new Date();
        const activeRentals = regularResponse.data.filter(rental => {
          if (rental.duration_minutes > 0) {
            const startTime = new Date(rental.start_time);
            const endTime = new Date(startTime.getTime() + rental.duration_minutes * 60000);
            return endTime > now;
          }
          return true;
        });
        
        return {
          success: true,
          data: activeRentals,
          count: activeRentals.length,
          message: `ÿ™ŸÖ ÿ¨ŸÑÿ® ${activeRentals.length} ÿ™ÿ£ÿ¨Ÿäÿ± ŸÜÿ¥ÿ∑`
        };
      }
      
      return regularResponse;
    }
  } catch (error) {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©:', error);
    return {
      success: false,
      message: 'ÿ™ÿπÿ∞ÿ± ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©',
      data: []
    };
  }
};

