// ÿ™ÿ≠ÿØŸäÿ´ ŸÑÿπÿ®ÿ© ŸÅŸä ÿßŸÑŸÅÿ±ÿπ
export const updateBranchGame = async (branchId, gameId, updateData) => {
  try {
    const response = await api.put(`/branches/${branchId}/games/${gameId}`, updateData);
    return response.data;
  } catch (error) {
    console.error('Error updating branch game:', error);
    return { success: false, message: 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑŸÑÿπÿ®ÿ©' };
  }
};

// ÿ•ÿ≤ÿßŸÑÿ© ŸÑÿπÿ®ÿ© ŸÖŸÜ ÿßŸÑŸÅÿ±ÿπ
export const deleteBranchGame = async (branchId, gameId) => {
  try {
    const response = await api.delete(`/branches/${branchId}/games/${gameId}`);
    return response.data;
  } catch (error) {
    console.error('Error deleting branch game:', error);
    return { success: false, message: 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ•ÿ≤ÿßŸÑÿ© ÿßŸÑŸÑÿπÿ®ÿ©' };
  }
};

// ÿ¨ŸÑÿ® ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© ŸÑŸÑÿ•ÿ∂ÿßŸÅÿ© ŸÑŸÑŸÅÿ±ÿπ
export const getAvailableGamesForBranch = async (branchId) => {
  try {
    // ÿ¨ŸÑÿ® ÿ¨ŸÖŸäÿπ ÿßŸÑÿ£ŸÑÿπÿßÿ®
    const allGames = await getGames();
    // ÿ¨ŸÑÿ® ÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÅÿ±ÿπ
    const branchGames = await getBranchGames(branchId);
    
    if (allGames.success && branchGames.success) {
      // ÿ™ÿµŸÅŸäÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿ∫Ÿäÿ± ÿßŸÑŸÖÿ∂ÿßŸÅÿ© ŸÑŸÑŸÅÿ±ÿπ
      const addedGameIds = branchGames.data.map(g => g.game_id);
      const availableGames = allGames.data.filter(game => !addedGameIds.includes(game.id));
      
      return { 
        success: true, 
        data: availableGames 
      };
    }
    return { success: false, message: 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©' };
  } catch (error) {
    console.error('Error getting available games:', error);
    return { success: false, message: 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©' };
  }
};

export default authService;
