// ÙÙŠ src/pages/Rentals.jsx - ØªØ­Ø¯ÙŠØ« Ù„ÙŠØ¹Ø±Ø¶ ÙÙ‚Ø· Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„ÙØ±Ø¹ Ø§Ù„ØªØ§Ø¨Ø¹ Ù„Ù‡
import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  const { user, logout } = useAuth(); // Ø¥Ø¶Ø§ÙØ© logout
  const [branch, setBranch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedTab, setSelectedTab] = useState('available');
  const [showRentalForm, setShowRentalForm] = useState(false);
  const [showRentalDetail, setShowRentalDetail] = useState(false);
  const [selectedGame, setSelectedGame] = useState(null);
  const [selectedRental, setSelectedRental] = useState(null);
  const [selectedGames, setSelectedGames] = useState([]);
  const [quantities, setQuantities] = useState({});
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ ÙˆØ§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
  const [availableGames, setAvailableGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  
  // Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù†Ù…ÙˆØ°Ø¬
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(60);
  const [discount, setDiscount] = useState(0);
  const [paymentMethod, setPaymentMethod] = useState('cash');

  // ==================== USE EFFECTS ====================

  useEffect(() => {
    if (user) {
      // ðŸ”¥ **Ø§Ù„ØªØ­Ù‚Ù‚ Ù…Ù† Ø£Ù† Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù… Ù„Ø¯ÙŠÙ‡ ÙØ±Ø¹**
      if (!user.branch_id && user.role !== 'admin') {
        alert('âš ï¸ Ù„ÙŠØ³ Ù„Ø¯ÙŠÙƒ ÙØ±Ø¹ Ù…Ø¹ÙŠÙ†. ÙŠØ±Ø¬Ù‰ Ø§Ù„ØªÙˆØ§ØµÙ„ Ù…Ø¹ Ø§Ù„Ø¥Ø¯Ø§Ø±Ø©.');
        logout();
        return;
      }
      
      loadData();
      
      // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª ÙƒÙ„ 30 Ø«Ø§Ù†ÙŠØ©
      const interval = setInterval(() => {
        loadData();
      }, 30000);
      
      return () => clearInterval(interval);
    }
  }, [user]);

  // ==================== FUNCTIONS ====================

  // ðŸ¬ ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ÙØ±Ø¹
  const loadBranchData = async () => {
    try {
      if (!user?.branch_id) return;
      
      const response = await api.get('/branches');
      if (response.success) {
        const userBranch = response.data.find(b => b.id === user.branch_id);
        if (!userBranch) {
          console.error('âŒ Ø§Ù„ÙØ±Ø¹ ØºÙŠØ± Ù…ÙˆØ¬ÙˆØ¯');
          alert('âš ï¸ Ø§Ù„ÙØ±Ø¹ Ø§Ù„ØªØ§Ø¨Ø¹ Ù„Ùƒ ØºÙŠØ± Ù…ÙˆØ¬ÙˆØ¯ ÙÙŠ Ø§Ù„Ù†Ø¸Ø§Ù…');
          return;
        }
        
        setBranch(userBranch);
        console.log('âœ… Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ÙØ±Ø¹:', userBranch.name);
      }
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„ÙØ±Ø¹:', error);
    }
  };

  // ðŸŽ® ØªØ­Ù…ÙŠÙ„ Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„ÙØ±Ø¹ Ø§Ù„Ø®Ø§Øµ Ø¨Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù…
  const loadGamesData = async () => {
    try {
      if (!user?.branch_id) {
        console.warn('âš ï¸ Ø§Ù„Ù…Ø³ØªØ®Ø¯Ù… Ù„ÙŠØ³ Ù„Ø¯ÙŠÙ‡ ÙØ±Ø¹');
        return;
      }
      
      console.log(`ðŸŽ® Ø¬Ø§Ø±ÙŠ ØªØ­Ù…ÙŠÙ„ Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„ÙØ±Ø¹ ${user.branch_id}...`);
      
      // Ù…Ø­Ø§ÙˆÙ„Ø© Ø§Ù„Ù…Ø³Ø§Ø±Ø§Øª Ø§Ù„Ù…Ø®ØªÙ„ÙØ©
      let response;
      
      try {
        // Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø© Ø§Ù„Ø£ÙˆÙ„Ù‰: Ø§Ù„Ù…Ø³Ø§Ø± Ø§Ù„Ù…Ø¨Ø§Ø´Ø±
        response = await api.get(`/branches/${user.branch_id}/games`);
      } catch (firstError) {
        console.log('â„¹ï¸ ÙØ´Ù„ Ø§Ù„Ù…Ø³Ø§Ø± Ø§Ù„Ø£ÙˆÙ„ØŒ Ø¬Ø±Ø¨ Ø§Ù„Ù…Ø³Ø§Ø± Ø§Ù„Ø¨Ø¯ÙŠÙ„...');
        try {
          // Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø© Ø§Ù„Ø«Ø§Ù†ÙŠØ©: Ø§Ù„Ù…Ø³Ø§Ø± Ø§Ù„Ø¨Ø¯ÙŠÙ„
          response = await api.get('/games', {
            params: { branch_id: user.branch_id, status: 'Ù…ØªØ§Ø­' }
          });
        } catch (secondError) {
          console.log('â„¹ï¸ ÙØ´Ù„ Ø§Ù„Ù…Ø³Ø§Ø± Ø§Ù„Ø«Ø§Ù†ÙŠØŒ Ø¬Ø±Ø¨ Ø§Ù„Ù…Ø³Ø§Ø± Ø§Ù„Ø«Ø§Ù„Ø«...');
          // Ø§Ù„Ù…Ø­Ø§ÙˆÙ„Ø© Ø§Ù„Ø«Ø§Ù„Ø«Ø©: Ø¬Ù„Ø¨ ÙƒÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø«Ù… Ø§Ù„ØªØµÙÙŠØ©
          const allGames = await api.get('/games');
          if (allGames.success) {
            const branchGames = allGames.data.filter(game => 
              game.branch_id === user.branch_id && game.status === 'Ù…ØªØ§Ø­'
            );
            response = { success: true, data: branchGames };
          }
        }
      }
      
      if (response?.success) {
        // âœ… ØªØ£ÙƒØ¯ Ù…Ù† ÙˆØ¬ÙˆØ¯ Ø§Ù„Ø­Ù‚ÙˆÙ„ Ø§Ù„Ø£Ø³Ø§Ø³ÙŠØ©
        const processedGames = response.data.map(game => ({
          id: game.id || game.branch_game_id || Date.now(),
          name: game.name || 'Ù„Ø¹Ø¨Ø© ØºÙŠØ± Ù…Ø¹Ø±ÙˆÙØ©',
          category: game.category || 'Ø£Ø®Ø±Ù‰',
          image_url: game.image_url || 'default-game.jpg',
          price_per_hour: game.price_per_hour || 100,
          price_per_15min: game.price_per_15min || Math.ceil((game.price_per_hour || 100) / 4),
          status: game.status || 'Ù…ØªØ§Ø­',
          available_quantity: game.available_quantity || 1,
          quantity: game.quantity || 1,
          branch_id: game.branch_id || user.branch_id
        }));
        
        setAvailableGames(processedGames);
        console.log(`âœ… ØªÙ… ØªØ­Ù…ÙŠÙ„ ${processedGames.length} Ù„Ø¹Ø¨Ø© Ù„Ù„ÙØ±Ø¹`);
      } else {
        console.warn('âš ï¸ Ù„Ø§ ØªÙˆØ¬Ø¯ Ø£Ù„Ø¹Ø§Ø¨ Ù„Ù„ÙØ±Ø¹');
        setAvailableGames([]);
      }
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨:', error);
      setAvailableGames([]);
    }
  };

  // â±ï¸ ØªØ­Ù…ÙŠÙ„ ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„ÙØ±Ø¹
  const loadActiveRentals = async () => {
    try {
      if (!user?.branch_id) return;
      
      console.log(`â±ï¸ Ø¬Ø§Ø±ÙŠ ØªØ­Ù…ÙŠÙ„ ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„ÙØ±Ø¹ ${user.branch_id}...`);
      
      let response;
      
      try {
        response = await api.get('/rentals/active', {
          params: { branch_id: user.branch_id }
        });
      } catch (error) {
        // Fallback
        response = await api.get('/rentals', {
          params: { 
            branch_id: user.branch_id,
            status: 'Ù†Ø´Ø·'
          }
        });
      }
      
      if (response?.success) {
        setActiveRentals(response.data || []);
        console.log(`âœ… ØªÙ… ØªØ­Ù…ÙŠÙ„ ${response.data?.length || 0} ØªØ£Ø¬ÙŠØ± Ù†Ø´Ø·`);
      }
    } catch (error) {
      console.error('âŒ Ø®Ø·Ø£ ÙÙŠ ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª:', error);
