// frontend/src/services/api.js
import axios from 'axios';

// ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ŸÜŸÅÿ≥ ÿßŸÑŸÄ PORT
const API_BASE_URL = process.env.REACT_APP_API_URL || 'http://localhost:5000/api';

const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 15000,
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json',
    'Accept-Language': 'ar'
  },
  withCredentials: false // ÿ∫Ÿäÿ± Ÿáÿ∞ÿß ÿ•ŸÑŸâ false ÿ•ÿ∞ÿß ŸÉŸÜÿ™ ŸÑÿß ÿ™ÿ≥ÿ™ÿÆÿØŸÖ cookies
});

// ÿ£ÿ∂ŸÅ Ÿáÿ∞ÿß ŸÑŸÑÿ™ÿµÿ≠Ÿäÿ≠
api.interceptors.request.use(
  (config) => {
    console.log(`üì§ [${config.method.toUpperCase()}] ${config.baseURL}${config.url}`);
    return config;
  },
  (error) => {
    console.error('‚ùå Request Error:', error.message);
    return Promise.reject(error);
  }
);

api.interceptors.response.use(
  (response) => {
    console.log(`‚úÖ [${response.status}] ${response.config.url}`);
    return response;
  },
  (error) => {
    if (!error.response) {
      console.error('üåê Network Error: Server is not responding');
      console.error('   Check if backend is running on http://localhost:5000');
    } else {
      console.error(`‚ùå Server Error [${error.response.status}]:`, error.response.data);
    }
    return Promise.reject(error);
  }
);
