// src/context/AuthContext.jsx
import React, { createContext, useState, useContext, useEffect } from 'react';
import api from '../services/api';
import authService from '../services/authService';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth Ÿäÿ¨ÿ® ÿ£ŸÜ Ÿäÿ≥ÿ™ÿÆÿØŸÖ ÿØÿßÿÆŸÑ AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  
  // ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖŸÜ localStorage ÿπŸÜÿØ ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ
  useEffect(() => {
    const loadUser = async () => {
      try {
        console.log('üîç ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ...');
        
        // ÿßŸÑÿ≠ÿµŸàŸÑ ÿπŸÑŸâ ÿßŸÑÿ™ŸàŸÉŸÜ ŸÖŸÜ localStorage
        const token = localStorage.getItem('token');
        const userStr = localStorage.getItem('user');
        
        console.log('üîë ÿ≠ÿßŸÑÿ© ÿßŸÑÿ™ŸàŸÉŸÜ:', token ? 'ŸÖŸàÿ¨ŸàÿØ' : 'ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØ');
        console.log('üë§ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:', userStr ? 'ŸÖŸàÿ¨ŸàÿØÿ©' : 'ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØÿ©');
        
        if (token && userStr) {
          // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿµÿ≠ÿ© ÿßŸÑÿ™ŸàŸÉŸÜ
          const tokenParts = token.split('_');
          if (tokenParts.length >= 3 && tokenParts[0] === 'l3bty') {
            try {
              const userData = JSON.parse(userStr);
              
              // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿµÿ≠ÿ© ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
              if (userData && userData.id && userData.name) {
                setUser(userData);
                console.log('‚úÖ ÿ™ŸÖ ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:', userData);
              } else {
                console.warn('‚ö†Ô∏è ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿ∫Ÿäÿ± ÿµÿßŸÑÿ≠ÿ©ÿå ŸÖÿ≥ÿ≠ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...');
                clearAuthData();
              }
            } catch (parseError) {
              console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÑŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:', parseError);
              clearAuthData();
            }
          } else {
            console.warn('‚ö†Ô∏è ÿßŸÑÿ™ŸàŸÉŸÜ ÿ∫Ÿäÿ± ÿµÿßŸÑÿ≠ÿå ŸÖÿ≥ÿ≠ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...');
            clearAuthData();
          }
        } else {
          console.log('‚ÑπÔ∏è ŸÑÿß ŸäŸàÿ¨ÿØ ŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÖÿ≥ÿ¨ŸÑ');
        }
      } catch (error) {
        console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:', error);
        clearAuthData();
      } finally {
        setLoading(false);
      }
    };
    
    loadUser();
  }, []);

  const clearAuthData = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('user');
    localStorage.removeItem('lastLogin');
    setUser(null);
    delete api.defaults.headers.common['Authorization'];
  };

  // ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ
  const login = async (email, password) => {
    try {
      setLoading(true);
      console.log('üîê ŸÖÿ≠ÿßŸàŸÑÿ© ÿ™ÿ≥ÿ¨ŸäŸÑ ÿØÿÆŸàŸÑ ŸÖŸÜ AuthContext...', email);
      
      const result = await authService.login(email, password);
      
      if (result.success && result.user) {
        setUser(result.user);
        console.log('‚úÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÜÿßÿ¨ÿ≠ ŸÅŸä AuthContext:', result.user.name);
        return result;
      } else {
        console.log('‚ùå ŸÅÿ¥ŸÑ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÅŸä AuthContext:', result.message);
        return result;
      }
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ (AuthContext):', error);
      return {
        success: false,
        message: error.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿÆÿßÿØŸÖ'
      };
    } finally {
      setLoading(false);
    }
  };

  // ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨
  const logout = () => {
    console.log('üö™ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨ ŸÖŸÜ AuthContext...');
    clearAuthData();
    window.location.href = '/login';
  };

  // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑŸÖÿµÿßÿØŸÇÿ©
  const isAuthenticated = () => {
    const token = localStorage.getItem('token');
    const userExists = !!user;
    
    console.log('üîç ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑŸÖÿµÿßÿØŸÇÿ©:', {
      hasToken: !!token,
      hasUser: userExists,
      isAuthenticated: !!(token && userExists)
    });
    
    return !!(token && userExists);
  };

  // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿµŸÑÿßÿ≠Ÿäÿßÿ™
  const hasRole = (role) => {
    if (!user || !user.role) return false;
    return user.role.toLowerCase() === role.toLowerCase();
  };

  const hasAnyRole = (roles) => {
    if (!user || !user.role) return false;
    const userRole = user.role.toLowerCase();
    const roleList = Array.isArray(roles) ? 
      roles.map(r => r.toLowerCase()) : 
      [roles.toLowerCase()];
    return roleList.includes(userRole);
  };

  // ÿ™ÿ≠ÿØŸäÿ´ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
  const updateUser = (userData) => {
    try {
      const updatedUser = { ...user, ...userData };
      setUser(updatedUser);
      localStorage.setItem('user', JSON.stringify(updatedUser));
      console.log('‚úÖ ÿ™ŸÖ ÿ™ÿ≠ÿØŸäÿ´ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ŸÅŸä AuthContext');
      return updatedUser;
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ÿØŸäÿ´ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ:', error);
      return null;
    }
  };

  // ÿßŸÑŸÇŸäŸÖÿ© ÿßŸÑÿ™Ÿä ŸäŸàŸÅÿ±Ÿáÿß Context
  const value = {
    user,
    loading,
    login,
    logout,
    isAuthenticated, // Ÿáÿ∞ÿß Ÿäÿ¨ÿ® ÿ£ŸÜ ŸäŸÉŸàŸÜ ÿØÿßŸÑÿ©
    hasRole,
    hasAnyRole,
    updateUser
  };

  console.log('üîÑ ŸÇŸäŸÖÿ© AuthContext:', value);

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
