// frontend/src/pages/employee/Rentals.jsx
import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  const { user } = useAuth();
  const [loading, setLoading] = useState(true);
  const [currentShift, setCurrentShift] = useState(null);
  const [shiftStats, setShiftStats] = useState({
    total_rentals: 0,
    total_revenue: 0,
    unique_customers: 0,
    active_rentals: 0
  });
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ŸÑÿπÿßÿ®
  const [availableGames, setAvailableGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  
  // ÿ™ÿ£ÿ¨Ÿäÿ± ŸÖÿ™ÿπÿØÿØ
  const [selectedGames, setSelectedGames] = useState([]);
  const [quantities, setQuantities] = useState({});
  const [showRentalForm, setShowRentalForm] = useState(false);
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÜŸÖŸàÿ∞ÿ¨
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(60);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  const [discount, setDiscount] = useState(0);
  
  // ÿ≠ÿßŸÑÿ© ŸÑÿπÿ±ÿ∂ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™ ŸÅŸä modal
  const [showShiftStatsModal, setShowShiftStatsModal] = useState(false);
  const [detailedShiftStats, setDetailedShiftStats] = useState(null);
  
  const timersRef = useRef({});

  // ÿ™ÿ£ÿ´Ÿäÿ±ÿßÿ™
  useEffect(() => {
    if (user) {
      initializeShift();
      
      const interval = setInterval(() => {
        if (currentShift) {
          loadData();
        }
      }, 30000);
      
      return () => {
        clearInterval(interval);
        Object.values(timersRef.current).forEach(timer => clearInterval(timer));
      };
    }
  }, [user, currentShift]);

  // ==================== SHIFT MANAGEMENT ====================

  const initializeShift = async () => {
    try {
      console.log('üîÑ ÿ¨ŸÑÿ® ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑŸÜÿ¥ÿ∑...');
      
      const response = await api.get('/shifts/active');
      
      console.log('üì• ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿßŸÑÿÆÿßÿØŸÖ:', response);
      console.log('üìä ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ©:', response.data);
      
      // ÿßŸÑÿ≠ÿßŸÑÿ© 1: ÿ•ÿ∞ÿß ŸÉÿßŸÜ response.data Ÿäÿ≠ÿ™ŸàŸä ÿπŸÑŸâ id ŸÖÿ®ÿßÿ¥ÿ±ÿ©
      if (response.data && response.data.id) {
        console.log('‚úÖ ÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿ¥ŸäŸÅÿ™ (ÿ®ŸäÿßŸÜÿßÿ™ ŸÖÿ®ÿßÿ¥ÿ±ÿ©):', response.data.id);
        setCurrentShift(response.data);
        await loadShiftStats(response.data.id);
        await loadData();
        return;
      }
      
      // ÿßŸÑÿ≠ÿßŸÑÿ© 2: ÿ•ÿ∞ÿß ŸÉÿßŸÜ response.data Ÿäÿ≠ÿ™ŸàŸä ÿπŸÑŸâ ÿ≠ŸÇŸÑ success
      if (response.data && typeof response.data === 'object') {
        if (response.data.success === true) {
          if (response.data.data && response.data.data.id) {
            console.log('‚úÖ ÿ™ŸÖ ÿßŸÑÿπÿ´Ÿàÿ± ÿπŸÑŸâ ÿ¥ŸäŸÅÿ™ (ŸÅŸä ÿ≠ŸÇŸÑ data):', response.data.data.id);
            setCurrentShift(response.data.data);
            await loadShiftStats(response.data.data.id);
            await loadData();
            return;
          } else if (response.data.data === null) {
            console.log('‚ÑπÔ∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑ (data: null)');
            setCurrentShift(null);
            setLoading(false);
            return;
          }
        } else if (response.data.success === false) {
          console.log('‚ÑπÔ∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑ (success: false)');
          console.log('üìù ÿ±ÿ≥ÿßŸÑÿ© ÿßŸÑÿÆÿßÿØŸÖ:', response.data.message);
          setCurrentShift(null);
          setLoading(false);
          return;
        }
      }
      
      // ÿßŸÑÿ≠ÿßŸÑÿ© 3: ÿ•ÿ∞ÿß ŸÉÿßŸÜÿ™ ÿßŸÑÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ŸÅÿßÿ±ÿ∫ÿ©
      console.log('‚ÑπÔ∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑ (ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ŸÅÿßÿ±ÿ∫ÿ©)');
      setCurrentShift(null);
      
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑŸÜÿ¥ÿ∑:', error);
      console.error('üîç ÿ™ŸÅÿßÿµŸäŸÑ ÿßŸÑÿÆÿ∑ÿ£:', error.response?.data || error.message);
      setCurrentShift(null);
      
      // ÿπÿ±ÿ∂ ÿ±ÿ≥ÿßŸÑÿ© ÿÆÿ∑ÿ£ ŸÖŸÅÿµŸÑÿ©
      if (error.response?.status === 401) {
        showNotification('error', 'ÿßŸÜÿ™Ÿáÿ™ ÿ¨ŸÑÿ≥ÿ™ŸÉ. Ÿäÿ±ÿ¨Ÿâ ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿØÿÆŸàŸÑ ŸÖÿ±ÿ© ÿ£ÿÆÿ±Ÿâ');
      } else if (error.response?.status === 404) {
        showNotification('info', 'ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑ ÿ≠ÿßŸÑŸäÿßŸã');
      } else if (error.code === 'NETWORK_ERROR') {
        showNotification('error', 'ŸÅÿ¥ŸÑ ŸÅŸä ÿßŸÑÿßÿ™ÿµÿßŸÑ ÿ®ÿßŸÑÿÆÿßÿØŸÖ. ÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßÿ™ÿµÿßŸÑ ÿßŸÑÿ•ŸÜÿ™ÿ±ŸÜÿ™');
      } else {
        showNotification('error', 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™');
      }
    } finally {
      setLoading(false);
    }
  };

  const loadShiftStats = async (shiftId) => {
    try {
      const response = await api.get(`/shifts/${shiftId}/stats`);
      
      if (response.data && response.data.success) {
        setShiftStats(response.data.data);
      } else if (response.data) {
        // ÿ•ÿ∞ÿß ŸÉÿßŸÜÿ™ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ™ÿ£ÿ™Ÿä ŸÖÿ®ÿßÿ¥ÿ±ÿ© ÿ®ÿØŸàŸÜ ÿ≠ŸÇŸÑ success
        setShiftStats(response.data);
      }
    } catch (error) {
      console.error('Error loading shift stats:', error);
      // ÿ™ÿπŸäŸäŸÜ ŸÇŸäŸÖ ÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ© ŸÅŸä ÿ≠ÿßŸÑÿ© ÿßŸÑÿÆÿ∑ÿ£
      setShiftStats({
        total_rentals: 0,
        total_revenue: 0,
        unique_customers: 0,
        active_rentals: activeRentals.length
      });
    }
  };

  // ÿØÿßŸÑÿ© ÿ¨ÿØŸäÿØÿ© ŸÑÿπÿ±ÿ∂ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ŸÖŸÅÿµŸÑÿ©
  const viewDetailedShiftStats = async () => {
    try {
      showNotification('info', 'ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™...');
      
      const response = await api.get(`/shifts/${currentShift.id}/stats`);
      
      if (response.data && response.data.success) {
        setDetailedShiftStats(response.data.data);
        setShowShiftStatsModal(true);
      } else if (response.data) {
        setDetailedShiftStats(response.data);
        setShowShiftStatsModal(true);
      } else {
        showNotification('error', 'ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ŸÖÿ™ÿßÿ≠ÿ© ŸÑŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸä');
      }
    } catch (error) {
      console.error('Error loading detailed stats:', error);
      showNotification('error', 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™');
    }
  };

  // ÿØÿßŸÑÿ© ŸÑÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØ
  const startNewShift = async () => {
    try {
      console.log('üöÄ ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØ...');
      
      const response = await api.post('/shifts/start');
      
      console.log('üì• ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™:', response.data);
      
      // ÿßŸÑÿ™ÿπÿßŸÖŸÑ ŸÖÿπ ŸÖÿÆÿ™ŸÑŸÅ ÿ£ÿ¥ŸÉÿßŸÑ ÿßŸÑÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ©
      if (response.data && response.data.id) {
        // ÿßŸÑÿ≠ÿßŸÑÿ© 1: ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿ™ÿ£ÿ™Ÿä ŸÖÿ®ÿßÿ¥ÿ±ÿ©
        console.log('‚úÖ ÿ™ŸÖ ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ÿ®ŸÜÿ¨ÿßÿ≠:', response.data.id);
        setCurrentShift(response.data);
        await loadShiftStats(response.data.id);
        await loadData();
        showNotification('success', 'ÿ™ŸÖ ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ÿ®ŸÜÿ¨ÿßÿ≠!');
        return;
      }
      
      if (response.data && response.data.data && response.data.data.id) {
        // ÿßŸÑÿ≠ÿßŸÑÿ© 2: ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÅŸä ÿ≠ŸÇŸÑ data
        console.log('‚úÖ ÿ™ŸÖ ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ÿ®ŸÜÿ¨ÿßÿ≠:', response.data.data.id);
        setCurrentShift(response.data.data);
        await loadShiftStats(response.data.data.id);
        await loadData();
        showNotification('success', 'ÿ™ŸÖ ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ÿ®ŸÜÿ¨ÿßÿ≠!');
        return;
      }
      
      if (response.data && response.data.success) {
        // ÿßŸÑÿ≠ÿßŸÑÿ© 3: ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ŸÖŸÜÿ∏ŸÖÿ©
        console.log('‚úÖ ÿ™ŸÖ ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ÿ®ŸÜÿ¨ÿßÿ≠');
        showNotification('success', response.data.message || 'ÿ™ŸÖ ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ÿ®ŸÜÿ¨ÿßÿ≠');
        // ÿ•ÿπÿßÿØÿ© ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™
        await initializeShift();
        return;
      }
      
      // ÿ£Ÿä ÿ≠ÿßŸÑÿ© ÿ£ÿÆÿ±Ÿâ
      console.log('‚úÖ ÿ™ŸÖ ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ÿ®ŸÜÿ¨ÿßÿ≠ (ÿ≠ÿßŸÑÿ© ÿπÿßŸÖÿ©)');
      showNotification('success', 'ÿ™ŸÖ ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ÿ®ŸÜÿ¨ÿßÿ≠');
      await initializeShift();
      
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™:', error);
      
      let errorMessage = 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™';
      
      if (error.response) {
        if (error.response.data && error.response.data.message) {
          errorMessage = error.response.data.message;
        } else if (error.response.status === 400) {
          errorMessage = 'ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑ ÿ®ÿßŸÑŸÅÿπŸÑ';
        } else if (error.response.status === 401) {
          errorMessage = 'ÿ∫Ÿäÿ± ŸÖÿµÿ±ÿ≠ ÿ®ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™';
        }
      }
      
      showNotification('error', errorMessage);
    }
  };

  // ÿØÿßŸÑÿ© ŸÑÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™
  const endCurrentShift = async () => {
    if (!currentShift) return;
    
    if (!window.confirm('ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸäÿü ÿ≥Ÿäÿ™ŸÖ ÿ≠ŸÅÿ∏ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™.')) {
      return;
    }
    
    try {
      const response = await api.post(`/shifts/${currentShift.id}/end`);
      
      console.log('üì• ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™:', response.data);
      
      if (response.data && response.data.success) {
        showNotification('success', 'ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ÿ®ŸÜÿ¨ÿßÿ≠!');
        setCurrentShift(null);
        setShiftStats({
          total_rentals: 0,
          total_revenue: 0,
          unique_customers: 0,
          active_rentals: 0
        });
        setAvailableGames([]);
        setActiveRentals([]);
      } else {
        showNotification('success', 'ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™');
        setCurrentShift(null);
        setShiftStats({
          total_rentals: 0,
          total_revenue: 0,
          unique_customers: 0,
          active_rentals: 0
        });
        setAvailableGames([]);
        setActiveRentals([]);
      }
      
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™:', error);
      showNotification('error', 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™');
    }
  };

  // ==================== LOAD DATA ====================

  const loadData = async () => {
    try {
      console.log('üì• ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÜÿ∏ÿßŸÖ...');
      
      // ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ŸÅŸÇÿ∑ ÿ•ÿ∞ÿß ŸÉÿßŸÜ ŸáŸÜÿßŸÉ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑
      if (currentShift) {
        console.log('üìä ÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑŸÜÿ¥ÿ∑...');
        await Promise.all([
          loadAvailableGames(),
          loadActiveRentals(),
          loadShiftStats(currentShift.id)
        ]);
      } else {
        console.log('‚è∏Ô∏è ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑ - ÿ™ÿÆÿ∑Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™');
      }
      
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:', error);
      showNotification('error', 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ÿπÿ∂ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™');
    }
  };

  const loadAvailableGames = async () => {
    try {
      console.log('üéÆ ÿ¨ŸÑÿ® ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©...');
      
      const response = await api.get('/games', {
        params: {
          branch_id: user.branch_id,
          status: 'ŸÖÿ™ÿßÿ≠'
        }
      });
      
      console.log('üì• ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿßŸÑÿ£ŸÑÿπÿßÿ®:', response.data);
      
      if (response.data && response.data.success) {
        setAvailableGames(response.data.data);
      } else if (response.data && Array.isArray(response.data)) {
        setAvailableGames(response.data);
      } else {
        console.warn('‚ö†Ô∏è ÿ™ŸÜÿ≥ŸäŸÇ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ');
        setAvailableGames([]);
      }
      
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ£ŸÑÿπÿßÿ®:', error);
      setAvailableGames([]);
    }
  };

  const loadActiveRentals = async () => {
    try {
      console.log('‚è±Ô∏è ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©...');
      
      const response = await api.get('/rentals', {
        params: {
          branch_id: user.branch_id,
          status: 'ŸÜÿ¥ÿ∑',
          shift_id: currentShift?.id
        }
      });
      
      console.log('üì• ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™:', response.data);
      
      let rentalsData = [];
      
      if (response.data && response.data.success && Array.isArray(response.data.data)) {
        rentalsData = response.data.data;
      } else if (response.data && Array.isArray(response.data)) {
        rentalsData = response.data;
      }
      
      console.log(`‚úÖ ÿ™ŸÖ ÿ¨ŸÑÿ® ${rentalsData.length} ÿ™ÿ£ÿ¨Ÿäÿ± ŸÜÿ¥ÿ∑`);
      setActiveRentals(rentalsData);
      
      // ÿ™ÿ≠ÿØŸäÿ´ ÿπÿØÿØ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ© ŸÅŸä shiftStats
      setShiftStats(prev => ({
        ...prev,
        active_rentals: rentalsData.length
      }));
      
      // ÿ™ŸÜÿ∏ŸäŸÅ ÿßŸÑŸÖÿ§ŸÇÿ™ÿßÿ™ ÿßŸÑŸÇÿØŸäŸÖÿ©
      Object.values(timersRef.current).forEach(timer => clearInterval(timer));
      timersRef.current = {};
      
      // ÿ•ŸÜÿ¥ÿßÿ° ŸÖÿ§ŸÇÿ™ÿßÿ™ ÿ¨ÿØŸäÿØÿ© ŸÑŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™
      rentalsData.forEach(rental => {
        startTimerForRental(rental);
      });
      
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™:', error);
      setActiveRentals([]);
    }
  };

  const startTimerForRental = (rental) => {
    if (!rental.expected_end_time || rental.duration_minutes === 0) return;
    
    const endTime = new Date(rental.expected_end_time);
    const now = new Date();
    const timeLeft = endTime - now;
    
    if (timeLeft <= 0) {
      completeExpiredRental(rental.id);
      return;
    }
    
    const timerId = setInterval(() => {
      const now = new Date();
      const timeLeft = endTime - now;
      
      if (timeLeft <= 0) {
        clearInterval(timersRef.current[rental.id]);
        delete timersRef.current[rental.id];
        completeExpiredRental(rental.id);
      }
    }, 60000);
    
    timersRef.current[rental.id] = timerId;
  };

  // ==================== MULTI-GAME RENTAL ====================

  const addGameToRental = (game) => {
    const existingIndex = selectedGames.findIndex(g => g.id === game.id);
    
    if (existingIndex === -1) {
      // ÿ•ÿ∂ÿßŸÅÿ© ŸÑÿπÿ®ÿ© ÿ¨ÿØŸäÿØÿ©
      const maxQuantity = Math.min(game.quantity || 1, 5);
      setSelectedGames([...selectedGames, { ...game, rentalId: Date.now() }]);
      setQuantities({ ...quantities, [game.id]: 1 });
    } else {
      // ÿ≤ŸäÿßÿØÿ© ŸÉŸÖŸäÿ© ŸÑÿπÿ®ÿ© ŸÖŸàÿ¨ŸàÿØÿ©
      const currentQuantity = quantities[game.id] || 1;
      const maxQuantity = Math.min(game.quantity || 1, 5);
      
      if (currentQuantity < maxQuantity) {
        setQuantities({ ...quantities, [game.id]: currentQuantity + 1 });
      }
    }
    
    // ŸÅÿ™ÿ≠ ŸÜŸÖŸàÿ∞ÿ¨ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ™ŸÑŸÇÿßÿ¶ŸäÿßŸã ÿ•ÿ∞ÿß ŸÑŸÖ ŸäŸÉŸÜ ŸÖŸÅÿ™Ÿàÿ≠ÿßŸã
    if (!showRentalForm) {
      setShowRentalForm(true);
    }
  };

  const removeGameFromRental = (gameId) => {
    const currentQuantity = quantities[gameId] || 1;
    
    if (currentQuantity > 1) {
      setQuantities({ ...quantities, [gameId]: currentQuantity - 1 });
    } else {
      setSelectedGames(selectedGames.filter(game => game.id !== gameId));
      const newQuantities = { ...quantities };
      delete newQuantities[gameId];
      setQuantities(newQuantities);
    }
  };

  const calculateTotalPrice = () => {
    let total = 0;
    
    selectedGames.forEach(game => {
      const quantity = quantities[game.id] || 1;
      let gamePrice = 0;
      
      if (selectedDuration >= 60) {
        gamePrice = (game.price_per_hour || 0) * (selectedDuration / 60);
      } else {
        const quarters = Math.ceil(selectedDuration / 15);
        gamePrice = (game.price_per_15min || 0) * quarters;
      }
      
      total += gamePrice * quantity;
    });
    
    return total * (1 - discount / 100);
  };

  const handleStartMultiRental = async () => {
    if (!validateForm()) return;
    if (!currentShift) {
      showNotification('error', 'Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ£ŸàŸÑÿßŸã');
      return;
    }
    
    try {
      const rentalGames = selectedGames.map(game => ({
        game_id: game.id,
        quantity: quantities[game.id] || 1,
        duration_minutes: selectedDuration
      }));
      
      const rentalData = {
        games: rentalGames,
        customer_name: customerName.trim(),
        customer_phone: customerPhone.trim(),
        shift_id: currentShift.id,
        payment_method: paymentMethod,
        notes: customerNotes,
        discount: discount
      };
      
      const response = await api.post('/rentals/multi', rentalData);
      
      if (response.data.success) {
        showNotification('success', response.data.message);
        
        // ÿ•ÿπÿßÿØÿ© ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
        await loadAvailableGames();
        await loadActiveRentals();
        await loadShiftStats(currentShift.id);
        
        // ÿ•ÿπÿßÿØÿ© ÿ™ÿπŸäŸäŸÜ ÿßŸÑŸÜŸÖŸàÿ∞ÿ¨
        resetForm();
        setShowRentalForm(false);
      }
    } catch (error) {
      showNotification('error', error.response?.data?.message || 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ®ÿØÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
    }
  };

  // ==================== RENTAL OPERATIONS ====================

  const completeExpiredRental = async (rentalId) => {
    try {
      const response = await api.put(`/rentals/${rentalId}/complete`);
      if (response.data.success) {
        console.log(`Rental ${rentalId} completed automatically`);
        await loadActiveRentals();
        await loadAvailableGames();
        if (currentShift) {
          await loadShiftStats(currentShift.id);
        }
      }
    } catch (error) {
      console.error('Error completing rental:', error);
    }
  };

  const handleEndRental = async (rentalId) => {
    try {
      const rental = activeRentals.find(r => r.id === rentalId);
      if (!rental) return;
      
      if (rental.duration_minutes === 0) {
        const finalAmount = prompt('ÿ£ÿØÿÆŸÑ ÿßŸÑÿ≥ÿπÿ± ÿßŸÑŸÜŸáÿßÿ¶Ÿä:', rental.total_amount);
        if (!finalAmount || isNaN(finalAmount)) {
          showNotification('error', 'ÿßŸÑÿ≥ÿπÿ± ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠');
          return;
        }
        
        await api.put(`/rentals/${rentalId}/complete`, {
          final_amount: parseFloat(finalAmount)
        });
      } else {
        if (!window.confirm(`ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÜŸáÿßÿ° ÿ™ÿ£ÿ¨Ÿäÿ± #${rental.rental_number}ÿü`)) return;
        
        await api.put(`/rentals/${rentalId}/complete`);
      }
      
      showNotification('success', `ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠`);
      await loadActiveRentals();
      await loadAvailableGames();
      if (currentShift) {
        await loadShiftStats(currentShift.id);
      }
    } catch (error) {
      showNotification('error', 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
    }
  };

  // ==================== HELPER FUNCTIONS ====================

  const validateForm = () => {
    if (!customerName.trim()) {
      showNotification('error', 'ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ•ÿØÿÆÿßŸÑ ÿßÿ≥ŸÖ ÿßŸÑÿπŸÖŸäŸÑ');
      return false;
    }
    
    if (!customerPhone.trim()) {
      showNotification('error', 'ÿßŸÑÿ±ÿ¨ÿßÿ° ÿ•ÿØÿÆÿßŸÑ ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ');
      return false;
    }
    
    if (!/^01[0-9]{9}$/.test(customerPhone)) {
      showNotification('error', 'ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ ÿ∫Ÿäÿ± ÿµÿ≠Ÿäÿ≠');
      return false;
    }
    
    if (selectedGames.length === 0) {
      showNotification('error', 'ÿßŸÑÿ±ÿ¨ÿßÿ° ÿßÿÆÿ™Ÿäÿßÿ± ŸÑÿπÿ®ÿ© Ÿàÿßÿ≠ÿØÿ© ÿπŸÑŸâ ÿßŸÑÿ£ŸÇŸÑ');
      return false;
    }
    
    return true;
  };

  const resetForm = () => {
    setCustomerName('');
    setCustomerPhone('');
    setCustomerNotes('');
    setSelectedGames([]);
    setQuantities({});
    setDurationType('fixed');
    setSelectedDuration(60);
    setPaymentMethod('cash');
