    alert(`${types[type]} ${message}`);
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0
    }).format(amount || 0);
  };

  const formatDuration = (minutes) => {
    if (!minutes || minutes === 0) return 'ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠';
    if (minutes < 60) return `${minutes} ÿØŸÇŸäŸÇÿ©`;
    
    const hours = Math.floor(minutes / 60);
    const remaining = minutes % 60;
    return `${hours} ÿ≥ÿßÿπÿ© ${remaining > 0 ? remaining + ' ÿØŸÇŸäŸÇÿ©' : ''}`;
  };

  const formatDateTime = (dateString) => {
    try {
      const date = new Date(dateString);
      return date.toLocaleString('ar-EG', {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      return 'ÿ™ÿßÿ±ŸäÿÆ ÿ∫Ÿäÿ± ŸÖÿπÿ±ŸàŸÅ';
    }
  };

  const calculateTimeRemaining = (rental) => {
    if (!rental.expected_end_time) return null;
    
    try {
      const endTime = new Date(rental.expected_end_time);
      const now = new Date();
      const diff = endTime - now;
      
      if (diff <= 0) return { expired: true, text: 'ÿßŸÜÿ™ŸáŸâ' };
      
      const minutes = Math.floor(diff / 60000);
      if (minutes < 60) return { expired: false, text: `${minutes} ÿØŸÇŸäŸÇÿ©` };
      
      const hours = Math.floor(minutes / 60);
      const remaining = minutes % 60;
      return { expired: false, text: `${hours} ÿ≥ÿßÿπÿ© ${remaining} ÿØŸÇŸäŸÇÿ©` };
    } catch (error) {
      return null;
    }
  };

  const calculateShiftDuration = () => {
    if (!currentShift || !currentShift.start_time) return '0 ÿØŸÇŸäŸÇÿ©';
    
    try {
      const startTime = new Date(currentShift.start_time);
      const now = new Date();
      const diff = now - startTime;
      const minutes = Math.floor(diff / 60000);
      
      if (minutes < 60) return `${minutes} ÿØŸÇŸäŸÇÿ©`;
      
      const hours = Math.floor(minutes / 60);
      const remaining = minutes % 60;
      return `${hours} ÿ≥ÿßÿπÿ© ${remaining > 0 ? remaining + ' ÿØŸÇŸäŸÇÿ©' : ''}`;
    } catch (error) {
      return '0 ÿØŸÇŸäŸÇÿ©';
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...</p>
      </div>
    );
  }

  return (
    <div className="rentals-page">
      {/* üîß ŸÇÿ≥ŸÖ ÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿ¥ŸäŸÅÿ™ */}
      <div className="shift-section">
        {currentShift ? (
          <div className="active-shift">
            <div className="shift-header">
              <h2>‚è±Ô∏è ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑ #{currentShift.id}</h2>
              <div className="shift-time">
                <span>ÿ®ÿØÿ£: {formatDateTime(currentShift.start_time)}</span>
                <span>ÿßŸÑŸÖÿØÿ©: {calculateShiftDuration()}</span>
              </div>
            </div>
            
            <div className="shift-stats">
              <div className="stat-item">
                <span className="stat-label">ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™:</span>
                <span className="stat-value">{shiftStats.total_rentals || 0}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">ÿßŸÑÿ•Ÿäÿ±ÿßÿØÿßÿ™:</span>
                <span className="stat-value">{formatCurrency(shiftStats.total_revenue || 0)}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">ÿßŸÑÿπŸÖŸÑÿßÿ°:</span>
                <span className="stat-value">{shiftStats.unique_customers || 0}</span>
              </div>
              <div className="stat-item">
                <span className="stat-label">ÿßŸÑŸÜÿ¥ÿ∑ÿ©:</span>
                <span className="stat-value">{shiftStats.active_rentals || activeRentals.length}</span>
              </div>
            </div>
            
            <div className="shift-actions">
              <button 
                className="btn-end-shift"
                onClick={endCurrentShift}
              >
                üèÅ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™
              </button>
              <button 
                className="btn-view-stats"
                onClick={viewDetailedShiftStats}
              >
                üìä ÿπÿ±ÿ∂ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™ ÿßŸÑŸÉÿßŸÖŸÑÿ©
              </button>
              <button 
                className="btn-refresh"
                onClick={() => {
                  initializeShift();
                  loadData();
                }}
              >
                üîÑ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
              </button>
            </div>
          </div>
        ) : (
          <div className="no-shift">
            <div className="no-shift-content">
              <h3>üì≠ ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑</h3>
              <p>Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØ ŸÑŸÑÿ®ÿØÿ£ ŸÅŸä ÿ®Ÿäÿπ Ÿàÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑÿ£ŸÑÿπÿßÿ®</p>
              <div className="shift-info">
                <div>
                  <strong>ÿßŸÑŸÅÿ±ÿπ: </strong>
                  {user?.branch_name || `ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ`}
                </div>
                <div>
                  <strong>ÿßŸÑŸÖŸàÿ∏ŸÅ: </strong>
                  {user?.name || `ÿ∫Ÿäÿ± ŸÖÿ≠ÿØÿØ`}
                </div>
              </div>
              
              <div className="shift-buttons">
                <button 
                  className="btn-start-shift"
                  onClick={startNewShift}
                >
                  üöÄ ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØ
                </button>
                
                <button 
                  className="btn-check-shift"
                  onClick={initializeShift}
                >
                  üîç ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ¥ŸäŸÅÿ™ÿßÿ™
                </button>
              </div>
              
              <div className="shift-instructions">
                <h4>üìã ÿßŸÑÿ™ÿπŸÑŸäŸÖÿßÿ™:</h4>
                <ol>
                  <li>ÿßÿ∂ÿ∫ÿ∑ ÿπŸÑŸâ "ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØ"</li>
                  <li>ÿ≥Ÿäÿ™ŸÖ ÿ™ÿ≥ÿ¨ŸäŸÑ ŸàŸÇÿ™ ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™</li>
                  <li>ŸäŸÖŸÉŸÜŸÉ ÿßŸÑÿ¢ŸÜ ÿ®Ÿäÿπ Ÿàÿ™ÿ£ÿ¨Ÿäÿ± ÿßŸÑÿ£ŸÑÿπÿßÿ®</li>
                  <li>ÿßÿ∂ÿ∫ÿ∑ ÿπŸÑŸâ "ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™" ÿπŸÜÿØ ÿßŸÜÿ™Ÿáÿßÿ° ÿßŸÑÿπŸÖŸÑ</li>
                </ol>
              </div>
            </div>
          </div>
        )}
      </div>

      {/* üéÆ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© */}
      <div className="games-section">
        <div className="section-header">
          <h2>üéÆ ÿßŸÑÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÖÿ™ÿßÿ≠ÿ© ({availableGames.length})</h2>
          <div className="selected-games-info">
            {selectedGames.length > 0 && (
              <button 
                className="btn-start-rental"
                onClick={() => setShowRentalForm(true)}
              >
                üöÄ ÿ™ÿ£ÿ¨Ÿäÿ± ({selectedGames.length})
              </button>
            )}
          </div>
        </div>
        
        <div className="games-grid">
          {availableGames.length === 0 ? (
            <div className="empty-state">
              <p>üéÆ ŸÑÿß ÿ™Ÿàÿ¨ÿØ ÿ£ŸÑÿπÿßÿ® ŸÖÿ™ÿßÿ≠ÿ© ÿ≠ÿßŸÑŸäÿßŸã</p>
              {!currentShift && (
                <p className="hint">üí° Ÿäÿ¨ÿ® ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ£ŸàŸÑÿßŸã ŸÑÿπÿ±ÿ∂ ÿßŸÑÿ£ŸÑÿπÿßÿ®</p>
              )}
            </div>
          ) : (
            availableGames.map(game => (
              <div key={game.id} className="game-card">
                <div className="game-image">
                  <img 
                    src={`/images/${game.image_url || 'default-game.jpg'}`}
                    alt={game.name}
                  />
                  <div className="game-status">
                    <span className={`status-badge ${game.status === 'ŸÖÿ™ÿßÿ≠' ? 'available' : 'rented'}`}>
                      {game.status === 'ŸÖÿ™ÿßÿ≠' ? '‚úÖ ŸÖÿ™ÿßÿ≠' : '‚è±Ô∏è ŸÖÿ§ÿ¨ÿ±ÿ©'}
                    </span>
                  </div>
                </div>
                
                <div className="game-info">
                  <h3>{game.name}</h3>
                  <p className="game-category">{game.category}</p>
                  
                  <div className="game-pricing">
                    <div className="price-item">
                      <span>ÿ≥ÿπÿ± ÿßŸÑÿ≥ÿßÿπÿ©:</span>
                      <span>{formatCurrency(game.price_per_hour)}</span>
                    </div>
                    <div className="price-item">
                      <span>ÿ≥ÿπÿ± ÿßŸÑÿ±ÿ®ÿπ:</span>
                      <span>{formatCurrency(game.price_per_15min)}</span>
                    </div>
                  </div>
                  
                  <div className="game-quantity">
                    <span>ÿßŸÑŸÉŸÖŸäÿ© ÿßŸÑŸÖÿ™ÿßÿ≠ÿ©: {game.quantity || 1}</span>
                  </div>
                  
                  <button 
