import { useState, useEffect } from 'react';
import authService from '../../services/authService';
import './AdminPages.css';
import api from '../../services/api';

// ğŸ”¹ Ø§Ø³ØªÙŠØ±Ø§Ø¯ Ø§Ù„ØµÙˆØ± Ø¨Ø£Ù…Ø§Ù†
const importImage = (path) => {
    try {
        return require(`../../images/${path}`);
    } catch (error) {
        console.warn(`âš ï¸ Ù„Ù… ÙŠØªÙ… Ø§Ù„Ø¹Ø«ÙˆØ± Ø¹Ù„Ù‰ Ø§Ù„ØµÙˆØ±Ø©: ${path}`);
        return require('../../images/default-game.jpg');
    }
};

// ğŸ”¹ Ø§Ù„ØµÙˆØ± Ø§Ù„Ø§ÙØªØ±Ø§Ø¶ÙŠØ©
const defaultGameImg = importImage('default-game.jpg');
const driftCarImg = importImage('drift-car.jpg');
const hoverboardImg = importImage('hoverboard.jpg');
const electricBikeImg = importImage('electric-bike.jpg');
const electricCarImg = importImage('electric-car.jpg');
const electricScooterImg = importImage('electric-scooter.jpg');
const harleyImg = importImage('harley.jpg');
const segwayImg = importImage('segway.jpg');
const crazyCarImg = importImage('crazy-car.jpg');

// ğŸ”¹ ÙƒØ§Ø¦Ù† Ø§Ù„ØµÙˆØ± Ø¨Ø£Ù…Ø§Ù†
const gameImages = {
    'drift-car.jpg': driftCarImg,
    'hoverboard.jpg': hoverboardImg,
    'electric-bike.jpg': electricBikeImg,
    'electric-car.jpg': electricCarImg,
    'electric-scooter.jpg': electricScooterImg,
    'harley.jpg': harleyImg,
    'segway.jpg': segwayImg,
    'crazy-car.jpg': crazyCarImg,
    'default-game.jpg': defaultGameImg
};

// ğŸ”¹ Ø¯Ø§Ù„Ø© Ø§Ø­ØªØ±Ø§ÙÙŠØ© Ù„Ù„Ø­ØµÙˆÙ„ Ø¹Ù„Ù‰ ØµÙˆØ±Ø© Ø§Ù„Ù„Ø¹Ø¨Ø©
const getGameImage = (game) => {
    // ğŸ”¹ Ø§Ù„ØªØ­Ù‚Ù‚ Ø§Ù„Ø£Ø³Ø§Ø³ÙŠ
    if (!game || typeof game !== 'object') {
        console.warn('âš ï¸ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù„Ø¹Ø¨Ø© ØºÙŠØ± ØµØ§Ù„Ø­Ø©:', game);
        return defaultGameImg;
    }

    // ğŸ”¹ Ø£Ø®Ø° Ù†Ø³Ø®Ø© Ø¢Ù…Ù†Ø© Ù…Ù† Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª
    const safeGame = {
        name: game?.name ? String(game.name).trim() : '',
        image_url: game?.image_url || '',
        display_image: game?.display_image || '',
        thumbnail_url: game?.thumbnail_url || '',
        image_status: game?.image_status || 'completed',
        category: game?.category || ''
    };

    console.log('ğŸ” Ø§Ù„Ø¨Ø­Ø« Ø¹Ù† ØµÙˆØ±Ø© Ù„Ù„Ø¹Ø¨Ø©:', safeGame.name, safeGame.image_status);

    // 1. ğŸ”¹ Ø§Ø³ØªØ®Ø¯Ø§Ù… display_image Ù…Ù† Ø§Ù„Ø®Ø§Ø¯Ù… (Ø§Ù„Ø£ÙˆÙ„ÙˆÙŠØ© Ø§Ù„Ø£ÙˆÙ„Ù‰)
    if (safeGame.display_image && safeGame.display_image !== '/images/default-game.jpg') {
        console.log('âœ… Ø§Ø³ØªØ®Ø¯Ø§Ù… display_image:', safeGame.display_image);
        
        // Ø§Ù„ØªØ­Ù‚Ù‚ Ø¥Ø°Ø§ ÙƒØ§Ù†Øª Ø§Ù„ØµÙˆØ±Ø© Ù…ÙˆØ¬ÙˆØ¯Ø© Ù…Ø­Ù„ÙŠØ§Ù‹
        if (safeGame.display_image.startsWith('/images/')) {
            const imageName = safeGame.display_image.replace('/images/', '');
            if (gameImages[imageName]) {
                return gameImages[imageName];
            }
        }
        
        // Ø¥Ø°Ø§ ÙƒØ§Ù†Øª URL ÙƒØ§Ù…Ù„Ø©
        if (safeGame.display_image.startsWith('http')) {
            return safeGame.display_image;
        }
        
        // Ø¥Ø°Ø§ ÙƒØ§Ù†Øª Ù…Ø³Ø§Ø± Ù†Ø³Ø¨ÙŠ
        return safeGame.display_image;
    }

    // 2. ğŸ”¹ Ø§Ø³ØªØ®Ø¯Ø§Ù… image_url Ø¥Ø°Ø§ ÙƒØ§Ù†Øª Ù…ÙƒØªÙ…Ù„Ø©
    if (safeGame.image_url && safeGame.image_status === 'completed') {
        console.log('âœ… Ø§Ø³ØªØ®Ø¯Ø§Ù… image_url:', safeGame.image_url);
        
        // ØªÙ†Ø¸ÙŠÙ Ø§Ù„Ø±Ø§Ø¨Ø·
        let cleanUrl = String(safeGame.image_url).trim();
        
        if (cleanUrl.startsWith('/images/')) {
            const imageName = cleanUrl.replace('/images/', '');
            if (gameImages[imageName]) {
                return gameImages[imageName];
            }
            return cleanUrl;
        }
        
        if (!cleanUrl.startsWith('http') && !cleanUrl.startsWith('/')) {
            cleanUrl = `/images/${cleanUrl}`;
        }
        
        return cleanUrl;
    }

    // 3. ğŸ”¹ Ø¥Ø°Ø§ ÙƒØ§Ù†Øª Ø§Ù„ØµÙˆØ±Ø© Ù‚ÙŠØ¯ Ø§Ù„Ù…Ø¹Ø§Ù„Ø¬Ø©
    if (safeGame.image_status === 'processing') {
        console.log('â³ Ø§Ù„ØµÙˆØ±Ø© Ù‚ÙŠØ¯ Ø§Ù„Ù…Ø¹Ø§Ù„Ø¬Ø©');
        return '/images/loading-game.gif'; // Ø£Ùˆ return defaultGameImg;
    }

    // 4. ğŸ”¹ Ø§Ù„Ø¨Ø­Ø« Ø¨Ø§Ù„Ø§Ø³Ù… ÙÙŠ gameImages
    if (safeGame.name) {
        const cleanName = safeGame.name.toLowerCase();
        
        for (const [key, img] of Object.entries(gameImages)) {
            const keyLower = key.toLowerCase();
            if (cleanName.includes(keyLower) || keyLower.includes(cleanName)) {
                console.log(`âœ… ØªÙ… Ø§Ù„Ø¹Ø«ÙˆØ± Ø¹Ù„Ù‰ Ù…Ø·Ø§Ø¨Ù‚Ø© Ø¨Ø§Ù„Ø§Ø³Ù…: ${key}`);
                return img;
            }
        }
        
        // ğŸ”¹ Ø§Ù„Ø¨Ø­Ø« Ø¨Ø§Ù„Ø£Ø³Ù…Ø§Ø¡ Ø§Ù„Ø´Ø§Ø¦Ø¹Ø©
        const nameMapping = {
            'Ø¯Ø±ÙŠÙØª': driftCarImg,
            'Ù‡Ø§ÙØ±': hoverboardImg,
            'Ù‡ÙˆÙØ±Ø¨ÙˆØ±Ø¯': hoverboardImg,
            'Ø³ÙƒÙˆØªØ±': electricScooterImg,
            'Ø¹Ø±Ø¨Ø©': electricCarImg,
            'Ø³ÙŠØ§Ø±Ø©': electricCarImg,
            'Ù…ÙˆØªÙˆØ³ÙŠÙƒÙ„': electricBikeImg,
            'Ø¯Ø±Ø§Ø¬Ø©': electricBikeImg,
            'Ù‡Ø§Ø±Ù„ÙŠ': harleyImg,
            'Ø³ÙŠØ¬ÙˆØ§ÙŠ': segwayImg,
            'ÙƒØ±Ø²ÙŠ': crazyCarImg
        };
        
        for (const [keyword, img] of Object.entries(nameMapping)) {
            if (cleanName.includes(keyword.toLowerCase())) {
                console.log(`âœ… ØªÙ… Ø§Ù„Ø¹Ø«ÙˆØ± Ø¹Ù„Ù‰ Ù…Ø·Ø§Ø¨Ù‚Ø© Ø¨Ø§Ù„ÙƒÙ„Ù…Ø©: ${keyword}`);
                return img;
            }
        }
    }

    // 5. ğŸ”¹ Ø§Ù„Ø¨Ø­Ø« Ø¨Ø§Ù„ØªØµÙ†ÙŠÙ
    if (safeGame.category) {
        const categoryMap = {
