import React, { useState, useEffect } from 'react';
import authService from '../../services/authService';
import './AdminPages.css';

const BranchesManagement = () => {
  const [branches, setBranches] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [showGamesModal, setShowGamesModal] = useState(false);
  const [selectedBranch, setSelectedBranch] = useState(null);
  const [availableGames, setAvailableGames] = useState([]);
  const [branchGames, setBranchGames] = useState([]);
  
  const [newBranch, setNewBranch] = useState({
    name: '',
    location: '',
    city: 'القاهرة',
    contact_phone: '',
    contact_email: '',
    opening_time: '10:00:00',
    closing_time: '22:00:00'
  });

  // قائمة الألعاب المتاحة للإضافة
  const defaultGamesList = [
    { id: 1, name: 'دريفت كار', category: 'سيارات', price: 50, image: 'drift-car.jpg' },
    { id: 2, name: 'هافر بورد', category: 'كهربائية', price: 80, image: 'hoverboard.jpg' },
    { id: 3, name: 'موتوسيكل كهربائي', category: 'دراجات نارية', price: 120, image: 'electric-bike.jpg' },
    { id: 4, name: 'عربة كهربائية', category: 'سيارات', price: 200, image: 'electric-car.jpg' },
    { id: 5, name: 'سكوتر كهربائي', category: 'كهربائية', price: 70, image: 'electric-scooter.jpg' },
    { id: 6, name: 'هارلي', category: 'دراجات نارية', price: 250, image: 'harley.jpg' },
    { id: 7, name: 'سيجواي', category: 'كهربائية', price: 90, image: 'segway.jpg' }
  ];

  const loadBranches = async () => {
    try {
      setLoading(true);
      setError('');
      
      const response = await authService.getBranches();
      
      if (response.success) {
        setBranches(response.data);
      } else {
        setError(response.message || 'فشل في تحميل الفروع');
      }
    } catch (error) {
      setError('تعذر الاتصال بالخادم');
      console.error('Error loading branches:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadBranches();
  }, []);

  const handleAddBranch = async () => {
    if (!newBranch.name || !newBranch.location) {
      alert('اسم الفرع والموقع مطلوبان');
      return;
    }

    try {
      const response = await authService.createBranch(newBranch);
      
      if (response.success) {
        alert('تم إنشاء الفرع بنجاح');
        setShowAddModal(false);
        setNewBranch({
          name: '',
          location: '',
          city: 'القاهرة',
          contact_phone: '',
          contact_email: '',
          opening_time: '10:00:00',
          closing_time: '22:00:00'
        });
        loadBranches();
      } else {
        alert(response.message || 'فشل في إنشاء الفرع');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  const handleToggleStatus = async (id, currentStatus) => {
    try {
      const response = await authService.updateBranch(id, {
        is_active: currentStatus ? 0 : 1
      });
      
      if (response.success) {
        loadBranches();
      } else {
        alert(response.message || 'فشل في تحديث الحالة');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  const handleDeleteBranch = async (id) => {
    if (!window.confirm('هل تريد حذف هذا الفرع نهائياً؟')) return;

    try {
      const response = await authService.deleteBranch(id);
      
      if (response.success) {
        alert('تم حذف الفرع بنجاح');
        loadBranches();
      } else {
        alert(response.message || 'فشل في حذف الفرع');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  const handleOpenGamesModal = async (branch) => {
    setSelectedBranch(branch);
    setAvailableGames(defaultGamesList);
    setBranchGames([]); // سيتم جلبها من الخادم لاحقاً
    setShowGamesModal(true);
  };

  const handleAddGameToBranch = async (game) => {
    if (!selectedBranch) return;
    
    const price = prompt(`أدخل سعر الساعة للعبة "${game.name}" بالجنيه:`, game.price);
    
    if (!price || isNaN(price)) {
      alert('يرجى إدخال سعر صحيح');
      return;
    }

    try {
      // هنا سيتم إضافة اللعبة للفرع
      alert(`تم إضافة ${game.name} للفرع ${selectedBranch.name} بسعر ${price} جنيه/ساعة`);
      // في الواقع: await authService.addGameToBranch(selectedBranch.id, {...game, price_per_hour: price});
    } catch (error) {
      alert('تعذر إضافة اللعبة');
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>جاري تحميل الفروع...</p>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="page-header">
        <h1>🏬 إدارة الفروع</h1>
        <button 
          className="btn-add"
          onClick={() => setShowAddModal(true)}
        >
          + إضافة فرع جديد
        </button>
      </div>

      {error && (
        <div className="alert alert-error">
          {error}
        </div>
      )}

      <div className="branches-grid">
        {branches.map(branch => (
          <div key={branch.id} className="branch-card">
            <div className="branch-header">
              <div>
                <h3>{branch.name}</h3>
                <span className="branch-id">ID: {branch.id}</span>
              </div>
              <span className={`status-badge ${branch.is_active ? 'active' : 'inactive'}`}>
                {branch.is_active ? 'نشط' : 'غير نشط'}
              </span>
            </div>
            
            <div className="branch-info">
              <div className="info-item">
                <span className="label">الموقع:</span>
                <span className="value">{branch.location}</span>
              </div>
              
              <div className="info-item">
                <span className="label">المدينة:</span>
                <span className="value">{branch.city}</span>
              </div>
              
              <div className="info-item">
                <span className="label">أوقات العمل:</span>
                <span className="value">
                  {branch.opening_time?.substring(0, 5)} - {branch.closing_time?.substring(0, 5)}
                </span>
              </div>
              
              <div className="info-item">
                <span className="label">الهاتف:</span>
                <span className="value">{branch.contact_phone || 'غير محدد'}</span>
              </div>
              
              <div className="info-item">
                <span className="label">البريد:</span>
                <span className="value">{branch.contact_email || 'غير محدد'}</span>
              </div>
              
              <div className="info-item">
                <span className="label">تاريخ الإنشاء:</span>
                <span className="value">
                  {new Date(branch.created_at).toLocaleDateString('ar-SA')}
                </span>
              </div>

              <div className="info-item">
                <span className="label">عدد الألعاب:</span>
                <span className="value">{branch.total_games || 0} لعبة</span>
              </div>
            </div>

            <div className="branch-actions">
              <button 
                className="btn-manage"
                onClick={() => handleOpenGamesModal(branch)}
              >
                🎮 إدارة الألعاب
              </button>
              <button 
                className={`btn-status ${branch.is_active ? 'deactivate' : 'activate'}`}
                onClick={() => handleToggleStatus(branch.id, branch.is_active)}
              >
                {branch.is_active ? 'إيقاف' : 'تفعيل'}
              </button>
              <button 
                className="btn-delete"
                onClick={() => handleDeleteBranch(branch.id)}
              >
                حذف
              </button>
            </div>
          </div>
        ))}
      </div>

      {/* Modal إضافة فرع */}
      {showAddModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2>🏬 إضافة فرع جديد</h2>
              <button 
                className="close-btn"
                onClick={() => setShowAddModal(false)}
              >
                ×
              </button>
            </div>
            <div className="modal-body">
              <div className="form-group">
                <label>اسم الفرع *</label>
                <input
                  type="text"
                  value={newBranch.name}
                  onChange={(e) => setNewBranch({...newBranch, name: e.target.value})}
                  placeholder="أدخل اسم الفرع"
                />
              </div>
              
              <div className="form-group">
                <label>الموقع *</label>
                <input
                  type="text"
                  value={newBranch.location}
                  onChange={(e) => setNewBranch({...newBranch, location: e.target.value})}
                  placeholder="أدخل العنوان بالتفصيل"
                />
              </div>
              
              <div className="form-row">
                <div className="form-group">
                  <label>المدينة</label>
                  <input
                    type="text"
                    value={newBranch.city}
                    onChange={(e) => setNewBranch({...newBranch, city: e.target.value})}
                    placeholder="المدينة"
                  />
                </div>
                
                <div className="form-group">
                  <label>الهاتف</label>
                  <input
                    type="tel"
                    value={newBranch.contact_phone}
                    onChange={(e) => setNewBranch({...newBranch, contact_phone: e.target.value})}
                    placeholder="رقم الهاتف"
                  />
                </div>
              </div>
              
              <div className="form-row">
                <div className="form-group">
                  <label>وقت الفتح</label>
                  <input
                    type="time"
                    value={newBranch.opening_time?.substring(0, 5)}
                    onChange={(e) => setNewBranch({...newBranch, opening_time: e.target.value + ':00'})}
                  />
                </div>
                
                <div className="form-group">
                  <label>وقت الإغلاق</label>
                  <input
                    type="time"
                    value={newBranch.closing_time?.substring(0, 5)}
                    onChange={(e) => setNewBranch({...newBranch, closing_time: e.target.value + ':00'})}
                  />
                </div>
              </div>
              
              <div className="form-group">
                <label>البريد الإلكتروني</label>
                <input
                  type="email"
                  value={newBranch.contact_email}
                  onChange={(e) => setNewBranch({...newBranch, contact_email: e.target.value})}
                  placeholder="example@domain.com"
                />
              </div>
            </div>
            <div className="modal-footer">
              <button 
                className="btn-cancel"
                onClick={() => setShowAddModal(false)}
              >
                إلغاء
              </button>
              <button 
                className="btn-save"
                onClick={handleAddBranch}
              >
                حفظ الفرع
              </button>
            </div>
          </div>
        </div>
      )}

      {/* Modal إدارة ألعاب الفرع */}
      {showGamesModal && selectedBranch && (
        <div className="modal-overlay">
          <div className="modal wide-modal">
            <div className="modal-header">
              <h2>🎮 إدارة ألعاب فرع: {selectedBranch.name}</h2>
              <button 
                className="close-btn"
                onClick={() => setShowGamesModal(false)}
              >
                ×
              </button>
            </div>
            <div className="modal-body">
              
              <div className="section">
                <h3>➕ إضافة ألعاب جديدة للفرع</h3>
                <p className="section-description">
                  اختر من الألعاب المتاحة وأضفها للفرع مع تحديد السعر الخاص بكل لعبة
                </p>
                
                <div className="available-games-grid">
                  {availableGames.map(game => (
                    <div key={game.id} className="available-game-card">
                      <div className="game-image">
                        <img 
                          src={`/images/${game.image}`}
                          alt={game.name}
                          onError={(e) => {
                            e.target.onerror = null;
                            e.target.src = "/images/default-game.jpg";
                          }}
                        />
                      </div>
                      <div className="game-details">
                        <h4>{game.name}</h4>
                        <p className="game-category">{game.category}</p>
                        <p className="game-suggested-price">السعر المقترح: {game.price} ج/ساعة</p>
                      </div>
                      <button 
                        className="btn-add-game"
                        onClick={() => handleAddGameToBranch(game)}
                      >
                        + إضافة للفرع
                      </button>
                    </div>
                  ))}
                </div>
              </div>

              <div className="section">
                <h3>📋 الألعاب المضافة حالياً للفرع</h3>
                {branchGames.length === 0 ? (
                  <div className="empty-state">
                    <p>لا توجد ألعاب مضافة لهذا الفرع بعد</p>
                    <p>قم بإضافة ألعاب من القائمة أعلاه</p>
                  </div>
                ) : (
                  <div className="branch-games-list">
                    <table className="games-table">
                      <thead>
                        <tr>
                          <th>اللعبة</th>
                          <th>التصنيف</th>
                          <th>السعر/ساعة</th>
                          <th>الحالة</th>
                          <th>الإجراءات</th>
                        </tr>
                      </thead>
                      <tbody>
                        {branchGames.map(game => (
                          <tr key={game.id}>
                            <td>
                              <div className="game-info-cell">
                                <img src={`/images/${game.image}`} alt={game.name} />
                                <span>{game.name}</span>
                              </div>
                            </td>
                            <td>{game.category}</td>
                            <td>{game.price} جنيه</td>
                            <td>
                              <span className={`status-badge ${game.status === 'متاح' ? 'available' : 'rented'}`}>
                                {game.status}
                              </span>
                            </td>
                            <td>
                              <button className="btn-edit-small">تعديل</button>
                              <button className="btn-remove-small">إزالة</button>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                )}
              </div>
            </div>
            <div className="modal-footer">
              <button 
                className="btn-cancel"
                onClick={() => setShowGamesModal(false)}
              >
                إغلاق
              </button>
              <button 
                className="btn-save"
                onClick={() => {
                  alert('تم حفظ التعديلات');
                  setShowGamesModal(false);
                }}
              >
                حفظ التعديلات
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default BranchesManagement;
