        const user = JSON.parse(userData);
        const loginResult = await api.login(user.email, '123456');
        
        if (loginResult.success) {
          console.log('‚úÖ ÿ™ŸÖ ÿ™ÿ¨ÿØŸäÿØ ÿßŸÑÿ¨ŸÑÿ≥ÿ©');
          return true;
        } else {
          // ÿ•ÿ∞ÿß ŸÅÿ¥ŸÑ ÿ•ÿπÿßÿØÿ© ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑÿå ÿ•ÿπÿßÿØÿ© ÿ™Ÿàÿ¨ŸäŸá
          localStorage.clear();
          window.location.href = '/login';
          return false;
        }
      }
    }
    
    return true;
  } catch (error) {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑŸÖÿµÿßÿØŸÇÿ©:', error);
