const handleCreateRental = async (rentalData) => {
  console.log('üìù ÿ•ŸÜÿ¥ÿßÿ° ÿ™ÿ£ÿ¨Ÿäÿ± ÿ¨ÿØŸäÿØ ÿ®ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÜŸÇÿ∑ÿ© ÿßŸÑÿ∞ŸÉŸäÿ©', rentalData);
  
  try {
    const item = rentalData.rentalItems[0];
    
    if (!item || !item.gameId) {
      alert('‚ùå ŸÑŸÖ Ÿäÿ™ŸÖ ÿßÿÆÿ™Ÿäÿßÿ± ŸÑÿπÿ®ÿ©');
      return;
    }
    
    const game = games.find(g => g.id === item.gameId);
    
    if (!game) {
      alert('‚ùå ÿßŸÑŸÑÿπÿ®ÿ© ÿ∫Ÿäÿ± ŸÖŸàÿ¨ŸàÿØÿ©');
