const GamesGrid = ({ games, branchId, onSelectGame, loading }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [availableGames, setAvailableGames] = useState([]);

  /* ===============================
     ŸÅŸÑÿ™ÿ±ÿ© ÿ£ŸÑÿπÿßÿ® ÿßŸÑŸÅÿ±ÿπ ÿßŸÑÿ≠ÿßŸÑŸä
  =============================== */
  useEffect(() => {
    if (!games?.length || !branchId) {
      setAvailableGames([]);
      return;
    }

    const currentBranchId = Number(branchId);

    const branchGames = games.filter(
      game => game && Number(game.branch_id) === currentBranchId
    );

    setAvailableGames(branchGames);
  }, [games, branchId]);

  /* ===============================
     ÿßŸÑÿ®ÿ≠ÿ´ ŸàÿßŸÑŸÅŸÑÿ™ÿ±ÿ©
  =============================== */
  const filteredGames = useMemo(() => {
    if (!availableGames.length) return [];

    const search = searchTerm.toLowerCase();

    return availableGames.filter(game => {
      const matchesSearch =
        game.name?.toLowerCase().includes(search) ||
        game.category?.toLowerCase().includes(search);

      const matchesCategory =
        selectedCategory === 'all' ||
        game.category === selectedCategory;

      return matchesSearch && matchesCategory;
    });
  }, [availableGames, searchTerm, selectedCategory]);

  /* ===============================
     ÿßÿ≥ÿ™ÿÆÿ±ÿßÿ¨ ÿßŸÑÿ™ÿµŸÜŸäŸÅÿßÿ™
  =============================== */
  const categories = useMemo(() => {
    if (!availableGames.length) return ['all'];

    const cats = new Set(['all']);
    availableGames.forEach(g => g.category && cats.add(g.category));

    return Array.from(cats);
  }, [availableGames]);

  /* ===============================
     ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿµŸàÿ±
  =============================== */
// ŸÅŸä Rentals.jsx - ŸÇŸÖ ÿ®ÿ™ÿπÿØŸäŸÑ ÿØÿßŸÑÿ© getGameImage
// ŸÅŸä Rentals.jsx - ÿØÿßŸÑÿ© getGameImage ÿßŸÑŸÖÿπÿØŸÑÿ©
const getGameImage = useCallback((game) => {
  if (!game) return '/images/default-game.jpg';
  
  // ÿÆÿ±Ÿäÿ∑ÿ© ÿßŸÑÿµŸàÿ± ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿäÿ©
  const imageMap = {
    'Driftcar': '/images/Driftcar.jpg',
    'Havoerboard': '/images/Hoverboard.jpg',
    'Sctooter': '/images/Scooter.jpg',
    'Car': '/images/Car.jpg',
    'MOtor': '/images/Motor.jpg',
    'Harlly': '/images/harley.jpg',
    'Segway': '/images/Segway.jpg',
    'Crazycar': '/images/Ninebot.jpg',
    'skeet': '/images/Skate.jpg',
    'Trampoline': '/images/Trampoline.jpg',
    'Vedio game': '/images/Simulator.jpg'
  };
  
  // ÿ•ÿ∞ÿß ŸÉÿßŸÜÿ™ ÿßŸÑÿµŸàÿ±ÿ© ŸÖŸàÿ¨ŸàÿØÿ© ŸÅŸä ŸÇÿßÿπÿØÿ© ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™
  if (game.image_url && game.image_url !== 'default-game.jpg') {
    // ÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ Ÿàÿ¨ŸàÿØ ÿßŸÑÿµŸàÿ±ÿ© ÿ£ŸàŸÑÿßŸã
    if (game.image_url.startsWith('http')) {
      return game.image_url;
    }
    
    // ÿ¨ÿ±ÿ® ŸÖÿ≥ÿßÿ±ÿßÿ™ ŸÖÿÆÿ™ŸÑŸÅÿ©
    const possiblePaths = [
      `/images/${game.image_url}`,
      `/public/images/${game.image_url}`,
      `/${game.image_url}`,
      game.image_url
    ];
    
    // ÿßÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿÆÿ±Ÿäÿ∑ÿ© ŸÉÿ®ÿØŸäŸÑ
    return imageMap[game.name] || possiblePaths[0];
  }
  
  // ÿßÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿÆÿ±Ÿäÿ∑ÿ©
  return imageMap[game.name] || '/images/default-game.jpg';
}, []);

