      return;
    }
    
    // ‚úÖ ÿ™ÿ≠ŸÑŸäŸÑ ŸÉŸÑ ÿ™ÿ£ÿ¨Ÿäÿ± ŸÑŸÑÿ™ÿ≠ŸÇŸÇ
    rentals.forEach((rental, index) => {
      const rentalType = getRentalType(rental);
      console.log(`üìä ÿ™ÿ£ÿ¨Ÿäÿ± ${index + 1}:`, {
        id: rental.id,
        customer: rental.customer_name,
        rental_type: rental.rental_type,
        is_open_time: rental.is_open_time,
        payment_status: rental.payment_status,
        paid_amount: rental.paid_amount,
        detected_type: rentalType
      });
    });
    
    const filtered = rentals.filter(rental => {
      const matchesSearch = 
        (rental.customer_name || '').toLowerCase().includes(searchTerm.toLowerCase()) ||
        (rental.customer_phone || '').includes(searchTerm) ||
        (rental.game_name || '').toLowerCase().includes(searchTerm.toLowerCase()) ||
        (rental.rental_number || '').includes(searchTerm);
      
      return matchesSearch;
    });
    
    setFilteredRentals(filtered);
  }, [rentals, searchTerm]);

  // ‚úÖ ÿØÿßŸÑÿ© ŸÖÿ≠ÿ≥ŸÜÿ© ŸÑÿ™ÿ≠ÿØŸäÿØ ŸÜŸàÿπ ÿßŸÑŸàŸÇÿ™
// ‚úÖ ÿØÿßŸÑÿ© ŸÖÿ≠ÿ≥ŸÜÿ© ŸÑÿ™ÿ≠ÿØŸäÿØ ŸÜŸàÿπ ÿßŸÑŸàŸÇÿ™
const getRentalType = (rental) => {
  if (!rental) return 'fixed';
  
  console.log('üîç ÿ™ÿ≠ÿØŸäÿØ ŸÜŸàÿπ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±:', {
    id: rental.id,
    rental_type: rental.rental_type,
    is_open_time: rental.is_open_time,
    payment_status: rental.payment_status
  });
  
  // ÿ£ŸàŸÑŸàŸäÿ© ŸÑŸÄ rental_type
  if (rental.rental_type === 'open') {
    console.log('‚úÖ ŸÜŸàÿπ: open (ŸÖŸÜ rental_type)');
    return 'open';
  }
  
  if (rental.rental_type === 'fixed') {
    console.log('‚úÖ ŸÜŸàÿπ: fixed (ŸÖŸÜ rental_type)');
    return 'fixed';
  }
  
  // ÿ´ŸÖ is_open_time
  if (rental.is_open_time === 1) {
    console.log('‚úÖ ŸÜŸàÿπ: open (ŸÖŸÜ is_open_time)');
    return 'open';
  }
  
  if (rental.is_open_time === 0) {
    console.log('‚úÖ ŸÜŸàÿπ: fixed (ŸÖŸÜ is_open_time)');
    return 'fixed';
  }
  
  // ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿ≠ÿßŸÑÿ© ÿßŸÑÿØŸÅÿπ ŸÉÿ®ÿØŸäŸÑ
  if (rental.payment_status === 'ÿπŸÜÿØ ÿßŸÑÿ•ŸÜŸáÿßÿ°' || rental.payment_status === 'pending') {
    console.log('‚úÖ ŸÜŸàÿπ: open (ŸÖŸÜ payment_status)');
    return 'open';
  }
  
  if (rental.payment_status === 'ŸÖÿØŸÅŸàÿπ ŸÖÿ≥ÿ®ŸÇÿßŸã' || rental.payment_status === 'paid') {
    console.log('‚úÖ ŸÜŸàÿπ: fixed (ŸÖŸÜ payment_status)');
    return 'fixed';
  }
  
  // ÿßŸÑÿßŸÅÿ™ÿ±ÿßÿ∂Ÿä
  console.log('‚ö†Ô∏è ŸÜŸàÿπ: fixed (ÿßŸÅÿ™ÿ±ÿßÿ∂Ÿä)');
  return 'fixed';
};

  // ‚úÖ ÿπÿØŸëÿßÿØ ÿ™ŸÜÿßÿ≤ŸÑŸä ŸÑŸÑŸàŸÇÿ™ ÿßŸÑÿ´ÿßÿ®ÿ™
  const calculateCountdownFixed = (rental) => {
    if (!rental || !rental.start_time) return { minutes: 0, seconds: 0, expired: true };
    
    const startTime = new Date(rental.start_time);
    const now = new Date();
    const duration = rental.duration_minutes || 15;
    const endTime = new Date(startTime.getTime() + duration * 60000);
    
    const totalSeconds = Math.floor((endTime - now) / 1000);
    
    if (totalSeconds <= 0) {
      return { minutes: 0, seconds: 0, expired: true };
    }
    
    const minutes = Math.floor(totalSeconds / 60);
    const seconds = totalSeconds % 60;
    
    return { minutes, seconds, expired: false };
  };

  // ‚úÖ ÿπÿØŸëÿßÿØ ÿ™ÿµÿßÿπÿØŸä ŸÑŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÅÿ™Ÿàÿ≠
  const calculateCountupOpen = (rental) => {
    if (!rental || !rental.start_time) return { minutes: 0, seconds: 0 };
    
    const startTime = new Date(rental.start_time);
    const now = new Date();
    const totalSeconds = Math.floor((now - startTime) / 1000);
    
    const minutes = Math.floor(totalSeconds / 60);
    const seconds = totalSeconds % 60;
    
    return { minutes, seconds };
  };

  // ‚úÖ ÿØÿßŸÑÿ© ŸÑÿ≠ÿ≥ÿßÿ® ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÖÿ≥ÿ™ÿ≠ŸÇ
  const calculateRemainingTimeValue = (rental) => { 
    if (!rental) return 0;
    
    const rentalType = getRentalType(rental);
    
    if (rentalType === 'open') {
      // ÿ≠ÿ≥ÿßÿ® ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÅÿ™Ÿàÿ≠
      if (!rental.start_time) return 0;
      
      const startTime = new Date(rental.start_time);
      const now = new Date();
      const elapsedMinutes = Math.floor((now - startTime) / 60000);
      const minMinutes = Math.max(elapsedMinutes, 15); // ÿßŸÑÿ≠ÿØ ÿßŸÑÿ£ÿØŸÜŸâ 15 ÿØŸÇŸäŸÇÿ©
      const units = Math.ceil(minMinutes / 15);
      const pricePer15Min = rental.price_per_15min || 100;
      return units * pricePer15Min;
    } else {
      // ÿßŸÑŸàŸÇÿ™ ÿßŸÑÿ´ÿßÿ®ÿ™: ÿ•ÿ±ÿ¨ÿßÿπ ÿßŸÑŸÖÿ®ŸÑÿ∫ ÿßŸÑŸÖÿØŸÅŸàÿπ ÿ®ÿßŸÑŸÅÿπŸÑ
      return rental.paid_amount || rental.total_amount || 0;
    }
  };

  // ‚úÖ ÿØÿßŸÑÿ© ŸÑÿπÿ±ÿ∂ ÿßŸÑÿπÿØŸëÿßÿØ
  const renderTimer = (rental) => {
    const rentalType = getRentalType(rental);
    
    if (rentalType === 'fixed') {
      const { minutes, seconds, expired } = calculateCountdownFixed(rental);
      
      if (expired) {
        return (
          <div className="rentals-timer rentals-timer-expired">
            <Clock size={12} />
            <span>‚è∞ ÿßŸÜÿ™ŸáŸâ</span>
          </div>
        );
      }
      
      return (
        <div className="rentals-timer rentals-timer-countdown">
          <Clock size={12} />
          <span>‚è±Ô∏è {minutes.toString().padStart(2, '0')}:{seconds.toString().padStart(2, '0')}</span>
        </div>
      );
    } else {
      const { minutes, seconds } = calculateCountupOpen(rental);
      
      return (
        <div className="rentals-timer rentals-timer-countup">
          <Clock size={12} />
          <span>‚è≥ {minutes.toString().padStart(2, '0')}:{seconds.toString().padStart(2, '0')}</span>
        </div>
      );
    }
  };

  // ‚úÖ ÿØÿßŸÑÿ© ŸÑÿπÿ±ÿ∂ ŸÜŸàÿπ ÿßŸÑŸàŸÇÿ™ ÿ®ÿ¥ŸÉŸÑ ÿµÿ≠Ÿäÿ≠
// ‚úÖ ÿØÿßŸÑÿ© ŸÖÿ≠ÿ≥ŸÜÿ© ŸÑÿπÿ±ÿ∂ ŸÜŸàÿπ ÿßŸÑŸàŸÇÿ™
const renderTimeType = (rental) => {
  const rentalType = getRentalType(rental);
  
  console.log('üéØ ÿπÿ±ÿ∂ ŸÜŸàÿπ ÿßŸÑŸàŸÇÿ™:', {
    id: rental.id,
    rental_type: rental.rental_type,
    is_open_time: rental.is_open_time,
    payment_status: rental.payment_status,
    detected_type: rentalType
  });
  
  if (rentalType === 'open') {
    return (
      <div className="rentals-time-type-badge rentals-open-time">
        <Unlock size={12} />
        <span>ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠</span>
        <span className="rentals-badge-details">
          (ÿßŸÑÿØŸÅÿπ ÿπŸÜÿØ ÿßŸÑÿ•ŸÜŸáÿßÿ°)
        </span>
      </div>
    );
  } else {
    return (
      <div className="rentals-time-type-badge rentals-fixed-time">
        <Lock size={12} />
        <span>ŸàŸÇÿ™ ÿ´ÿßÿ®ÿ™</span>
        <span className="rentals-badge-details">
          (ŸÖÿØŸÅŸàÿπ ŸÖÿ≥ÿ®ŸÇÿßŸã)
        </span>
      </div>
    );
  }
};

// ‚úÖ ÿØÿßŸÑÿ© ŸÖÿ≠ÿ≥ŸÜÿ© ŸÑÿπÿ±ÿ∂ ÿ≠ÿßŸÑÿ© ÿßŸÑÿØŸÅÿπ
