            onClick={() => {
              const element = document.querySelector('.rentals-right-column');
              if (element) {
                const activeSection = element.querySelector('.rentals-section:nth-child(1)');
                if (activeSection) {
                  activeSection.scrollIntoView({ behavior: 'smooth' });
                }
              }
            }}
            className="rentals-btn rentals-btn-outline rentals-quick-btn"
          >
            <Zap size={16} />
            ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©
            <span className="rentals-badge">{activeRentals.length}</span>
          </button>
          
          {isManager && (
            <button 
              onClick={() => {
                const element = document.querySelector('.rentals-right-column');
                if (element) {
