import axios from 'axios';

const API_BASE_URL = 'http://localhost:5000/api';

// ÿ•ŸÜÿ¥ÿßÿ° ŸÜÿ≥ÿÆÿ© ŸÖŸÜ axios ŸÖÿπ ÿ•ÿπÿØÿßÿØÿßÿ™ ŸÖÿ≥ÿ®ŸÇÿ©
const api = axios.create({
  baseURL: API_BASE_URL,
  timeout: 10000,
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json',
    'Accept-Language': 'ar'
  }
});

// Interceptor ŸÑŸÑÿ∑ŸÑÿ®ÿßÿ™
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    
    console.log('üì§ ÿ∑ŸÑÿ® API:', {
      url: config.url,
      method: config.method,
      headers: config.headers
    });
    
    return config;
  },
  (error) => {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ∑ŸÑÿ® API:', error);
    return Promise.reject(error);
  }
);

// Interceptor ŸÑŸÑÿ±ÿØŸàÿØ
api.interceptors.response.use(
  (response) => {
    console.log('‚úÖ ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© API ŸÜÿßÿ¨ÿ≠ÿ©:', {
      url: response.config.url,
      status: response.status,
      data: response.data
    });
    return response;
  },
  (error) => {
    console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿßÿ≥ÿ™ÿ¨ÿßÿ®ÿ© API:', {
      url: error.config?.url,
      status: error.response?.status,
      message: error.message,
      response: error.response?.data
    });
    
    // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑÿÆÿ∑ÿ£ 401 (ÿ∫Ÿäÿ± ŸÖÿµÿ±ÿ≠)
    if (error.response?.status === 401) {
      console.log('üîê ÿ¨ŸÑÿ≥ÿ© ÿ∫Ÿäÿ± ÿµÿßŸÑÿ≠ÿ©ÿå ÿ™ÿ≥ÿ¨ŸäŸÑ ÿßŸÑÿÆÿ±Ÿàÿ¨...');
      localStorage.removeItem('token');
      localStorage.removeItem('user');
      window.location.href = '/login';
    }
    
    return Promise.reject(error);
  }
);

// ÿØÿßŸÑÿ© ŸÑÿ™ÿπŸäŸäŸÜ ÿßŸÑÿ™ŸàŸÉŸÜ
api.setToken = (token) => {
  if (token) {
    api.defaults.headers.common['Authorization'] = `Bearer ${token}`;
  } else {
    delete api.defaults.headers.common['Authorization'];
