        }
      } else {
        showNotification('success', '‚úÖ ÿ™ŸÖ ÿ®ÿØÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠!');
        await api.put(`/games/${selectedGames[0].id}`, { status: 'ŸÖÿ§ÿ¨ÿ±ÿ©' });
        await loadData();
        resetForm();
        setShowRentalForm(false);
