    showNotification('error', `ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£: ${error.response?.data?.message || error.message}`);
  }
};

  const handleEndRental = async (rentalId) => {
    const rental = activeRentals.find(r => r.id === rentalId);
    
    if (!rental) return;
    
    try {
      let finalPrice = rental.total_amount;
      
      // ÿ•ÿ∞ÿß ŸÉÿßŸÜ ÿßŸÑŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠ÿßŸãÿå ŸÜÿ≠ÿ™ÿßÿ¨ ŸÑÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ≥ÿπÿ± ÿßŸÑŸÜŸáÿßÿ¶Ÿä
      if (rental.is_open_time) {
        const priceInput = prompt(
          `ÿ™ÿ£ÿ¨Ÿäÿ± #${rental.rental_number || rental.id}\n` +
          `ÿßŸÑÿπŸÖŸäŸÑ: ${rental.customer_name}\n` +
          `ÿßŸÑŸÑÿπÿ®ÿ©: ${rental.game_name}\n\n` +
          'ÿ£ÿØÿÆŸÑ ÿßŸÑÿ≥ÿπÿ± ÿßŸÑŸÜŸáÿßÿ¶Ÿä (ÿ¨.ŸÖ):',
          rental.total_amount || '0'
        );
        
        if (priceInput === null) return;
        
        finalPrice = parseFloat(priceInput);
