      }
    } catch (error) {
      console.error('Error ending rental:', error);
      showNotification('error', 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
    }
  };

  const updateGameStatus = async (gameId, status) => {
    try {
      await api.put(`/games/${gameId}`, { status });
    } catch (error) {
      console.error('Error updating game status:', error);
    }
  };

  const showNotification = (type, message) => {
    if (type === 'error') {
      alert(`‚ùå ${message}`);
    } else {
      alert(`‚úÖ ${message}`);
    }
  };

  const resetForm = () => {
    setCustomerName('');
    setCustomerPhone('');
    setCustomerNotes('');
    setSelectedGames([]);
    setQuantities({});
    setDurationType('fixed');
    setSelectedDuration(60);
    setStartTime('');
    setEndTime('');
    setDiscount(0);
    setPaymentMethod('cash');
    setDepositAmount(0);
    setTotalPrice(0);
    setFinalPrice(0);
  };

  const formatCurrency = (amount) => {
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0
    }).format(amount || 0);
  };

  const formatTime = (dateString) => {
    try {
      return new Date(dateString).toLocaleTimeString('ar-EG', {
        hour: '2-digit',
        minute: '2-digit'
      });
    } catch (error) {
      return '--:--';
    }
  };

  const formatDuration = (minutes) => {
    if (!minutes) return 'ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠';
    if (minutes < 60) {
      return `${minutes} ÿØŸÇŸäŸÇÿ©`;
    } else {
      const hours = Math.floor(minutes / 60);
      const remainingMinutes = minutes % 60;
      return `${hours} ÿ≥ÿßÿπÿ© ${remainingMinutes > 0 ? remainingMinutes + ' ÿØŸÇŸäŸÇÿ©' : ''}`;
