import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { Search, Filter, Download, RefreshCw, Eye, CheckCircle, Clock, XCircle, Edit, MoreVertical } from 'lucide-react';
import authService from '../../services/authService';
import activityService from '../../services/activityService';
import './AdminPages.css';
import './RentalsManagement.css';

const RentalsManagement = () => {
  // ==================== STATES ====================
  const [rentals, setRentals] = useState([]);
  const [filteredRentals, setFilteredRentals] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [selectedRental, setSelectedRental] = useState(null);
  const [actionLoading, setActionLoading] = useState(null);
  
  // Modal States
  const [showDetailsModal, setShowDetailsModal] = useState(false);
  const [showFiltersModal, setShowFiltersModal] = useState(false);
  const [showActionsModal, setShowActionsModal] = useState(false);
  const [showExportModal, setShowExportModal] = useState(false);
  
  // Filter States
  const [filters, setFilters] = useState({
    status: 'all',
    branch_id: 'all',
    game_id: 'all',
    customer_name: '',
    rental_number: '',
    date_from: '',
    date_to: '',
    payment_status: 'all',
    min_amount: '',
    max_amount: '',
    employee_id: 'all'
  });
  
  // Data States
  const [branches, setBranches] = useState([]);
  const [games, setGames] = useState([]);
  const [employees, setEmployees] = useState([]);
  
  // Pagination & Stats
  const [currentPage, setCurrentPage] = useState(1);
  const [itemsPerPage, setItemsPerPage] = useState(15);
  const [searchTerm, setSearchTerm] = useState('');
  const [sortConfig, setSortConfig] = useState({ key: 'created_at', direction: 'desc' });
  
  const [stats, setStats] = useState({
    total: 0,
    active: 0,
    completed: 0,
    cancelled: 0,
    refunded: 0,
    totalRevenue: 0,
    pendingPayments: 0,
    todaysRentals: 0
  });
  
  // ==================== EFFECTS ====================
  useEffect(() => {
    loadInitialData();
  }, []);
  
  useEffect(() => {
    if (rentals.length > 0) {
      applyFiltersAndSearch();
    }
  }, [rentals, filters, searchTerm, sortConfig]);
  
  // ==================== DATA LOADING ====================
  const loadInitialData = async () => {
    try {
      setLoading(true);
      setError('');
      
      const [branchesRes, gamesRes, employeesRes, rentalsRes] = await Promise.allSettled([
        authService.getBranches(),
        authService.getGames(),
        authService.getEmployees(),
        authService.getRentals({ page: 1, limit: 1000 })
      ]);
      
      // Handle branches
      if (branchesRes.status === 'fulfilled' && branchesRes.value.success) {
        setBranches(branchesRes.value.data || []);
      }
      
      // Handle games
      if (gamesRes.status === 'fulfilled' && gamesRes.value.success) {
        setGames(gamesRes.value.data || []);
      }
      
      // Handle employees
      if (employeesRes.status === 'fulfilled' && employeesRes.value.success) {
        setEmployees(employeesRes.value.data || []);
      }
      
      // Handle rentals
      if (rentalsRes.status === 'fulfilled' && rentalsRes.value.success) {
        const rentalsData = rentalsRes.value.data?.data || rentalsRes.value.data || [];
        setRentals(rentalsData);
        calculateStats(rentalsData);
      } else {
        setError('ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™');
      }
      
    } catch (error) {
      console.error('Error loading initial data:', error);
      setError('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™');
    } finally {
      setLoading(false);
    }
  };
  
  const loadRentals = async (customFilters = {}) => {
    try {
      setLoading(true);
      const params = buildApiParams(customFilters);
      const response = await authService.getRentals(params);
      
      if (response.success) {
        const rentalsData = response.data?.data || response.data || [];
        setRentals(rentalsData);
        calculateStats(rentalsData);
        setError('');
      } else {
        setError(response.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™');
      }
    } catch (error) {
      console.error('Error loading rentals:', error);
      setError('ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™');
    } finally {
      setLoading(false);
    }
  };
  
  // ==================== STATS CALCULATION ====================
  const calculateStats = useCallback((rentalsList) => {
    const total = rentalsList.length;
    const active = rentalsList.filter(r => r.status === 'ŸÜÿ¥ÿ∑').length;
    const completed = rentalsList.filter(r => r.status === 'ŸÖŸÉÿ™ŸÖŸÑ').length;
    const cancelled = rentalsList.filter(r => r.status === 'ŸÖŸÑÿ∫Ÿä').length;
    const refunded = rentalsList.filter(r => r.status === 'ŸÖÿ≥ÿ™ÿ±ÿØ').length;
    
    const today = new Date().toISOString().split('T')[0];
    const todaysRentals = rentalsList.filter(r => 
      r.start_time && r.start_time.split('T')[0] === today
    ).length;
    
    const totalRevenue = rentalsList
      .filter(r => r.status === 'ŸÖŸÉÿ™ŸÖŸÑ' && r.payment_status === 'ŸÖÿØŸÅŸàÿπ')
      .reduce((sum, rental) => sum + (parseFloat(rental.total_amount) || 0), 0);
    
    const pendingPayments = rentalsList
      .filter(r => r.status === 'ŸÖŸÉÿ™ŸÖŸÑ' && r.payment_status === 'ÿ∫Ÿäÿ± ŸÖÿØŸÅŸàÿπ')
      .reduce((sum, rental) => sum + (parseFloat(rental.total_amount) || 0), 0);
    
    setStats({
      total,
      active,
      completed,
      cancelled,
      refunded,
      totalRevenue,
      pendingPayments,
      todaysRentals
    });
  }, []);
  
  // ==================== FILTERS & SEARCH ====================
  const buildApiParams = (customFilters = {}) => {
    const params = {};
    const activeFilters = { ...filters, ...customFilters };
    
    Object.keys(activeFilters).forEach(key => {
      if (activeFilters[key] && activeFilters[key] !== 'all' && activeFilters[key] !== '') {
        params[key] = activeFilters[key];
      }
    });
    
    return params;
  };
  
  const applyFiltersAndSearch = () => {
    let result = [...rentals];
    
    // Apply search term
    if (searchTerm) {
      const term = searchTerm.toLowerCase();
      result = result.filter(rental =>
        (rental.customer_name && rental.customer_name.toLowerCase().includes(term)) ||
        (rental.rental_number && rental.rental_number.toLowerCase().includes(term)) ||
        (rental.customer_phone && rental.customer_phone.includes(term)) ||
        (rental.game_name && rental.game_name.toLowerCase().includes(term))
      );
    }
    
    // Apply filters
    if (filters.status !== 'all') {
      result = result.filter(r => r.status === filters.status);
    }
    
    if (filters.branch_id !== 'all') {
      result = result.filter(r => r.branch_id === filters.branch_id);
    }
    
    if (filters.game_id !== 'all') {
      result = result.filter(r => r.game_id === filters.game_id);
    }
    
    if (filters.payment_status !== 'all') {
      result = result.filter(r => r.payment_status === filters.payment_status);
    }
    
    if (filters.customer_name) {
      result = result.filter(r => 
        r.customer_name && r.customer_name.toLowerCase().includes(filters.customer_name.toLowerCase())
      );
    }
    
    if (filters.rental_number) {
      result = result.filter(r => 
        r.rental_number && r.rental_number.includes(filters.rental_number)
      );
    }
    
    if (filters.date_from) {
      result = result.filter(r => 
        r.start_time && new Date(r.start_time) >= new Date(filters.date_from)
      );
    }
    
    if (filters.date_to) {
      const toDate = new Date(filters.date_to);
      toDate.setHours(23, 59, 59, 999);
      result = result.filter(r => 
        r.start_time && new Date(r.start_time) <= toDate
      );
    }
    
    if (filters.min_amount) {
      result = result.filter(r => 
        parseFloat(r.total_amount || 0) >= parseFloat(filters.min_amount)
      );
    }
    
    if (filters.max_amount) {
      result = result.filter(r => 
        parseFloat(r.total_amount || 0) <= parseFloat(filters.max_amount)
      );
    }
    
    // Apply sorting
    if (sortConfig.key) {
      result.sort((a, b) => {
        let aValue = a[sortConfig.key];
        let bValue = b[sortConfig.key];
        
        if (sortConfig.key.includes('time') || sortConfig.key.includes('date')) {
          aValue = new Date(aValue);
          bValue = new Date(bValue);
        }
        
        if (sortConfig.key === 'total_amount') {
          aValue = parseFloat(aValue || 0);
          bValue = parseFloat(bValue || 0);
        }
        
        if (aValue < bValue) {
          return sortConfig.direction === 'asc' ? -1 : 1;
        }
        if (aValue > bValue) {
          return sortConfig.direction === 'asc' ? 1 : -1;
        }
        return 0;
      });
    }
    
    setFilteredRentals(result);
    setCurrentPage(1);
  };
  
  const handleSort = (key) => {
    setSortConfig(prev => ({
      key,
      direction: prev.key === key && prev.direction === 'asc' ? 'desc' : 'asc'
    }));
  };
  
  const handleFilterChange = (key, value) => {
    setFilters(prev => ({ ...prev, [key]: value }));
  };
  
  const handleApplyFilters = () => {
    loadRentals();
    setShowFiltersModal(false);
  };
  
  const handleResetFilters = () => {
    setFilters({
      status: 'all',
      branch_id: 'all',
      game_id: 'all',
      customer_name: '',
      rental_number: '',
      date_from: '',
      date_to: '',
      payment_status: 'all',
      min_amount: '',
      max_amount: '',
      employee_id: 'all'
    });
    setSearchTerm('');
    setSortConfig({ key: 'created_at', direction: 'desc' });
    loadRentals();
  };
  
  // ==================== RENTAL ACTIONS ====================
  const handleViewDetails = (rental) => {
    setSelectedRental(rental);
    setShowDetailsModal(true);
  };
  
  const handleEndRental = async (rental) => {
    if (!window.confirm(`ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± #${rental.rental_number}ÿü`)) return;
    
    try {
      setActionLoading(rental.id);
      
      const response = await authService.endRental(rental.id, {
        payment_method: 'ŸÉÿßÿ¥'
      });
      
      if (response.success) {
        await activityService.log('update', 'rental', rental.id, rental.rental_number, 
          `ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿ™ÿ£ÿ¨Ÿäÿ± ÿ±ŸÇŸÖ: ${rental.rental_number}`);
        
        showNotification('success', '‚úÖ ÿ™ŸÖ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠');
        loadRentals();
        setShowDetailsModal(false);
      } else {
        showNotification('error', response.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
      }
    } catch (error) {
      showNotification('error', 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
    } finally {
      setActionLoading(null);
    }
  };
  
  const handleCancelRental = async (rental) => {
    if (!window.confirm(`ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± #${rental.rental_number}ÿü`)) return;
    
    try {
      setActionLoading(rental.id);
      
      const response = await authService.cancelRental(rental.id);
      
      if (response.success) {
        await activityService.log('update', 'rental', rental.id, rental.rental_number, 
          `ÿ™ŸÖ ÿ•ŸÑÿ∫ÿßÿ° ÿ™ÿ£ÿ¨Ÿäÿ± ÿ±ŸÇŸÖ: ${rental.rental_number}`);
        
        showNotification('success', '‚úÖ ÿ™ŸÖ ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ÿ®ŸÜÿ¨ÿßÿ≠');
        loadRentals();
      } else {
        showNotification('error', response.message || 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
      }
    } catch (error) {
      showNotification('error', 'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ÿ£ÿ´ŸÜÿßÿ° ÿ•ŸÑÿ∫ÿßÿ° ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±');
    } finally {
      setActionLoading(null);
    }
  };
  
