const getGameImageUrl = (game) => {
  if (!game) return '/images/default-game.jpg';
  
  // البحث في الخريطة أولاً
  const gameName = (game.name || '').trim();
  
  for (const [key, imagePath] of Object.entries(GAME_IMAGE_MAP)) {
    const gameNameLower = gameName.toLowerCase();
    const keyLower = key.toLowerCase();
    
    if (gameNameLower.includes(keyLower) || keyLower.includes(gameNameLower)) {
      return imagePath;
    }
  }
  
  // ثانياً: محاولة إيجاد الصور من قاعدة البيانات
  let imageUrl = game.image_url || game.external_image_url || game.thumbnail_url || game.image;
  
  if (imageUrl) {
    if (imageUrl.startsWith('http://') || imageUrl.startsWith('https://')) {
      return imageUrl;
    }
    
    if (imageUrl.startsWith('/')) {
      return imageUrl;
    }
    
    return `/${imageUrl}`;
  }
  
  // ثالثاً: استخدام الصورة الافتراضية
  return '/images/default-game.jpg';
};

const handleImageError = (e) => {
  e.target.onerror = null;
  e.target.src = '/images/default-game.jpg';
  e.target.classList.add('image-error');
};


