const RentalActionsDropdown = ({ rental, onActionClick }) => {
  const [showDropdown, setShowDropdown] = useState(false);
  const dropdownRef = useRef(null);
  
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
        setShowDropdown(false);
      }
    };
    
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);
  
  const isWithin3Minutes = () => {
    if (!rental.start_time) return false;
    const startTime = new Date(rental.start_time);
    const now = new Date();
    const minutesPassed = Math.floor((now - startTime) / 60000);
    return minutesPassed <= 3;
  };
  
  return (
    <div className="rentals-page-actions-dropdown-container" ref={dropdownRef}>
      <button 
        className="rentals-page-actions-btn"
        onClick={() => setShowDropdown(!showDropdown)}
