const updateGameStatus = (gameId, newStatus) => {
  console.log(`🔄 تحديث حالة اللعبة ${gameId} إلى: ${newStatus}`);
  
  setGames(prevGames => 
    prevGames.map(game => 
      game.id === gameId 
        ? { 
            ...game, 
            status: newStatus,
            ...(newStatus === 'مؤجرة' ? { 
              rented_at: new Date().toISOString(),
              rented_locally: true 
            } : {
              rented_at: null,
              rented_locally: false
            })
          } 
        : game
    )
  );
  
  // تحديث الإحصائيات
  if (newStatus === 'مؤجرة') {
    setStatistics(prev => ({
      ...prev,
      availableGames: Math.max(0, prev.availableGames - 1)
    }));
  } else if (newStatus === 'متاح') {
    setStatistics(prev => ({
      ...prev,
      availableGames: prev.availableGames + 1
    }));
  }
};
