      loadActiveRentals().catch(err => console.log('⚠️ خطأ في التأجيرات:', err.message)),
      !isEmployee ? loadCompletedRentals().catch(err => console.log('⚠️ خطأ في المكتملة:', err.message)) : Promise.resolve()
    ]);
    
    await updateStatistics();
    
  } catch (error) {
    console.error('❌ خطأ في تحميل البيانات:', error);
  }
