const startCountdownTimer = (rentalId, startTime, durationMinutes) => {
  // ⚡ تحقق خاص للتأجيرات الثابتة
  if (durationMinutes > 0 && durationMinutes !== 0) {
    console.log(`⏰ تأجير ثابت ${rentalId}: ${durationMinutes} دقيقة`);
    
    // ⚡ عند انتهاء الوقت، أكمل التأجير تلقائياً
    const timer = setInterval(() => {
      const elapsed = Math.floor((new Date() - new Date(startTime)) / 60000);
      
      if (elapsed >= durationMinutes) {
        console.log(`🛑 تأجير ثابت ${rentalId} انتهى`);
        clearInterval(timer);
        
        // ⚡ أكمل التأجير تلقائياً
        const rental = activeRentals.find(r => r.id === rentalId);
        if (rental && rental.payment_status === 'مدفوع') {
          handleAutoCompleteRental(rental);
        }
      }
    }, 30000); // تحقق كل 30 ثانية
  }
};
// ⚡ **دالة مساعدة لتحديث الوقت المتبقي**
const updateRentalTimeLeft = (rentalId, endTime) => {
  const now = new Date();
  const timeLeftMs = endTime - now;
  
  if (timeLeftMs <= 0) {
    console.log(`🛑 تأجير ${rentalId} انتهى`);
    
    // إلغاء العداد
    if (countdownTimersRef.current[rentalId]) {
      clearInterval(countdownTimersRef.current[rentalId]);
      delete countdownTimersRef.current[rentalId];
    }
    
    // تحديث حالة التأجير
    setActiveRentals(prev => prev.map(rental => {
      if (rental.id === rentalId) {
        return {
          ...rental,
          time_left: 'انتهى',
          remaining_seconds: 0,
          is_expired: true
        };
      }
      return rental;
    }));
    
    // ⚡ **تشغيل الإكمال التلقائي بعد ثانيتين**
    setTimeout(() => {
      const expiredRental = activeRentals.find(r => r.id === rentalId);
      if (expiredRental && !expiredRental.is_open_time) {
        console.log(`⚡ تشغيل الإكمال التلقائي للتأجير ${rentalId}`);
        handleAutoCompleteRental(expiredRental);
      }
    }, 2000);
    
    return;
  }
  
  const totalSeconds = Math.floor(timeLeftMs / 1000);
  const minutes = Math.floor(totalSeconds / 60);
  const seconds = totalSeconds % 60;
  
  const timeLeftFormatted = `${minutes}:${seconds.toString().padStart(2, '0')}`;
  
  // تحديث الوقت المتبقي في القائمة
  setActiveRentals(prev => prev.map(rental => {
    if (rental.id === rentalId) {
      return {
        ...rental,
        time_left: timeLeftFormatted,
        remaining_seconds: totalSeconds,
        is_expired: false
      };
    }
    return rental;
  }));
};
// ==================== دالة جديدة: التحقق من التأجيرات المنتهية وتحويلها ====================
const checkAndAutoCompleteRentals = async () => {
  try {
    const now = new Date();
    console.log('⏰ التحقق من التأجيرات المنتهية...');
    
    // 🔧 إصلاح: تضمين التأجيرات المنتهية الوقت المفتوح أيضاً
    const expiredRentals = activeRentals.filter(rental => {
      // التأجيرات ذات الوقت الثابت المنتهية
      if (!rental.is_open_time && rental.duration_minutes > 0) {
        const startTime = new Date(rental.start_time);
        const endTime = new Date(startTime.getTime() + rental.duration_minutes * 60000);
        const isExpired = now > endTime && rental.payment_status === 'مدفوع';
        
        if (isExpired && !rental.is_expired) {
          console.log(`🛑 تأجير ${rental.rental_number} منتهي (وقت ثابت)`);
          return true;
        }
      }
      
      // 🔧 إضافة: التأجيرات التي انتهى عدّادها مباشرة
      if (rental.time_left === 'انتهى' && rental.payment_status === 'مدفوع') {
        console.log(`🛑 تأجير ${rental.rental_number} انتهى (عُدّاد)`);
        return true;
      }
      
      return false;
    });
    
    if (expiredRentals.length > 0) {
      console.log(`🔄 العثور على ${expiredRentals.length} تأجير منتهي للتحويل`);
      
      for (const rental of expiredRentals) {
        await handleAutoCompleteRental(rental);
      }
      
      // 🔧 إضافة: تحديث البيانات مباشرة بعد التحويل
      await loadActiveRentals();
      await updateStatistics();
    }
  } catch (error) {
    console.error('❌ خطأ في التحقق من التأجيرات المنتهية:', error);
  }
};
