// ==================== دالة إنهاء الوقت المفتوح المحسنة ====================
const handleEndOpenTimeEnhanced = async (rental) => {
  try {
    // أضف هذا التحقق في البداية:
    if (rental.status === 'مكتمل') {
      showNotification('info', `✅ التأجير ${rental.rental_number} مكتمل بالفعل!`);
      return;
    }
    
    if (rental.status !== 'نشط') {
      showNotification('error', `⚠️ التأجير بحالة: ${rental.status} - لا يمكن إنهاؤه`);
      return;
    }

    // 1. تشخيص التأجير أولاً
    try {
      const diagnostic = await api.get(`/debug/full-rental-check/${rental.id}`);
      console.log('🔍 تشخيص التأجير:', diagnostic);
    } catch (diagError) {
      console.warn('⚠️ لا يمكن تشخيص التأجير:', diagError.message);
    }
    
    const startTime = new Date(rental.start_time);
    const now = new Date();
    const elapsedMinutes = Math.floor((now - startTime) / 60000);
    
    if (elapsedMinutes < 1) {
      showNotification('error', 'يجب أن تكون المدة دقيقة واحدة على الأقل');
      return;
    }
    
    const pricePer15Min = rental.price_per_15min || 100;
    const units = Math.ceil(elapsedMinutes / 15);
    const finalAmount = pricePer15Min * units;
    
    const confirmMessage = `
🏁 إنهاء التأجير - نظام متكامل

التأجير: #${rental.rental_number}
العميل: ${rental.customer_name}
اللعبة: ${rental.game_name}

⏱️ المدة الفعلية: ${elapsedMinutes} دقيقة
💰 السعر/15 دقيقة: ${formatCurrency(pricePer15Min)}
📦 عدد الوحدات: ${units}

🎯 المبلغ الإجمالي: ${formatCurrency(finalAmount)}

هل تريد المتابعة مع ربط كامل بقاعدة البيانات؟`;

    if (!window.confirm(confirmMessage)) return;
    
    const paymentMethod = prompt(
      `اختر طريقة الدفع للمبلغ ${formatCurrency(finalAmount)}:\n\n1. كاش 💵\n2. بطاقة 💳\n3. فودافون كاش 📱\n\nأدخل الرقم:`,
      '1'
    );
    
    let paymentMethodText = 'كاش';
    if (paymentMethod === '2') paymentMethodText = 'بطاقة';
    if (paymentMethod === '3') paymentMethodText = 'فودافون كاش';
    
    showNotification('info', '🔄 جاري إنهاء التأجير بربط كامل بقاعدة البيانات...');
    
    // ⚡ **استخدام النقطة المتكاملة الجديدة**
    try {
      console.log(`📤 إرسال طلب إنهاء تأجير ${rental.id}...`);
      
      const response = await api.put(`/rentals/${rental.id}/finish`, {
        final_amount: finalAmount,
        payment_method: paymentMethodText,
        notes: `إنهاء يدوي - المدة: ${elapsedMinutes} دقيقة`,
        shift_id: rental.shift_id || currentShift?.id
      });
      
      console.log('📥 استجابة إنهاء التأجير:', response);
      
      if (response.success) {
        showNotification('success', `✅ تم إنهاء التأجير بنجاح!\n💰 المبلغ: ${formatCurrency(finalAmount)}\n📊 تم الربط الكامل بقاعدة البيانات`);
        
        // ⚡ **تحديث البيانات من السيرفر (ليس محلياً)**
        setTimeout(async () => {
          await loadActiveRentals();
          await loadGames();
          await updateStatistics();
        }, 1000);
        
      } else {
        showNotification('error', response.message || 'فشل إنهاء التأجير');
      }
      
    } catch (error) {
      console.error('❌ خطأ في إنهاء التأجير:', error);
      
      // محاولة إصلاح الجدول أولاً
      try {
        showNotification('warning', '⚠️ جاري محاولة إصلاح قاعدة البيانات...');
        
        const fixResponse = await api.post('/debug/fix-rentals-table-advanced');
        console.log('🔧 استجابة إصلاح الجدول:', fixResponse);
        
        if (fixResponse.success) {
          showNotification('info', '✅ تم إصلاح الجدول، جاري إعادة المحاولة...');
          
          // إعادة المحاولة بعد الإصلاح
          const retryResponse = await api.put(`/rentals/${rental.id}/finish`, {
            final_amount: finalAmount,
            payment_method: paymentMethodText
          });
          
          if (retryResponse.success) {
            showNotification('success', '✅ تم إنهاء التأجير بعد الإصلاح');
            setTimeout(async () => {
              await loadActiveRentals();
              await loadGames();
            }, 1000);
          } else {
            showNotification('error', '❌ فشل حتى بعد الإصلاح');
          }
        } else {
          showNotification('error', '❌ فشل إصلاح الجدول');
        }
      } catch (fixError) {
        console.error('❌ خطأ في الإصلاح:', fixError);
        showNotification('error', 'حدث خطأ فادح في النظام');
      }
    }
    
  } catch (error) {
    console.error('🔥 خطأ فادح في إنهاء الوقت المفتوح:', error);
    showNotification('error', 'حدث خطأ غير متوقع');
  }
};
const handleReRentEnhanced = async (rental) => {
  try {
    // 1. التحقق الأساسي
    if (!currentShift) {
      showNotification('error', 'يجب بدء شيفت أولاً قبل إعادة التأجير');
      return;
    }

    if (!rental || !rental.customer_name || !rental.customer_phone) {
      showNotification('error', 'بيانات التأجير غير صحيحة');
      return;
    }

    // 2. سؤال المستخدم عن نوع التأجير الجديد
    const confirmMessage = `
🔄 إعادة تأجير للعميل

العميل: ${rental.customer_name}
الهاتف: ${rental.customer_phone}

ما نوع التأجير الجديد الذي تريده؟

1. نفس التأجير السابق
2. تأجير جديد بلعبة مختلفة`;

    const choice = prompt(confirmMessage + '\n\nأدخل الرقم (1 أو 2):');
    
    if (!choice) {
      showNotification('info', 'تم إلغاء إعادة التأجير');
      return;
    }

    let selectedGameId = rental.game_id;
    let selectedGameName = rental.game_name;
    let selectedGamePrice = rental.price_per_15min || 100;

    // 3. إذا اختار لعبة مختلفة
    if (choice === '2') {
      const availableGames = games.filter(g => g.status === 'متاح');
      
      if (availableGames.length === 0) {
        showNotification('error', 'لا توجد ألعاب متاحة حالياً');
        return;
      }

      let gamesList = '🎮 اختر لعبة جديدة:\n\n';
      availableGames.forEach((game, index) => {
        gamesList += `${index + 1}. ${game.name} - ${formatCurrency(game.price_per_15min)} / 15 دقيقة\n`;
      });

      const gameChoice = prompt(gamesList + '\n🔢 أدخل رقم اللعبة:');
      
      if (!gameChoice) {
        showNotification('info', 'تم إلغاء اختيار اللعبة');
        return;
      }

      const selectedIndex = parseInt(gameChoice) - 1;
      
      if (selectedIndex >= 0 && selectedIndex < availableGames.length) {
        selectedGameId = availableGames[selectedIndex].id;
        selectedGameName = availableGames[selectedIndex].name;
        selectedGamePrice = availableGames[selectedIndex].price_per_15min;
      } else {
        showNotification('error', 'رقم اللعبة غير صحيح');
        return;
      }
    }

    // 4. اختيار نوع التأجير
    const typeChoice = prompt(
      '⏱️ اختر نوع التأجير الجديد:\n\n' +
      '1. مدة ثابتة (15 دقيقة) - 💰 يدفع الآن\n' +
      '2. وقت مفتوح - 💳 يدفع عند الانتهاء\n\n' +
      '🔢 أدخل الرقم (1 أو 2):',
      '1'
    );

    if (!typeChoice) {
      showNotification('info', 'تم إلغاء التأجير');
      return;
    }

    const isFixedTime = typeChoice === '1';
    const durationMinutes = isFixedTime ? 15 : 0;

    // 5. حساب المبلغ
    let totalAmount = 0;
    if (isFixedTime) {
      const units = Math.ceil(durationMinutes / 15);
      totalAmount = selectedGamePrice * units;
    }

    // 6. تأكيد المستخدم
    const finalConfirmMessage = `
✅ تأكيد إعادة التأجير
══════════════════════════════

👤 العميل: ${rental.customer_name}
📱 الهاتف: ${rental.customer_phone}

🎮 اللعبة: ${selectedGameName}
💰 السعر/15 دقيقة: ${formatCurrency(selectedGamePrice)}

⏱️ نوع التأجير: ${isFixedTime ? `${durationMinutes} دقيقة (ثابت)` : 'وقت مفتوح'}
💸 ${isFixedTime ? `المبلغ: ${formatCurrency(totalAmount)} (يدفع الآن)` : 'يدفع عند الإنهاء'}

══════════════════════════════
هل تريد المتابعة؟`;

    if (!window.confirm(finalConfirmMessage)) {
      showNotification('info', 'تم إلغاء التأجير');
      return;
    }

    // 7. إنشاء التأجير الجديد
    showNotification('info', '🔄 جاري إنشاء التأجير الجديد...');

    // تحديث حالة اللعبة فورياً
    setGames(prevGames => 
      prevGames.map(game => 
        game.id === selectedGameId 
          ? { 
              ...game, 
              status: 'مؤجرة',
              rented_locally: true,
              rented_at: new Date().toISOString()
            } 
          : game
      )
    );

    // بيانات التأجير الجديد
    const rentalData = {
      rental_number: `RENT-${Date.now()}-${Math.floor(Math.random() * 1000)}`,
      game_id: selectedGameId,
      customer_name: rental.customer_name,
      customer_phone: rental.customer_phone,
      branch_id: user.branch_id,
      shift_id: currentShift.id,
      duration_minutes: durationMinutes,
      total_amount: totalAmount,
      payment_method: 'كاش', // افتراضي للعميل الموجود
      payment_status: isFixedTime ? 'مدفوع' : 'عند الإنهاء',
      paid_amount: isFixedTime ? totalAmount : 0,
      notes: `إعادة تأجير - التأجير السابق: ${rental.rental_number}`,
      status: 'نشط',
      start_time: new Date().toISOString()
    };

    // محاولة إنشاء التأجير
