// ⚡ دالة مساعدة: تحديث إيراد الشيفت
const updateShiftRevenue = async (amount) => {
  try {
    if (!currentShift || !amount || amount <= 0) return;
    
    console.log(`💰 تحديث إيراد الشيفت بمبلغ: ${formatCurrency(amount)}`);
    
    // محاولة تحديث عبر API
    try {
      const response = await api.put(`/shifts/${currentShift.id}/update-revenue`, {
        revenue_added: amount,
        rental_count: 1
      });
      
      if (response.success) {
        console.log(`✅ تم تحديث إيراد الشيفت عبر API`);
      }
    } catch (apiError) {
      console.log('⚠️ لا يمكن تحديث عبر API، تحديث محلي:', apiError.message);
    }
    
    // تحديث محلي فوري
    setShiftStats(prev => ({
      ...prev,
      total_revenue: (prev.total_revenue || 0) + amount,
      total_rentals: (prev.total_rentals || 0) + 1
    }));
    
    setStatistics(prev => ({
      ...prev,
      shiftRevenue: (prev.shiftRevenue || 0) + amount,
      todayRevenue: (prev.todayRevenue || 0) + amount
    }));
    
  } catch (error) {
    console.error('❌ خطأ في تحديث الإيراد:', error);
  }
};
// دالة تنسيق العملة
const formatCurrency = (amount) => {
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};
// دالة التحقق من رقم الهاتف
const validatePhone = (phone) => {
  const phoneRegex = /^01[0-9]{9}$/;
  return phoneRegex.test(phone);
};
// ⚡ **دالة مساعدة: تحديث حالة لعبة واحدة فورياً**
