import React, { useState, useEffect } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import api from '../../services/api';
import {
  Gamepad2, Zap, DollarSign, Clock, Users,
  CheckCircle, RefreshCw, Building, Loader2,
  ShoppingCart, User, BarChart3, TrendingUp
} from 'lucide-react';
import './EmployeeDashboard.css';

const EmployeeDashboard = () => {
  const navigate = useNavigate();
  const [loading, setLoading] = useState(true);
  const [user, setUser] = useState(null);
  const [currentShift, setCurrentShift] = useState(null);
  const [stats, setStats] = useState({
    availableGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

  // ÿØÿßŸÑÿ© ÿ™ŸÜÿ≥ŸäŸÇ ÿßŸÑÿπŸÖŸÑÿ©
  const formatCurrency = (amount) => {
    if (!amount && amount !== 0) return '0 ÿ¨.ŸÖ';
    const num = parseFloat(amount) || 0;
    return new Intl.NumberFormat('ar-EG', {
      style: 'currency',
      currency: 'EGP',
      minimumFractionDigits: 0,
      maximumFractionDigits: 2
    }).format(num);
  };

  // üîß ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ©
  const loadDashboardData = async () => {
    try {
      setLoading(true);
      
      // ÿ¨ŸÑÿ® ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ
      const userData = JSON.parse(localStorage.getItem('user') || '{}');
      setUser(userData);
      
      // ÿ¨ŸÑÿ® ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸä
      const shiftResponse = await api.get('/shifts/current');
      if (shiftResponse.success) {
        setCurrentShift(shiftResponse.data);
      }
      
