import React, { useState, useEffect } from 'react';
import authService from '../../services/authService';
import './AdminPages.css';

const GamesManagement = () => {
  const [games, setGames] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showAddModal, setShowAddModal] = useState(false);
  const [selectedCategory, setSelectedCategory] = useState('ALL');
  const [branches, setBranches] = useState([]);
  const [newGame, setNewGame] = useState({
    name: '',
    description: '',
    category: 'كهربائية',
    image_url: '',
    price_per_hour: 100,
    price_per_15min: 25,
    branch_id: ''
  });

  const categories = [
    'كهربائية', 'سيارات', 'دراجات نارية', 'مائية', 'أخرى'
  ];

  const loadBranches = async () => {
    try {
      const response = await authService.getBranches();
      if (response.success && response.data.length > 0) {
        setBranches(response.data);
        // تعيين أول فرع كافتراضي
        setNewGame(prev => ({
          ...prev,
          branch_id: response.data[0].id
        }));
      }
    } catch (error) {
      console.error('Error loading branches:', error);
    }
  };

  const loadGames = async () => {
    try {
      setLoading(true);
      setError('');
      
      const response = await authService.getGames();
      
      if (response.success) {
        setGames(response.data);
      } else {
        setError(response.message || 'فشل في تحميل الألعاب');
      }
    } catch (error) {
      setError('تعذر الاتصال بالخادم');
      console.error('Error loading games:', error);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadBranches();
    loadGames();
  }, []);

  const handleAddGame = async () => {
    if (!newGame.name || !newGame.branch_id) {
      alert('اسم اللعبة والفرع مطلوبان');
      return;
    }

    try {
      const response = await authService.createGame(newGame);
      
      if (response.success) {
        alert('تم إنشاء اللعبة بنجاح');
        setShowAddModal(false);
        setNewGame({
          name: '',
          description: '',
          category: 'كهربائية',
          image_url: '',
          price_per_hour: 100,
          price_per_15min: 25,
          branch_id: branches.length > 0 ? branches[0].id : ''
        });
        loadGames();
      } else {
        alert(response.message || 'فشل في إنشاء اللعبة');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  const handleToggleStatus = async (id, currentStatus) => {
    try {
      const newStatus = currentStatus === 'متاح' ? 'مؤجر' : 'متاح';
      const response = await authService.updateGame(id, { status: newStatus });
      
      if (response.success) {
        loadGames();
      } else {
        alert(response.message || 'فشل في تحديث الحالة');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  const handleDeleteGame = async (id) => {
    if (!window.confirm('هل تريد حذف هذه اللعبة؟')) return;

    try {
      const response = await authService.updateGame(id, { is_active: 0 });
      
      if (response.success) {
        alert('تم حذف اللعبة بنجاح');
        loadGames();
      } else {
        alert(response.message || 'فشل في حذف اللعبة');
      }
    } catch (error) {
      alert('تعذر الاتصال بالخادم');
    }
  };

  if (loading) {
    return (
      <div className="loading-container">
        <div className="spinner"></div>
        <p>جاري تحميل الألعاب...</p>
      </div>
    );
  }

  return (
    <div className="admin-page">
      <div className="page-header">
        <h1>إدارة الألعاب</h1>
        <button 
          className="btn-add"
          onClick={() => setShowAddModal(true)}
        >
          + إضافة لعبة جديدة
        </button>
      </div>

      {error && (
        <div className="alert alert-error">
          {error}
        </div>
      )}

      {/* الفلترة حسب التصنيف */}
      <div className="filters">
        <div className="category-filter">
          <button 
            className={`category-btn ${selectedCategory === 'ALL' ? 'active' : ''}`}
            onClick={() => setSelectedCategory('ALL')}
          >
            الكل
          </button>
          {categories.map(category => (
            <button
              key={category}
              className={`category-btn ${selectedCategory === category ? 'active' : ''}`}
              onClick={() => setSelectedCategory(category)}
            >
              {category}
            </button>
          ))}
        </div>
      </div>

      <div className="games-grid">
        {games
          .filter(game => selectedCategory === 'ALL' || game.category === selectedCategory)
          .map(game => (
            <div key={game.id} className="game-card">
              <div className="game-image">
                {game.image_url ? (
                  <img 
                    src={game.image_url}
                    alt={game.name}
                    onError={(e) => {
                      e.target.onerror = null;
                      e.target.src = "/images/default-game.jpg";
                    }}
                  />
                ) : (
                  <div className="no-image">لا توجد صورة</div>
                )}
              </div>
              
              <div className="game-content">
                <div className="game-header">
                  <h3>{game.name}</h3>
                  <span className="game-category">{game.category}</span>
                </div>
                
                <div className="game-info">
                  <p className="game-description">{game.description || 'لا يوجد وصف'}</p>
                  
                  <div className="game-specs">
                    <div className="spec-item">
                      <span className="spec-label">السعر/ساعة:</span>
                      <span className="spec-value">{game.price_per_hour} جنيه</span>
                    </div>
                    <div className="spec-item">
                      <span className="spec-label">السعر/15 دقيقة:</span>
                      <span className="spec-value">{game.price_per_15min} جنيه</span>
                    </div>
                    <div className="spec-item">
                      <span className="spec-label">الفرع:</span>
                      <span className="spec-value">{game.branch_name || 'غير محدد'}</span>
                    </div>
                  </div>
                  
                  <div className="game-status">
                    <span className={`status-badge ${game.status === 'متاح' ? 'available' : 'rented'}`}>
                      {game.status || 'غير محدد'}
                    </span>
                    <span className={`status-badge ${game.is_active ? 'active' : 'inactive'}`}>
                      {game.is_active ? 'نشطة' : 'غير نشطة'}
                    </span>
                  </div>
                </div>
                
                <div className="game-actions">
                  <button 
                    className={`btn-status ${game.status === 'متاح' ? 'make-unavailable' : 'make-available'}`}
                    onClick={() => handleToggleStatus(game.id, game.status)}
                  >
                    {game.status === 'متاح' ? 'تأجير' : 'إرجاع'}
                  </button>
                  <button 
                    className="btn-delete"
                    onClick={() => handleDeleteGame(game.id)}
                  >
                    {game.is_active ? 'حذف' : 'استعادة'}
                  </button>
                </div>
              </div>
            </div>
          ))}
      </div>

      {/* Modal إضافة لعبة */}
      {showAddModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2>إضافة لعبة جديدة</h2>
              <button 
                className="close-btn"
                onClick={() => setShowAddModal(false)}
              >
                ×
              </button>
            </div>
            <div className="modal-body">
              <div className="form-group">
                <label>اسم اللعبة *</label>
                <input
                  type="text"
                  value={newGame.name}
                  onChange={(e) => setNewGame({...newGame, name: e.target.value})}
                  placeholder="أدخل اسم اللعبة"
                />
              </div>
              
              <div className="form-row">
                <div className="form-group">
                  <label>التصنيف</label>
                  <select
                    value={newGame.category}
                    onChange={(e) => setNewGame({...newGame, category: e.target.value})}
                  >
                    {categories.map(category => (
                      <option key={category} value={category}>
                        {category}
                      </option>
                    ))}
                  </select>
                </div>
                
                <div className="form-group">
                  <label>الفرع *</label>
                  <select
                    value={newGame.branch_id}
                    onChange={(e) => setNewGame({...newGame, branch_id: e.target.value})}
                  >
                    {branches.map(branch => (
                      <option key={branch.id} value={branch.id}>
                        {branch.name}
                      </option>
                    ))}
                  </select>
                </div>
              </div>
              
              <div className="form-row">
                <div className="form-group">
                  <label>السعر/ساعة (جنيه)</label>
                  <input
                    type="number"
                    value={newGame.price_per_hour}
                    onChange={(e) => setNewGame({...newGame, price_per_hour: parseInt(e.target.value) || 0})}
                    min="0"
                  />
                </div>
                
                <div className="form-group">
                  <label>السعر/15 دقيقة (جنيه)</label>
                  <input
                    type="number"
                    value={newGame.price_per_15min}
                    onChange={(e) => setNewGame({...newGame, price_per_15min: parseInt(e.target.value) || 0})}
                    min="0"
                  />
                </div>
              </div>
              
              <div className="form-group">
                <label>رابط الصورة (اختياري)</label>
                <input
                  type="text"
                  value={newGame.image_url}
                  onChange={(e) => setNewGame({...newGame, image_url: e.target.value})}
                  placeholder="https://example.com/image.jpg"
                />
              </div>
              
              <div className="form-group">
                <label>الوصف (اختياري)</label>
                <textarea
                  value={newGame.description}
                  onChange={(e) => setNewGame({...newGame, description: e.target.value})}
                  placeholder="أدخل وصفاً للعبة"
                  rows="3"
                />
              </div>
            </div>
            <div className="modal-footer">
              <button 
                className="btn-cancel"
                onClick={() => setShowAddModal(false)}
              >
                إلغاء
              </button>
              <button 
                className="btn-save"
                onClick={handleAddGame}
              >
                حفظ
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default GamesManagement;
