// frontend/src/pages/employee/Dashboard.jsx (Ù…Ø­Ø¯Ø« ÙˆÙ…ØªÙƒØ§Ù…Ù„)
import React, { useState, useEffect, useCallback } from 'react';
import { Link } from 'react-router-dom';
import { 
  Gamepad2, Clock, Users, DollarSign, Zap, Package, 
  TrendingUp, AlertCircle, RefreshCw, BarChart3,
  Calendar, CheckCircle, Loader2, ShoppingCart,
  Building, MapPin, User as UserIcon, Watch,
  History, PlayCircle, PauseCircle, AlertTriangle
} from 'lucide-react';
import api from '../../services/api';
import authService from '../../services/authService';
import './EmployeeDashboard.css';

const EmployeeDashboard = () => {
  const [stats, setStats] = useState({
    availableGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0,
    totalGames: 0,
    totalCustomers: 0,
    shiftRevenue: 0
  });
  
  const [availableGames, setAvailableGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [recentRentals, setRecentRentals] = useState([]);
  const [topGames, setTopGames] = useState([]);
  const [user, setUser] = useState(null);
  const [branch, setBranch] = useState(null);
  const [currentShift, setCurrentShift] = useState(null);
  const [loading, setLoading] = useState({
    stats: true,
    games: true,
    rentals: true,
    shift: true,
    branch: true
  });
  const [lastUpdate, setLastUpdate] = useState(null);
  const [refreshing, setRefreshing] = useState(false);
  const [shiftTimer, setShiftTimer] = useState({
    hours: 0,
    minutes: 0,
    seconds: 0
  });

  // Ø­Ø³Ø§Ø¨ Ù…Ø¯Ø© Ø§Ù„Ø´ÙŠÙØª
  const calculateShiftDuration = useCallback(() => {
    if (!currentShift || !currentShift.start_time) {
      return { hours: 0, minutes: 0, seconds: 0 };
    }
    
    const startTime = new Date(currentShift.start_time);
    const now = new Date();
    const diffMs = now - startTime;
    
    const hours = Math.floor(diffMs / (1000 * 60 * 60));
    const minutes = Math.floor((diffMs % (1000 * 60 * 60)) / (1000 * 60));
    const seconds = Math.floor((diffMs % (1000 * 60)) / 1000);
    
    return { hours, minutes, seconds };
  }, [currentShift]);

  // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¹Ø¯ Ø§Ù„ØªÙ†Ø§Ø²Ù„ÙŠ
  useEffect(() => {
    if (!currentShift) return;
    
    const updateTimer = () => {
      setShiftTimer(calculateShiftDuration());
    };
    
    updateTimer();
    const interval = setInterval(updateTimer, 1000);
    
    return () => clearInterval(interval);
  }, [currentShift, calculateShiftDuration]);

  // ØªØ­Ù…ÙŠÙ„ Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ù„ÙˆØ­Ø©
  const loadDashboardData = useCallback(async () => {
    try {
      const currentUser = authService.getCurrentUser();
      setUser(currentUser);
      
      // ØªØ­Ù…ÙŠÙ„ Ù…Ø¹Ù„ÙˆÙ…Ø§Øª Ø§Ù„ÙØ±Ø¹
      await loadBranchInfo();
      
      // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ø­Ø§Ù„ÙŠ
      await loadCurrentShift();
      
      // Ø¥Ø°Ø§ ÙƒØ§Ù† Ù‡Ù†Ø§Ùƒ Ø´ÙŠÙØª Ù†Ø´Ø·ØŒ Ø­Ù…Ù‘Ù„ Ø¨ÙŠØ§Ù†Ø§ØªÙ‡
      if (currentShift) {
        await loadShiftStats();
        await loadActiveRentals();
        await loadRecentRentals();
        await loadTopGames();
      } else {
        // Ø¨Ø¯ÙˆÙ† Ø´ÙŠÙØªØŒ Ø­Ù…Ù‘Ù„ Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø¹Ø§Ù…Ø© ÙÙ‚Ø·
        await loadDashboardStats();
        await loadAvailableGames();
      }
      
    } catch (error) {
      console.error('Error loading dashboard data:', error);
    } finally {
      setLoading({ stats: false, games: false, rentals: false, shift: false, branch: false });
      setLastUpdate(new Date());
    }
  }, [currentShift]);

  // ØªØ­Ù…ÙŠÙ„ Ù…Ø¹Ù„ÙˆÙ…Ø§Øª Ø§Ù„ÙØ±Ø¹
  const loadBranchInfo = async () => {
    try {
      setLoading(prev => ({ ...prev, branch: true }));
      
      if (user?.branch_id) {
        const response = await api.get(`/branches/${user.branch_id}`);
        if (response.data.success) {
          setBranch(response.data.data);
        }
      }
    } catch (error) {
      console.error('Error loading branch info:', error);
    } finally {
      setLoading(prev => ({ ...prev, branch: false }));
    }
  };

  // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø´ÙŠÙØª Ø§Ù„Ø­Ø§Ù„ÙŠ
  const loadCurrentShift = async () => {
    try {
      setLoading(prev => ({ ...prev, shift: true }));
      
      const response = await api.get('/shifts/simple');
      if (response.data.success && response.data.data) {
        const shift = response.data.data;
        
        // ØªØ­Ù‚Ù‚ Ù…Ù† Ø£Ù† Ø§Ù„Ø´ÙŠÙØª ÙŠÙ†ØªÙ…ÙŠ Ù„Ù„Ù…Ø³ØªØ®Ø¯Ù… Ø§Ù„Ø­Ø§Ù„ÙŠ
        if (shift.employee_id === user?.id || shift.employee_name === user?.name) {
          setCurrentShift(shift);
        } else {
          console.log('âš ï¸ Ø§Ù„Ø´ÙŠÙØª Ù„Ø§ ÙŠÙ†ØªÙ…ÙŠ Ù„Ù„Ù…Ø³ØªØ®Ø¯Ù… Ø§Ù„Ø­Ø§Ù„ÙŠ');
          setCurrentShift(null);
        }
      } else {
        setCurrentShift(null);
      }
    } catch (error) {
      console.error('Error loading current shift:', error);
      setCurrentShift(null);
    } finally {
      setLoading(prev => ({ ...prev, shift: false }));
    }
  };

  // ØªØ­Ù…ÙŠÙ„ Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„Ø´ÙŠÙØª
  const loadShiftStats = async () => {
    try {
      setLoading(prev => ({ ...prev, stats: true }));
      
      if (!currentShift || !currentShift.id) return;
      
      // Ø¬Ù„Ø¨ Ø¥ÙŠØ±Ø§Ø¯ Ø§Ù„Ø´ÙŠÙØª
      const revenueResponse = await api.get(`/shifts/${currentShift.id}/revenue-details`);
      if (revenueResponse.data.success) {
        const revenueData = revenueResponse.data.data || {};
        const shiftRevenue = revenueData.total_revenue || 0;
        
        // Ø¬Ù„Ø¨ Ø¹Ø¯Ø¯ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø©
        const activeResponse = await api.get('/rentals/active');
        const activeCount = activeResponse.data.success ? activeResponse.data.data.length : 0;
        
        // Ø¬Ù„Ø¨ Ø¹Ø¯Ø¯ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„Ù…ØªØ§Ø­Ø©
        const gamesResponse = await api.get('/games/available');
        const availableCount = gamesResponse.data.success ? gamesResponse.data.data.length : 0;
        
        // Ø¬Ù„Ø¨ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù…ÙƒØªÙ…Ù„Ø© ÙÙŠ Ø§Ù„Ø´ÙŠÙØª
        const completedResponse = await api.get('/rentals/completed-only', {
          params: { shift_id: currentShift.id, branch_id: user?.branch_id }
        });
        const completedCount = completedResponse.data.success ? completedResponse.data.data.length : 0;
        
        setStats({
          availableGames: availableCount,
          activeRentals: activeCount,
          todayRevenue: shiftRevenue,
          todayRentals: completedCount,
          totalGames: 0, // Ø³ÙŠØªÙ… ØªØ­Ø¯ÙŠØ«Ù‡ Ù…Ù† loadAvailableGames
          totalCustomers: 0, // ØºÙŠØ± Ù…Ø­Ø¯Ø¯ ÙÙŠ Ø§Ù„Ø´ÙŠÙØª
          shiftRevenue: shiftRevenue
        });
      }
    } catch (error) {
      console.error('Error loading shift stats:', error);
    } finally {
      setLoading(prev => ({ ...prev, stats: false }));
    }
  };

  // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø¥Ø­ØµØ§Ø¦ÙŠØ§Øª Ø§Ù„Ø¹Ø§Ù…Ø© (Ø¹Ù†Ø¯ Ø¹Ø¯Ù… ÙˆØ¬ÙˆØ¯ Ø´ÙŠÙØª)
  const loadDashboardStats = async () => {
    try {
      setLoading(prev => ({ ...prev, stats: true }));
      
      const response = await api.get('/dashboard/stats');
      if (response.data.success) {
        const statsData = response.data.data || {};
        
        setStats({
          availableGames: statsData.available_games || 0,
          activeRentals: statsData.active_rentals || 0,
          todayRevenue: statsData.today_revenue || 0,
          todayRentals: statsData.today_rentals || 0,
          totalGames: statsData.total_games || 0,
          totalCustomers: statsData.total_customers || 0,
          shiftRevenue: 0
        });
      }
    } catch (error) {
      console.error('Error loading dashboard stats:', error);
    } finally {
      setLoading(prev => ({ ...prev, stats: false }));
    }
  };

  // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„Ù…ØªØ§Ø­Ø©
  const loadAvailableGames = async () => {
    try {
      setLoading(prev => ({ ...prev, games: true }));
      
      if (user?.branch_id) {
        const response = await api.get('/games/available', {
          params: { branch_id: user.branch_id }
        });
        
        if (response.data.success) {
          setAvailableGames(response.data.data || []);
        }
      }
    } catch (error) {
      console.error('Error loading available games:', error);
    } finally {
      setLoading(prev => ({ ...prev, games: false }));
    }
  };

  // ØªØ­Ù…ÙŠÙ„ Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª Ø§Ù„Ù†Ø´Ø·Ø©
  const loadActiveRentals = async () => {
    try {
      setLoading(prev => ({ ...prev, rentals: true }));
      
      if (user?.branch_id) {
        const response = await api.get('/rentals/active');
        
        if (response.data.success) {
          setActiveRentals(response.data.data || []);
        }
      }
    } catch (error) {
      console.error('Error loading active rentals:', error);
    } finally {
      setLoading(prev => ({ ...prev, rentals: false }));
    }
  };

  // ØªØ­Ù…ÙŠÙ„ Ø¢Ø®Ø± Ø§Ù„ØªØ£Ø¬ÙŠØ±Ø§Øª
  const loadRecentRentals = async () => {
    try {
      const response = await api.get('/rentals/recent', {
        params: { limit: 5, branch_id: user?.branch_id }
      });
      
      if (response.data.success) {
        setRecentRentals(response.data.data || []);
      }
    } catch (error) {
      console.error('Error loading recent rentals:', error);
    }
  };

  // ØªØ­Ù…ÙŠÙ„ Ø§Ù„Ø£Ù„Ø¹Ø§Ø¨ Ø§Ù„Ø£ÙƒØ«Ø± ØªØ£Ø¬ÙŠØ±Ø§Ù‹
  const loadTopGames = async () => {
    try {
      const response = await api.get('/games/top-rented', {
        params: { branch_id: user?.branch_id, limit: 3 }
      });
      
      if (response.data.success) {
        setTopGames(response.data.data || []);
      }
    } catch (error) {
      console.error('Error loading top games:', error);
    }
  };

  // Ø¨Ø¯Ø¡ Ø´ÙŠÙØª Ø¬Ø¯ÙŠØ¯
  const handleStartShift = async () => {
    try {
      setLoading(prev => ({ ...prev, shift: true }));
      
      const response = await api.post('/shifts/start');
      if (response.data.success) {
        const newShift = response.data.data;
        setCurrentShift(newShift);
        
        // ØªØ­Ø¯ÙŠØ« Ø§Ù„Ø¨ÙŠØ§Ù†Ø§Øª Ø¨Ø¹Ø¯ Ø¨Ø¯Ø¡ Ø§Ù„Ø´ÙŠÙØª
        await loadShiftStats();
        await loadActiveRentals();
        await loadAvailableGames();
        
        alert(`âœ… ØªÙ… Ø¨Ø¯Ø¡ Ø§Ù„Ø´ÙŠÙØª #${newShift.id} Ø¨Ù†Ø¬Ø§Ø­`);
