import React, { useState, useEffect, useRef, useCallback } from 'react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const Rentals = () => {
  // ==================== CONTEXT & STATES ====================
  const { user } = useAuth();
  
  // States ÿßŸÑÿ£ÿ≥ÿßÿ≥Ÿäÿ©
  const [branch, setBranch] = useState(null);
  const [loading, setLoading] = useState(true);
  const [loadingMessage, setLoadingMessage] = useState('ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...');
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ŸÑÿπÿßÿ® ŸàÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™
  const [games, setGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  
  // ŸÜÿßŸÅÿ∞ÿßÿ™ ŸÖÿÆÿ™ŸÑŸÅÿ©
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [showCancelModal, setShowCancelModal] = useState(false);
  const [showEndModal, setShowEndModal] = useState(false);
  const [showChangeGameModal, setShowChangeGameModal] = useState(false);
  const [showReRentalModal, setShowReRentalModal] = useState(false);
  const [showOpenTimeActions, setShowOpenTimeActions] = useState(false);
  const [showAddMoreGames, setShowAddMoreGames] = useState(false);
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑŸÜŸÖÿßÿ∞ÿ¨
  const [selectedGames, setSelectedGames] = useState([]);
  const [quantities, setQuantities] = useState({});
  const [customerName, setCustomerName] = useState('');
  const [customerPhone, setCustomerPhone] = useState('');
  const [customerNotes, setCustomerNotes] = useState('');
  const [durationType, setDurationType] = useState('fixed');
  const [selectedDuration, setSelectedDuration] = useState(15);
  const [paymentMethod, setPaymentMethod] = useState('cash');
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿ•ÿ∂ÿßŸÅŸäÿ©
  const [selectedOpenTimeRental, setSelectedOpenTimeRental] = useState(null);
  const [editRental, setEditRental] = useState(null);
  const [editGame, setEditGame] = useState(null);
  const [cancelRental, setCancelRental] = useState(null);
  const [cancelAmount, setCancelAmount] = useState('');
  const [endRental, setEndRental] = useState(null);
  const [endAmount, setEndAmount] = useState('');
  const [remainingAmount, setRemainingAmount] = useState(0);
  const [selectedNewGame, setSelectedNewGame] = useState(null);
  const [reRentalData, setReRentalData] = useState(null);
  const [reRentalGame, setReRentalGame] = useState(null);
  const [reRentalDuration, setReRentalDuration] = useState(60);
  
  // ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™
  const [currentShift, setCurrentShift] = useState(null);
  const [shiftStats, setShiftStats] = useState({
    totalRevenue: 0,
    totalRentals: 0,
    activeRentals: 0
  });
  
  // ÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™
  const [statistics, setStatistics] = useState({
    availableGames: 0,
    totalGames: 0,
    todayRevenue: 0,
    todayRentals: 0
  });
  
  // ÿ™ŸÜÿ®ŸäŸáÿßÿ™ ÿßŸÑŸàŸÇÿ™ ÿßŸÑŸÖŸÜÿ™ŸáŸä
  const [expiringAlerts, setExpiringAlerts] = useState([]);
  
  // Refs
  const timersRef = useRef({});
  const countdownTimersRef = useRef({});
  
  // ==================== CONSTANTS ====================
  const fixedDurations = [
    { value: 15, label: '15 ÿØŸÇŸäŸÇÿ©', priceMultiplier: 1 },
    { value: 30, label: '30 ÿØŸÇŸäŸÇÿ©', priceMultiplier: 2 },
    { value: 45, label: '45 ÿØŸÇŸäŸÇÿ©', priceMultiplier: 3 },
    { value: 60, label: 'ÿ≥ÿßÿπÿ© Ÿàÿßÿ≠ÿØÿ©', priceMultiplier: 4 },
    { value: 120, label: 'ÿ≥ÿßÿπÿ™ŸäŸÜ', priceMultiplier: 8 },
    { value: 180, label: '3 ÿ≥ÿßÿπÿßÿ™', priceMultiplier: 12 },
    { value: 240, label: '4 ÿ≥ÿßÿπÿßÿ™', priceMultiplier: 16 },
    { value: 300, label: '5 ÿ≥ÿßÿπÿßÿ™', priceMultiplier: 20 },
    { value: 360, label: '6 ÿ≥ÿßÿπÿßÿ™', priceMultiplier: 24 }
  ];
  
  // ==================== USE EFFECTS ====================
  useEffect(() => {
    if (user) {
      loadShiftData();
      loadData();
      
      const dataInterval = setInterval(() => {
        loadData();
      }, 30000);
      
      const checkExpiredInterval = setInterval(() => {
        checkAndAutoCompleteExpiredRentals();
      }, 60000);
      
      return () => {
        clearInterval(dataInterval);
        clearInterval(checkExpiredInterval);
        Object.values(timersRef.current).forEach(timer => clearInterval(timer));
        Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
      };
    }
  }, [user]);
  
  useEffect(() => {
    Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
    countdownTimersRef.current = {};
    
    activeRentals.forEach(rental => {
      if (rental.duration_minutes > 0 && rental.status === 'ŸÜÿ¥ÿ∑') {
        startCountdownTimer(rental.id, rental.start_time, rental.duration_minutes);
      }
    });
    
    return () => {
      Object.values(countdownTimersRef.current).forEach(timer => clearInterval(timer));
    };
  }, [activeRentals]);
  
  useEffect(() => {
    const checkAndCompleteExpiredRentals = async () => {
      const now = new Date();
      const alerts = [];
      
      activeRentals.forEach(rental => {
        if (rental.duration_minutes > 0 && rental.status === 'ŸÜÿ¥ÿ∑') {
          const startTime = new Date(rental.start_time);
          const endTime = new Date(startTime.getTime() + rental.duration_minutes * 60000);
          const timeLeftMs = endTime - now;
          
          if (timeLeftMs <= 0) {
            handleAutoCompleteRental(rental);
          } else if (timeLeftMs <= 300000) {
            alerts.push({
              rentalId: rental.id,
              rentalNumber: rental.rental_number,
              customerName: rental.customer_name,
              gameName: rental.game_name,
              timeLeft: Math.ceil(timeLeftMs / 60000)
            });
          }
        }
      });
      
      setExpiringAlerts(alerts);
    };
    
    const interval = setInterval(checkAndCompleteExpiredRentals, 60000);
    return () => clearInterval(interval);
  }, [activeRentals]);
  
  // ==================== API FUNCTIONS ====================
  const loadShiftData = async () => {
    try {
      if (!user?.branch_id) return;
      
      const response = await api.getActiveShift();
      if (response.success && response.data) {
        setCurrentShift(response.data);
        
        const shiftRentals = activeRentals.filter(r => r.shift_id === response.data.id);
        const revenue = shiftRentals.reduce((sum, rental) => sum + (rental.paid_amount || 0), 0);
        
        setShiftStats({
          totalRevenue: revenue,
          totalRentals: shiftRentals.length,
          activeRentals: shiftRentals.filter(r => r.status === 'ŸÜÿ¥ÿ∑').length
        });
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™:', error);
    }
  };
  
  const loadData = useCallback(async () => {
    try {
      if (!user?.branch_id) return;
      
      setLoadingMessage('ÿ¨ÿßÿ±Ÿä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™...');
      
      const branchResponse = await api.get(`/branches/${user.branch_id}`);
      if (branchResponse.success) {
        setBranch(branchResponse.data);
      }
      
      await loadGames();
      await loadActiveRentals();
      await loadCompletedRentals();
      await loadStatistics();
      
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:', error);
      showNotification('error', 'ŸÅÿ¥ŸÑ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™');
    } finally {
      setLoading(false);
    }
  }, [user]);
  
  const loadGames = async () => {
    try {
      if (!user?.branch_id) return;
      
      const gamesResponse = await api.get('/games', {
        params: { 
          branch_id: user.branch_id,
          status: 'active'
        }
      });
      
      if (gamesResponse.success) {
        const gamesWithQuantity = gamesResponse.data.map(game => ({
          ...game,
          available_quantity: game.quantity || 1,
          rented_quantity: 0
        }));
        
        // ÿ≠ÿ≥ÿßÿ® ÿßŸÑŸÉŸÖŸäÿßÿ™ ÿßŸÑŸÖÿ§ÿ¨ÿ±ÿ©
        const updatedGames = await calculateRentedQuantities(gamesWithQuantity);
        setGames(updatedGames);
      } else {
        setGames([]);
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®:', error);
      setGames([]);
    }
  };
  
  const calculateRentedQuantities = async (gamesList) => {
    try {
      const activeRentalsResponse = await api.get('/rentals/active/quantities');
      const activeRentalsData = activeRentalsResponse.success ? activeRentalsResponse.data : [];
      
      const rentedMap = {};
      activeRentalsData.forEach(rental => {
        rentedMap[rental.game_id] = rental.total_quantity;
      });
      
      return gamesList.map(game => ({
        ...game,
        rented_quantity: rentedMap[game.id] || 0,
        available_quantity: Math.max(0, game.quantity - (rentedMap[game.id] || 0))
      }));
      
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ≠ÿ≥ÿßÿ® ÿßŸÑŸÉŸÖŸäÿßÿ™ ÿßŸÑŸÖÿ§ÿ¨ÿ±ÿ©:', error);
      return gamesList;
    }
  };
  
  const loadActiveRentals = async () => {
    try {
      if (!user?.branch_id) return;
      
      const response = await api.get('/rentals/active', {
        params: { 
          branch_id: user.branch_id
        }
      });
      
      if (response.success) {
        const now = new Date();
        const rentalsWithTimers = response.data.map(rental => {
          let timeLeft = 'ŸàŸÇÿ™ ŸÖŸÅÿ™Ÿàÿ≠';
          let isExpired = false;
          
          if (rental.duration_minutes > 0) {
            const startTime = new Date(rental.start_time);
            const endTime = new Date(startTime.getTime() + rental.duration_minutes * 60000);
            const timeLeftMs = endTime - now;
            
            if (timeLeftMs > 0) {
              const minutesLeft = Math.floor(timeLeftMs / 60000);
              const hours = Math.floor(minutesLeft / 60);
              const minutes = minutesLeft % 60;
              timeLeft = hours > 0 ? `${hours}ÿ≥ ${minutes.toString().padStart(2, '0')}ÿØ` : `${minutes} ÿØŸÇŸäŸÇÿ©`;
            } else {
              timeLeft = 'ÿßŸÜÿ™ŸáŸâ';
              isExpired = true;
            }
          }
          
          return {
            ...rental,
            time_left: timeLeft,
            is_expired: isExpired
          };
        });
        
        setActiveRentals(rentalsWithTimers);
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©:', error);
      setActiveRentals([]);
    }
  };
  
  const loadCompletedRentals = async () => {
    try {
      if (!user?.branch_id) return;
      
      const response = await api.get('/rentals', {
        params: { 
          branch_id: user.branch_id,
          status: 'ŸÖŸÉÿ™ŸÖŸÑ',
          limit: 100,
          sort_by: 'actual_end_time',
          sort_order: 'DESC'
        }
      });
      
      if (response.success) {
        setCompletedRentals(response.data || []);
      } else {
        setCompletedRentals([]);
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©:', error);
      setCompletedRentals([]);
    }
  };
  
  const loadStatistics = async () => {
    try {
      const response = await api.get('/dashboard/stats');
      
      if (response.success && response.data) {
        setStatistics({
          availableGames: response.data.available_games || 0,
          totalGames: response.data.total_games || 0,
          todayRevenue: response.data.today_revenue || 0,
          todayRentals: response.data.today_rentals || 0,
          activeRentals: response.data.active_rentals || 0,
          totalRevenue: response.data.total_revenue || 0
        });
      } else {
        const availableGamesCount = games.filter(g => g.available_quantity > 0).length;
        
        setStatistics({
          availableGames: availableGamesCount,
          totalGames: games.length,
          todayRevenue: 0,
          todayRentals: 0,
          activeRentals: activeRentals.length,
          totalRevenue: 0
        });
      }
    } catch (error) {
      console.error('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ•ÿ≠ÿµÿßÿ¶Ÿäÿßÿ™:', error);
      
      const availableGamesCount = games.filter(g => g.available_quantity > 0).length;
      
      setStatistics({
        availableGames: availableGamesCount,
        totalGames: games.length,
        todayRevenue: 0,
        todayRentals: 0,
        activeRentals: activeRentals.length,
        totalRevenue: 0
      });
    }
  };
  
  // ==================== SHIFT FUNCTIONS ====================
  const startShift = async () => {
    try {
      showNotification('info', 'ÿ¨ÿßÿ±Ÿä ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™...');
      const response = await api.startShift();
      if (response.success) {
        setCurrentShift(response.data);
        showNotification('success', 'ÿ™ŸÖ ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ÿ®ŸÜÿ¨ÿßÿ≠');
        loadData();
      }
    } catch (error) {
      showNotification('error', 'ŸÅÿ¥ŸÑ ÿ®ÿØÿ° ÿßŸÑÿ¥ŸäŸÅÿ™');
    }
  };
  
  // ‚≠ê‚≠ê ÿØÿßŸÑÿ© ÿ¨ÿØŸäÿØÿ©: ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ ŸÖÿπ ÿ≠ŸÅÿ∏ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ‚≠ê‚≠ê
  const endShiftWithData = async () => {
    if (!currentShift) {
      showNotification('error', 'ŸÑÿß ŸäŸàÿ¨ÿØ ÿ¥ŸäŸÅÿ™ ŸÜÿ¥ÿ∑ ŸÑÿ•ŸÜŸáÿßÿ¶Ÿá');
      return;
    }
    
    if (!window.confirm('ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ•ŸÜŸáÿßÿ° ÿßŸÑÿ¥ŸäŸÅÿ™ Ÿàÿ≠ŸÅÿ∏ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ÿü')) {
      return;
    }
    
    try {
      showNotification('info', 'ÿ¨ÿßÿ±Ÿä ÿ≠ŸÅÿ∏ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™...');
      
      // ÿ¨ŸÖÿπ ÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ¥ŸäŸÅÿ™
      const shiftData = {
        shift_id: currentShift.id,
