import React, { useState, useEffect, useCallback, useMemo } from 'react';
import { 
  Search, RefreshCw, Eye, XCircle, User, Calendar,
  DollarSign, ChevronLeft, ChevronRight, X,
  Clock, Smartphone, Download, BarChart3, History,
  Activity, FilterX
} from 'lucide-react';
import './RentalsManagement.css';

const RentalsManagement = () => {
  // ==================== Ø¨ÙŠØ§Ù†Ø§Øª ØªØ¬Ø±ÙŠØ¨ÙŠØ© ====================
  const generateMockData = () => {
    const employees = ['Ø£Ø­Ù…Ø¯ Ù…Ø­Ù…Ø¯', 'Ù…Ø­Ù…ÙˆØ¯ Ø¹Ù„ÙŠ', 'Ø®Ø§Ù„Ø¯ Ø­Ø³Ù†', 'ÙŠØ§Ø³Ø± Ø¹Ø¨Ø¯Ø§Ù„Ù„Ù‡'];
    const games = ['Ø³ÙŠØ§Ø±Ø© ÙƒÙ‡Ø±Ø¨Ø§Ø¦ÙŠØ©', 'Ø³ÙƒÙˆØªØ±', 'Ø¯Ø±Ø§Ø¬Ø© Ù†Ø§Ø±ÙŠØ©', 'Ù‡ÙˆÙØ± Ø¨ÙˆØ±Ø¯', 'Ø³ÙŠØ§Ø±Ø© Ø£Ø·ÙØ§Ù„'];
    const customers = ['Ù…Ø­Ù…Ø¯ Ø£Ø­Ù…Ø¯', 'Ø£Ø­Ù…Ø¯ Ù…Ø­Ù…ÙˆØ¯', 'Ø¹Ù„ÙŠ Ø­Ø³Ù†', 'Ù…Ø­Ù…ÙˆØ¯ Ø®Ø§Ù„Ø¯', 'ÙŠØ§Ø³Ø± Ù…Ø­Ù…Ø¯', 'Ø¹Ù…Ø± Ø¹Ø¨Ø¯Ø§Ù„Ù„Ù‡'];
    const branches = ['Ø§Ù„ÙØ±Ø¹ Ø§Ù„Ø±Ø¦ÙŠØ³ÙŠ', 'ÙØ±Ø¹ Ø§Ù„Ù…Ø¹Ø§Ø¯ÙŠ', 'ÙØ±Ø¹ 6 Ø£ÙƒØªÙˆØ¨Ø±', 'ÙØ±Ø¹ Ø§Ù„ØªØ¬Ù…Ø¹'];
    
    const rentals = [];
    const now = new Date();
    
    // Ø¥Ù†Ø´Ø§Ø¡ 50 ØªØ£Ø¬ÙŠØ± ØªØ¬Ø±ÙŠØ¨ÙŠ
    for (let i = 1; i <= 50; i++) {
      const daysAgo = Math.floor(Math.random() * 30);
      const startTime = new Date(now);
      startTime.setDate(startTime.getDate() - daysAgo);
      startTime.setHours(9 + Math.floor(Math.random() * 10), Math.floor(Math.random() * 60));
      
      const duration = 15 * (Math.floor(Math.random() * 8) + 1); // 15-120 Ø¯Ù‚ÙŠÙ‚Ø©
      const endTime = new Date(startTime);
      endTime.setMinutes(endTime.getMinutes() + duration);
      
      const pricePer15Min = 25 + Math.floor(Math.random() * 75);
      const totalAmount = (duration / 15) * pricePer15Min;
      const isPaid = Math.random() > 0.3;
      const status = Math.random();
      
      let rentalStatus = 'Ù…ÙƒØªÙ…Ù„';
      if (status < 0.2) rentalStatus = 'Ù†Ø´Ø·';
      else if (status < 0.3) rentalStatus = 'Ù…Ù„ØºÙŠ';
      else rentalStatus = 'Ù…ÙƒØªÙ…Ù„';
      
      const isOpenTime = Math.random() > 0.7;
      
      rentals.push({
        id: i,
        rental_number: `RENT-${i.toString().padStart(4, '0')}`,
        game_id: (i % 5) + 1,
        game_name: games[i % games.length],
        customer_name: customers[i % customers.length] + ' ' + (Math.floor(i/5) + 1),
        customer_phone: `010${Math.floor(10000000 + Math.random() * 90000000)}`,
        employee_name: employees[i % employees.length],
        branch_id: (i % 4) + 1,
        branch_name: branches[i % branches.length],
        shift_id: Math.floor(i / 5) + 1,
        shift_number: `SHIFT-${(Math.floor(i / 5) + 1).toString().padStart(3, '0')}`,
        start_time: startTime.toISOString(),
        end_time: endTime.toISOString(),
        rental_type: isOpenTime ? 'open' : 'fixed',
        is_open_time: isOpenTime ? 1 : 0,
        duration_minutes: duration,
        total_amount: totalAmount,
        final_amount: totalAmount,
        paid_amount: isPaid ? totalAmount : 0,
        payment_status: isPaid ? 'Ù…Ø¯ÙÙˆØ¹' : 'ØºÙŠØ± Ù…Ø¯ÙÙˆØ¹',
        status: rentalStatus,
        user_id: (i % 4) + 1,
        notes: ''
      });
    }
    
    return rentals;
  };

  // ==================== Ø¨ÙŠØ§Ù†Ø§Øª Ø§Ù„Ø´ÙŠÙØªØ§Øª Ø§Ù„ØªØ¬Ø±ÙŠØ¨ÙŠØ© ====================
  const generateMockShifts = (rentals) => {
    const shiftMap = {};
    
    rentals.forEach(rental => {
      if (!shiftMap[rental.shift_id]) {
        shiftMap[rental.shift_id] = {
          id: rental.shift_id,
          shift_number: rental.shift_number,
