import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { 
  Gamepad2, Clock, User, Phone, MapPin, Mail, 
  ShoppingCart, DollarSign, CheckCircle, XCircle, Eye, 
  Plus, Minus, Trash2, Calendar, Printer, Download,
  RefreshCw, AlertCircle, Check, X, Building, Zap,
  Lock, Unlock, Loader2, Filter, Search, ChevronRight
} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

// ==================== دوال مساعدة ====================
const formatCurrency = (amount) => {
  if (!amount && amount !== 0) return '0 ج.م';
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const validatePhone = (phone) => {
  if (!phone) return false;
  const cleaned = phone.replace(/\D/g, '');
  return cleaned.length === 11 && cleaned.startsWith('01');
};

const calculateDuration = (startTime, endTime) => {
  if (!startTime || !endTime) return 0;
  const start = new Date(startTime);
  const end = new Date(endTime);
  return Math.round((end - start) / (1000 * 60));
};

// ==================== مكون الألعاب المتاحة ====================
const GamesGrid = ({ games, branchId, onSelectGame, loading }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('all');
  const [availableGames, setAvailableGames] = useState([]);

  useEffect(() => {
    if (!branchId || !games) return;
    
    const branchGames = games.filter(game => {
      if (!game) return false;
      const gameBranchId = parseInt(game.branch_id);
      const currentBranchId = parseInt(branchId);
      return gameBranchId === currentBranchId && game.status === 'متاح';
    });
    
    setAvailableGames(branchGames);
  }, [games, branchId]);

  const filteredGames = useMemo(() => {
    if (!availableGames.length) return [];
    
    return availableGames.filter(game => {
      const matchesSearch = game.name?.toLowerCase().includes(searchTerm.toLowerCase()) ||
                          game.category?.toLowerCase().includes(searchTerm.toLowerCase());
      
      const matchesCategory = selectedCategory === 'all' || game.category === selectedCategory;
      
      return matchesSearch && matchesCategory;
    });
  }, [availableGames, searchTerm, selectedCategory]);

  const categories = useMemo(() => {
    if (!availableGames.length) return ['all'];
    
    const cats = new Set(['all']);
    availableGames.forEach(game => {
      if (game.category) cats.add(game.category);
    });
    return Array.from(cats);
  }, [availableGames]);

  const getGameImage = useCallback((game) => {
    if (!game) return '/images/default-game.jpg';
    
    const imageMap = {
      'دريفت كار': '/images/Driftcar.jpg',
      'هافر بورد': '/images/Hoverboard.jpg',
      'سكوتر': '/images/Scooter.jpg',
      'عربة كهربائية': '/images/Car.jpg',
      'موتوسيكل كهربائي': '/images/Motor.jpg',
      'هارلي': '/images/harley.jpg',
      'سيجواي': '/images/Segway.jpg',
      'كرازي كار': '/images/Ninebot.jpg',
      'سكيت كهربائي': '/images/Skate.jpg',
      'ترامبولين': '/images/Trampoline.jpg',
      'محاكي': '/images/Simulator.jpg'
    };
    
    if (game.image_url && game.image_url !== 'default-game.jpg') {
      return game.image_url.startsWith('http') ? game.image_url : `/images/${game.image_url}`;
    }
    
    return imageMap[game.name] || '/images/default-game.jpg';
  }, []);

  if (!branchId) {
    return (
      <div className="games-grid-section">
        <div className="empty-state">
          <Building size={48} />
          <h3>الرجاء تحديد فرع</h3>
          <p>يجب تحديد فرع لعرض الألعاب المتاحة</p>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div className="games-grid-section">
        <div className="loading-state">
          <Loader2 className="spinner" size={32} />
          <p>جاري تحميل الألعاب...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="games-grid-section">
      <div className="section-header">
        <h3 className="section-title">
          <Gamepad2 size={24} />
          الألعاب المتاحة
          <span className="section-count">({availableGames.length})</span>
        </h3>
        
        <div className="section-controls">
          <div className="search-box">
            <Search size={18} />
            <input
              type="text"
              placeholder="ابحث عن لعبة..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
            {searchTerm && (
              <button onClick={() => setSearchTerm('')} className="clear-search">
                <X size={16} />
              </button>
            )}
          </div>
          
          {categories.length > 1 && (
            <div className="filter-controls">
              <Filter size={18} />
              <select 
                value={selectedCategory}
                onChange={(e) => setSelectedCategory(e.target.value)}
                className="filter-select"
              >
                {categories.map(cat => (
                  <option key={cat} value={cat}>
                    {cat === 'all' ? 'جميع الفئات' : cat}
                  </option>
                ))}
              </select>
            </div>
          )}
        </div>
      </div>
      
      {availableGames.length === 0 ? (
        <div className="empty-state">
          <Gamepad2 size={48} />
          <h3>لا توجد ألعاب متاحة</h3>
          <p>جميع الألعاب مؤجرة أو قيد الصيانة</p>
        </div>
      ) : filteredGames.length === 0 ? (
        <div className="empty-state">
  <Search size={48} />
  <h3>لم يتم العثور على ألعاب</h3>
  <p>لا توجد ألعاب تطابق &quot;{searchTerm}&quot;</p>
</div>

      ) : (
        <div className="games-grid">
          {filteredGames.map(game => (
            <div 
              key={game.id} 
              className="game-card"
              onClick={() => onSelectGame(game)}
            >
              <div className="game-image-container">
                <img 
                  src={getGameImage(game)} 
                  alt={game.name}
                  className="game-image"
                  onError={(e) => e.target.src = '/images/default-game.jpg'}
                />
                <div className="game-status available">
                  <CheckCircle size={14} />
                  <span>متاح</span>
                </div>
              </div>
              
              <div className="game-info">
                <h4>{game.name}</h4>
                <div className="game-category">{game.category}</div>
                <div className="game-price">
                  {formatCurrency(game.price_per_15min)} / 15 دقيقة
                </div>
                <div className="game-actions">
                  <button className="btn-select-game">
                    <Plus size={16} />
                    إضافة للتأجير
                  </button>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );
};

// ==================== مكون تأجير متعدد ====================
const MultiRentalModal = ({ 
  show, 
  onClose, 
  selectedGames, 
  onConfirmRental, 
  currentShift 
  
}) => {
  const [customerInfo, setCustomerInfo] = useState({
    name: '',
    phone: '',
    email: '',
    address: ''
  });
  const [showAddMoreGames, setShowAddMoreGames] = useState(false);

  const [rentalItems, setRentalItems] = useState([]);
  const [paymentMethod, setPaymentMethod] = useState('كاش');
  const [paymentStatus, setPaymentStatus] = useState('عند الإنهاء');
  const [notes, setNotes] = useState('');
  const [isSubmitting, setIsSubmitting] = useState(false);

  useEffect(() => {
    if (show && selectedGames.length > 0) {
      const items = selectedGames.map(game => ({
        id: Date.now() + Math.random(),
        gameId: game.id,
        gameName: game.name,
        gameImage: '/images/default-game.jpg',
        rentalType: 'fixed',
        duration: 15,
        pricePer15min: game.price_per_15min || 100,
        quantity: 1,
        notes: ''
      }));
      setRentalItems(items);
    }
  }, [show, selectedGames]);

  const updateRentalItem = (id, updates) => {
    setRentalItems(items => 
      items.map(item => 
        item.id === id ? { ...item, ...updates } : item
      )
    );
  };

  const removeRentalItem = (id) => {
    setRentalItems(items => items.filter(item => item.id !== id));
  };

  const calculateItemPrice = useCallback((item) => {
    if (item.rentalType === 'open') return 0;
    const units = Math.ceil(item.duration / 15);
    return (item.pricePer15min * units * item.quantity) || 0;
  }, []);

  const calculateTotal = useCallback(() => {
    return rentalItems.reduce((total, item) => {
      return total + calculateItemPrice(item);
    }, 0);
  }, [rentalItems, calculateItemPrice]);

  const handleSubmit = async () => {
    if (!customerInfo.name || !customerInfo.phone) {
      alert('يرجى إدخال اسم العميل ورقم الهاتف');
      return;
    }
    
    if (!validatePhone(customerInfo.phone)) {
      alert('رقم الهاتف غير صحيح. يجب أن يكون 11 رقماً ويبدأ بـ 01');
      return;
    }
    
    if (rentalItems.length === 0) {
      alert('يرجى إضافة ألعاب للتأجير');
      return;
    }

    setIsSubmitting(true);

    try {
      const rentalData = {
        customerInfo,
        rentalItems,
        paymentMethod,
        paymentStatus,
        notes,
        totalAmount: calculateTotal(),
        shiftId: currentShift?.id
      };

      await onConfirmRental(rentalData);
      
      setCustomerInfo({ name: '', phone: '', email: '', address: '' });
      setRentalItems([]);
      setPaymentMethod('كاش');
      setPaymentStatus('عند الإنهاء');
      setNotes('');
      onClose();
      
    } catch (error) {
      console.error('خطأ في إنشاء التأجير:', error);
      alert('حدث خطأ في إنشاء التأجير');
    } finally {
      setIsSubmitting(false);
    }
  };

  if (!show) return null;

  return (
    <div className="modal-overlay">
      <div className="modal rental-modal">
        <div className="modal-header">
          <h2>
            <ShoppingCart size={24} />
            تأجير جديد ({selectedGames.length} لعبة)
          </h2>
          <button onClick={onClose} className="modal-close">
            <X size={24} />
          </button>
        </div>
        
        <div className="modal-body">
          {/* معلومات العميل */}
          <div className="form-section">
            <h3>
              <User size={20} />
              معلومات العميل
            </h3>
             <button
              type="button"
              className="btn btn-outline-secondary btn-sm"
              onClick={() => setShowAddMoreGames(true)}
              disabled={isSubmitting}
            >
              <Plus size={16} />
              إضافة لعبة أخرى
            </button>
            <div className="form-grid">
              <div className="form-group">
                <label>الاسم الكامل *</label>
                <div className="input-with-icon">
                  <User size={18} />
                  <input
                    type="text"
                    value={customerInfo.name}
                    onChange={(e) => setCustomerInfo({...customerInfo, name: e.target.value})}
                    placeholder="أدخل اسم العميل"
                    required
                  />
                </div>
              </div>
              
              <div className="form-group">
                <label>رقم الهاتف *</label>
                <div className="input-with-icon">
                  <Phone size={18} />
                  <input
                    type="tel"
                    value={customerInfo.phone}
                    onChange={(e) => setCustomerInfo({...customerInfo, phone: e.target.value})}
                    placeholder="01xxxxxxxxx"
                    required
                  />
                </div>
                {customerInfo.phone && !validatePhone(customerInfo.phone) && (
                  <span className="error-text">رقم هاتف غير صحيح</span>
                )}
              </div>
              
              <div className="form-group">
                <label>البريد الإلكتروني</label>
                <div className="input-with-icon">
                  <Mail size={18} />
                  <input
                    type="email"
                    value={customerInfo.email}
                    onChange={(e) => setCustomerInfo({...customerInfo, email: e.target.value})}
                    placeholder="email@example.com"
                  />
                </div>
              </div>
              
              <div className="form-group">
                <label>العنوان</label>
                <div className="input-with-icon">
                  <MapPin size={18} />
                  <input
                    type="text"
                    value={customerInfo.address}
                    onChange={(e) => setCustomerInfo({...customerInfo, address: e.target.value})}
                    placeholder="العنوان الكامل"
                  />
                </div>
              </div>
            </div>
          </div>
          
          {/* قائمة الألعاب */}
          <div className="rental-items-section">
            <h3>
              <ShoppingCart size={20} />
              قائمة الألعاب ({rentalItems.length})
            </h3>
            
            {rentalItems.map(item => (
              <div key={item.id} className="rental-item">
                <div className="item-header">
                  <div className="item-info">
                    <span className="item-name">{item.gameName}</span>
                    <span className="item-price">
                      {formatCurrency(item.pricePer15min)} / 15 دقيقة
                    </span>
                  </div>
                  <button 
                    onClick={() => removeRentalItem(item.id)}
                    className="btn-remove"
                    disabled={isSubmitting}
                  >
                    <Trash2 size={18} />
                  </button>
                </div>
                
                <div className="item-options">
                  <div className="option-row">
                    <div className="option-group">
                      <label>نوع التأجير:</label>
                      <div className="toggle-buttons">
                        <button
                          type="button"
                          className={`toggle-btn ${item.rentalType === 'fixed' ? 'active' : ''}`}
                          onClick={() => updateRentalItem(item.id, { rentalType: 'fixed', duration: 15 })}
                          disabled={isSubmitting}
                        >
                          <Clock size={16} />
                          وقت ثابت
                        </button>
                        <button
                          type="button"
                          className={`toggle-btn ${item.rentalType === 'open' ? 'active' : ''}`}
                          onClick={() => updateRentalItem(item.id, { rentalType: 'open', duration: 0 })}
                          disabled={isSubmitting}
                        >
                          <Unlock size={16} />
                          وقت مفتوح
                        </button>
                      </div>
                    </div>
                    
                    {item.rentalType === 'fixed' && (
                      <div className="option-group">
                        <label>المدة:</label>
                        <div className="duration-buttons">
                          {[15, 30, 45, 60, 90, 120].map(minutes => (
                            <button
                              key={minutes}
                              type="button"
                              className={`duration-btn ${item.duration === minutes ? 'active' : ''}`}
                              onClick={() => updateRentalItem(item.id, { duration: minutes })}
                              disabled={isSubmitting}
                            >
                              {minutes} دقيقة
                            </button>
                          ))}
                        </div>
                      </div>
                    )}
                    
                    <div className="option-group">
                      <label>الكمية:</label>
                      <div className="quantity-control">
                        <button 
                          type="button"
                          onClick={() => updateRentalItem(item.id, { 
                            quantity: Math.max(1, item.quantity - 1) 
                          })}
                          className="quantity-btn"
                          disabled={isSubmitting}
                        >
                          <Minus size={16} />
                        </button>
                        <span className="quantity-value">{item.quantity}</span>
                        <button 
                          type="button"
                          onClick={() => updateRentalItem(item.id, { 
                            quantity: item.quantity + 1 
                          })}
                          className="quantity-btn"
                          disabled={isSubmitting}
                        >
                          <Plus size={16} />
                        </button>

                        
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
          
          {/* المعلومات المالية */}
          <div className="payment-section">
            <h3>
              <DollarSign size={20} />
              المعلومات المالية
            </h3>
            
            <div className="form-row">
              <div className="form-group">
                <label>طريقة الدفع:</label>
                <select 
                  value={paymentMethod}
                  onChange={(e) => setPaymentMethod(e.target.value)}
                  disabled={isSubmitting}
                >
                  <option value="كاش">💵 نقدي</option>
                  <option value="بطاقة">💳 بطاقة ائتمان</option>
                  <option value="محفظة">📱 محفظة إلكترونية</option>
                  <option value="تحويل">🏦 تحويل بنكي</option>
                </select>
              </div>
              
              <div className="form-group">
                <label>حالة الدفع:</label>
                <select 
                  value={paymentStatus}
                  onChange={(e) => setPaymentStatus(e.target.value)}
                  disabled={isSubmitting}
                >
                  <option value="عند الإنهاء">⏳ عند الإنهاء</option>
                  <option value="مدفوع">✅ مدفوع</option>
                  <option value="جزئي">💰 مدفوع جزئياً</option>
                </select>
              </div>
            </div>
            
            <div className="form-group">
              <label>ملاحظات:</label>
              <textarea
                value={notes}
                onChange={(e) => setNotes(e.target.value)}
                placeholder="أي ملاحظات إضافية..."
                rows="3"
                disabled={isSubmitting}
              />
            </div>
          </div>
          
          {/* الملخص */}
          <div className="summary-section">
            <div className="summary-row">
              <span>عدد الألعاب:</span>
              <span>{rentalItems.length}</span>
            </div>
            <div className="summary-row">
              <span>إجمالي القطع:</span>
              <span>{rentalItems.reduce((sum, item) => sum + item.quantity, 0)}</span>
            </div>
            <div className="summary-row total">
              <span>المبلغ الإجمالي:</span>
              <span>{formatCurrency(calculateTotal())}</span>
            </div>
          </div>
        </div>
        
        <div className="modal-footer">
          <button 
            onClick={onClose} 
            className="btn btn-secondary"
            disabled={isSubmitting}
          >
            <X size={18} />
            إلغاء
          </button>
          <button 
            onClick={handleSubmit} 
            className="btn btn-primary"
            disabled={isSubmitting || rentalItems.length === 0}
          >
            {isSubmitting ? (
              <>
                <Loader2 className="spinner" size={18} />
                جاري المعالجة...
              </>
            ) : (
              <>
                <Check size={18} />
                بدء التأجير
              </>
            )}
          </button>
        </div>
      </div>
    </div>
  );
};

// ==================== مكون التأجيرات النشطة ====================
const ActiveRentalsTable = ({ rentals, onAction, loading }) => {
  const [countdowns, setCountdowns] = useState({});

  useEffect(() => {
    const updateCountdowns = () => {
      const newCountdowns = {};
      
      rentals.forEach(rental => {
        if (!rental) return;
        
        const isOpenTime = rental.is_open_time || rental.rental_type === 'open';
        
        if (!isOpenTime) {
          const endTime = new Date(rental.start_time);
          endTime.setMinutes(endTime.getMinutes() + (rental.duration_minutes || 0));
          const now = new Date();
          
          if (now > endTime) {
            newCountdowns[rental.id] = '⏰ انتهى';
          } else {
            const diffMs = endTime - now;
            const minutes = Math.floor(diffMs / 60000);
            const seconds = Math.floor((diffMs % 60000) / 1000);
            newCountdowns[rental.id] = `${minutes}:${seconds.toString().padStart(2, '0')}`;
          }
        } else {
          const startTime = new Date(rental.start_time);
          const now = new Date();
          const diffMs = now - startTime;
          const minutes = Math.floor(diffMs / 60000);
          const seconds = Math.floor((diffMs % 60000) / 1000);
          newCountdowns[rental.id] = `⏳ ${minutes}:${seconds.toString().padStart(2, '0')}`;
        }
      });
      
      setCountdowns(newCountdowns);
    };

    updateCountdowns();
    const interval = setInterval(updateCountdowns, 1000);
    
    return () => clearInterval(interval);
  }, [rentals]);

  const getRentalTimeInfo = useCallback((rental) => {
    if (!rental) return { type: '', remaining: '' };
    
    const isOpenTime = rental.is_open_time || rental.rental_type === 'open';
    
    if (isOpenTime) {
      return {
        type: '⏳ وقت مفتوح',
        remaining: countdowns[rental.id] || '⏳ 0:00'
      };
    }
    
    return {
      type: `${rental.duration_minutes} دقيقة`,
      remaining: countdowns[rental.id] || `${rental.duration_minutes || 0}:00`
    };
  }, [countdowns]);

  const calculateRentalAmount = useCallback((rental) => {
    if (!rental) return formatCurrency(0);
    
    if (rental.total_amount && rental.total_amount > 0) {
      return formatCurrency(rental.total_amount);
    }
    
    const isOpenTime = rental.is_open_time || rental.rental_type === 'open';
    if (isOpenTime) {
      const startTime = new Date(rental.start_time);
      const now = new Date();
      const minutes = Math.floor((now - startTime) / 60000);
      const units = Math.ceil(minutes / 15);
      const pricePer15Min = rental.price_per_15min || 100;
      
      return formatCurrency(units * pricePer15Min);
    }
    
    return formatCurrency(rental.total_amount || 0);
  }, []);

  if (loading) {
    return (
      <div className="active-rentals-section">
        <div className="loading-state">
          <Loader2 className="spinner" size={32} />
          <p>جاري تحميل التأجيرات...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="active-rentals-section">
      <div className="section-header">
        <h3 className="section-title">
          <Zap size={24} />
          التأجيرات النشطة
          <span className="section-count">({rentals.length})</span>
        </h3>
      </div>
      
      {rentals.length === 0 ? (
        <div className="empty-state">
          <Clock size={48} />
          <h3>لا توجد تأجيرات نشطة</h3>
          <p>ابدأ تأجير جديد من قائمة الألعاب</p>
        </div>
      ) : (
        <div className="table-container">
          <table className="rentals-table">
            <thead>
              <tr>
                <th>رقم التأجير</th>
                <th>العميل</th>
                <th>اللعبة</th>
                <th>المدة</th>
                <th>الوقت المتبقي</th>
                <th>المبلغ</th>
                <th>حالة الدفع</th>
                <th>الإجراءات</th>
              </tr>
            </thead>
            <tbody>
              {rentals.map(rental => {
                const timeInfo = getRentalTimeInfo(rental);
                const isOpenTime = rental.is_open_time || rental.rental_type === 'open';
                
                return (
                  <tr key={rental.id}>
                    <td>
                      <strong>#{rental.rental_number || rental.id}</strong>
                      {isOpenTime && <span className="badge open">مفتوح</span>}
                    </td>
                    <td>
                      <div className="customer-cell">
                        <div className="customer-name">{rental.customer_name}</div>
                        <div className="customer-phone">{rental.customer_phone}</div>
                      </div>
                    </td>
                    <td>{rental.game_name}</td>
                    <td>{timeInfo.type}</td>
                    <td>
                      <div className="countdown-cell">
                        <Clock size={14} />
                        <span>{timeInfo.remaining}</span>
                      </div>
                    </td>
                    <td className="amount-cell">
                      {calculateRentalAmount(rental)}
                    </td>
                    <td>
                      <span className={`payment-status ${rental.payment_status === 'مدفوع' ? 'paid' : 'pending'}`}>
                        {rental.payment_status === 'مدفوع' ? '✅ مدفوع' : '⏳ ' + rental.payment_status}
                      </span>
                    </td>
                    <td>
                      <div className="action-buttons">
                        <button
                          onClick={() => onAction('details', rental)}
                          className="action-btn view"
                          title="عرض التفاصيل"
                        >
                          <Eye size={16} />
                        </button>
                        
                        {isOpenTime && (
                          <button
                            onClick={() => onAction('end', rental)}
                            className="action-btn end"
                            title="إنهاء الوقت"
                          >
                            <Clock size={16} />
                          </button>
                        )}
                        
                        <button
                          onClick={() => onAction('cancel', rental)}
                          className="action-btn cancel"
                          title="إلغاء التأجير"
                        >
                          <XCircle size={16} />
                        </button>
                      </div>
                    </td>
                  </tr>
                );
              })}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

// ==================== مكون التأجيرات المكتملة ====================
const CompletedRentalsTable = ({ rentals, onExport, onPrint, loading, isManager }) => {
  const [selectedPeriod, setSelectedPeriod] = useState('today');
  const [filteredRentals, setFilteredRentals] = useState([]);

  useEffect(() => {
    const filterRentals = () => {
      if (!rentals || rentals.length === 0) {
        setFilteredRentals([]);
        return;
      }

      const now = new Date();
      let filtered = [...rentals];

      switch (selectedPeriod) {
        case 'today': {
          const today = now.toISOString().split('T')[0];
          filtered = rentals.filter(r => r.end_time && r.end_time.split('T')[0] === today);
          break;
        }
        case 'week': {
          const weekAgo = new Date();
          weekAgo.setDate(weekAgo.getDate() - 7);
          filtered = rentals.filter(r => r.end_time && new Date(r.end_time) >= weekAgo);
          break;
        }
        case 'month': {
          const monthAgo = new Date();
          monthAgo.setMonth(monthAgo.getMonth() - 1);
          filtered = rentals.filter(r => r.end_time && new Date(r.end_time) >= monthAgo);
          break;
        }
        default:
          break;
      }

      setFilteredRentals(filtered);
    };

    filterRentals();
  }, [rentals, selectedPeriod]);

  const totalRevenue = useMemo(() => {
    return filteredRentals.reduce((sum, rental) => 
      sum + (parseFloat(rental.total_amount) || 0), 0
    );
  }, [filteredRentals]);

  if (loading) {
    return (
      <div className="completed-rentals-section">
        <div className="loading-state">
          <Loader2 className="spinner" size={32} />
          <p>جاري تحميل التأجيرات المكتملة...</p>
        </div>
      </div>
    );
  }

  if (!isManager) return null;

  return (
    <div className="completed-rentals-section">
      <div className="section-header">
        <h3 className="section-title">
          <CheckCircle size={24} />
          التأجيرات المكتملة
          <span className="section-count">({filteredRentals.length})</span>
        </h3>
        
        <div className="section-controls">
          <div className="period-selector">
            <select
              value={selectedPeriod}
              onChange={(e) => setSelectedPeriod(e.target.value)}
              className="period-select"
            >
              <option value="today">اليوم</option>
              <option value="week">آخر 7 أيام</option>
              <option value="month">آخر 30 يوم</option>
              <option value="all">الكل</option>
            </select>
          </div>
          
          <button
            onClick={() => onExport(filteredRentals)}
            className="btn btn-success"
            disabled={filteredRentals.length === 0}
          >
            <Download size={18} />
            تصدير
          </button>
          
          <button
            onClick={() => onPrint(filteredRentals)}
            className="btn btn-primary"
            disabled={filteredRentals.length === 0}
          >
            <Printer size={18} />
            طباعة
          </button>
        </div>
      </div>
      
      {filteredRentals.length === 0 ? (
        <div className="empty-state">
          <CheckCircle size={48} />
          <h3>لا توجد تأجيرات مكتملة</h3>
          <p>لم تكتمل أي تأجيرات في الفترة المحددة</p>
        </div>
      ) : (
        <>
          <div className="revenue-summary">
            <div className="summary-card">
              <DollarSign size={32} />
              <div>
                <div className="summary-label">إجمالي الإيرادات</div>
                <div className="summary-value">{formatCurrency(totalRevenue)}</div>
              </div>
            </div>
          </div>
          
          <div className="table-container">
            <table className="rentals-table">
              <thead>
                <tr>
                  <th>رقم التأجير</th>
                  <th>العميل</th>
                  <th>اللعبة</th>
                  <th>وقت البدء</th>
                  <th>وقت الانتهاء</th>
                  <th>المدة</th>
                  <th>المبلغ</th>
                  <th>حالة الدفع</th>
                </tr>
              </thead>
              <tbody>
                {filteredRentals.map(rental => (
                  <tr key={rental.id}>
                    <td>#{rental.rental_number || rental.id}</td>
                    <td>
                      <div className="customer-info">
                        <div className="customer-name">{rental.customer_name}</div>
                        <div className="customer-phone">{rental.customer_phone}</div>
                      </div>
                    </td>
                    <td>{rental.game_name}</td>
                    <td>{formatTime(rental.start_time)}</td>
                    <td>{formatTime(rental.end_time)}</td>
                    <td>{calculateDuration(rental.start_time, rental.end_time)} دقيقة</td>
                    <td className="amount-cell">{formatCurrency(rental.total_amount)}</td>
                    <td>
                      <span className={`payment-status ${rental.payment_status === 'مدفوع' ? 'paid' : 'partial'}`}>
                        {rental.payment_status === 'مدفوع' ? '✅ مدفوع' : '⚠️ ' + rental.payment_status}
                      </span>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      )}
    </div>
  );
};

// ==================== مكون لوحة التحكم ====================
const DashboardStats = ({ stats, currentShift, onStartShift, onEndShift, loading }) => {
  if (loading) {
    return (
      <div className="dashboard-stats">
        <div className="stats-grid">
          {[1, 2, 3, 4].map(i => (
            <div key={i} className="stat-card skeleton"></div>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="dashboard-stats">
      <div className="stats-grid">
        <div className="stat-card">
          <div className="stat-icon">
            <Gamepad2 size={24} />
          </div>
          <div className="stat-content">
            <div className="stat-value">{stats.availableGames || 0}</div>
            <div className="stat-label">ألعاب متاحة</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">
            <Zap size={24} />
          </div>
          <div className="stat-content">
            <div className="stat-value">{stats.activeRentals || 0}</div>
            <div className="stat-label">تأجيرات نشطة</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">
            <DollarSign size={24} />
          </div>
          <div className="stat-content">
            <div className="stat-value">{formatCurrency(stats.todayRevenue || 0)}</div>
            <div className="stat-label">إيراد اليوم</div>
          </div>
        </div>
        
        <div className="stat-card">
          <div className="stat-icon">
            <Clock size={24} />
          </div>
          <div className="stat-content">
            <div className="stat-value">
              {currentShift ? `#${currentShift.id}` : '--'}
            </div>
            <div className="stat-label">الشيفت الحالي</div>
            <div className="shift-actions">
              {!currentShift ? (
                <button onClick={onStartShift} className="btn btn-success">
                  <Zap size={14} />
                  بدء شيفت
                </button>
              ) : (
                <button onClick={onEndShift} className="btn btn-warning">
                  <Lock size={14} />
                  إنهاء شيفت
                </button>
              )}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// ==================== المكون الرئيسي ====================
const Rentals = () => {
  const { user } = useAuth();
  const isManager = user?.role === 'admin' || user?.role === 'branch_manager';

  const [loading, setLoading] = useState({
    games: true,
    rentals: true,
    shift: true,
    stats: true
  });
  
  const [games, setGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [currentShift, setCurrentShift] = useState(null);
  const [selectedGames, setSelectedGames] = useState([]);
  const [showRentalModal, setShowRentalModal] = useState(false);
  
  const [stats, setStats] = useState({
    availableGames: 0,
    totalGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

  // ==================== تحميل البيانات ====================
  const loadGames = async () => {
    try {
      const response = await api.get('/games', {
        params: { branch_id: user?.branch_id }
      });
      
      if (response.success) {
        setGames(response.data);
        setStats(prev => ({
          ...prev,
          availableGames: response.data.filter(g => g.status === 'متاح').length,
          totalGames: response.data.length
        }));
      }
    } catch (error) {
      console.error('خطأ في تحميل الألعاب:', error);
    } finally {
      setLoading(prev => ({ ...prev, games: false }));
    }
  };

  const loadActiveRentals = async () => {
    try {
      const response = await api.get('/rentals/active');
      
      if (response.success) {
        setActiveRentals(response.data);
        setStats(prev => ({
          ...prev,
          activeRentals: response.data.length
        }));
      }
    } catch (error) {
      console.error('خطأ في تحميل التأجيرات النشطة:', error);
    } finally {
      setLoading(prev => ({ ...prev, rentals: false }));
    }
  };

  const loadCurrentShift = async () => {
    try {
      const response = await api.get('/shifts/simple');
      
      if (response.success) {
        setCurrentShift(response.data);
      }
    } catch (error) {
      console.error('خطأ في تحميل الشيفت:', error);
    } finally {
      setLoading(prev => ({ ...prev, shift: false }));
    }
  };

  const loadCompletedRentals = async () => {
    if (!isManager) return;
    
    try {
      const today = new Date().toISOString().split('T')[0];
      const response = await api.get('/rentals', {
        params: { 
          branch_id: user.branch_id,
          status: 'مكتمل',
          start_date: today
        }
      });
      
      if (response.success) {
        setCompletedRentals(response.data);
        
        const todayRentals = response.data.filter(r => 
          r.end_time && r.end_time.split('T')[0] === today
        );
        
        const todayRevenue = todayRentals.reduce((sum, rental) => 
          sum + (parseFloat(rental.total_amount) || 0), 0
        );
        
        setStats(prev => ({
          ...prev,
          todayRevenue: todayRevenue,
          todayRentals: todayRentals.length
        }));
      }
    } catch (error) {
      console.error('خطأ في تحميل التأجيرات المكتملة:', error);
    } finally {
      setLoading(prev => ({ ...prev, stats: false }));
    }
  };

  const loadAllData = useCallback(async () => {
    setLoading({ games: true, rentals: true, shift: true, stats: true });
    
    try {
      await Promise.all([
        loadGames(),
        loadActiveRentals(),
        loadCurrentShift(),
        loadCompletedRentals()
      ]);
    } catch (error) {
      console.error('خطأ في تحميل البيانات:', error);
    }
  }, [user]);

  useEffect(() => {
    if (user?.branch_id) {
      loadAllData();
    }
  }, [user?.branch_id]);


  const calculateItemPrice = useCallback((item) => {
  if (item.rentalType === 'open') return 0;
  const units = Math.ceil(item.duration / 15);
  return (item.pricePer15min * units * item.quantity) || 0;
}, []);
  // ==================== معالجة التأجيرات ====================
  const handleSelectGame = (game) => {
    if (!currentShift) {
      alert('يجب بدء شيفت أولاً');
      return;
    }
    
    if (!selectedGames.some(g => g.id === game.id)) {
      setSelectedGames([...selectedGames, game]);
    }
    setShowRentalModal(true);
  };

const handleCreateRental = async (rentalData) => {
  try {
    console.log('🎯 بدء إنشاء تأجير متعدد:', rentalData.rentalItems.length);
    
    const results = [];
    const failedGames = [];
    
    // إنشاء تأجير منفصل لكل لعبة
    for (const item of rentalData.rentalItems) {
      try {
        // البحث عن اللعبة في قائمة الألعاب
        const game = games.find(g => g.id === item.gameId);
        
        if (!game) {
          console.warn(`⚠️ اللعبة ${item.gameId} غير موجودة`);
          failedGames.push(item.gameName);
          continue;
        }
        
        // إعداد بيانات التأجير
        const rentalRequest = {
          game_id: item.gameId,
          customer_name: rentalData.customerInfo.name,
          customer_phone: rentalData.customerInfo.phone,
          customer_email: rentalData.customerInfo.email,
          customer_address: rentalData.customerInfo.address,
          duration_minutes: item.rentalType === 'fixed' ? item.duration : null,
          rental_type: item.rentalType,
          quantity: item.quantity,
          notes: item.notes || '',
          payment_method: rentalData.paymentMethod,
          payment_status: rentalData.paymentStatus,
          total_amount: calculateItemPrice(item),
          is_open_time: item.rentalType === 'open' ? 1 : 0
        };
        
        // استخدام API لإرسال التأجير
        const response = await api.createRental(rentalRequest);
        
        if (response && response.success) {
          results.push({
            game: item.gameName,
            success: true,
            rental_number: response.data?.rental_number || response.rental_number,
            rental_id: response.data?.id || response.rental_id
          });
          
          console.log(`✅ تم تأجير ${item.gameName}: ${response.rental_number || response.data?.rental_number}`);
        } else {
          failedGames.push(item.gameName);
          console.warn(`❌ فشل تأجير ${item.gameName}: ${response.message}`);
        }
      } catch (error) {
        console.error(`🔥 خطأ في تأجير ${item.gameName}:`, error);
        failedGames.push(item.gameName);
      }
    }
    
    // عرض رسالة النتيجة
    const successCount = results.length;
    const totalCount = rentalData.rentalItems.length;
    
    if (successCount === totalCount) {
      alert(`✅ تم إنشاء ${successCount} تأجير بنجاح`);
    } else if (successCount > 0) {
      alert(`⚠️ تم إنشاء ${successCount} تأجير بنجاح من أصل ${totalCount}\n\nفشل تأجير: ${failedGames.join(', ')}`);
    } else {
      alert(`❌ فشل إنشاء جميع التأجيرات`);
    }
    
    // إعادة تحميل البيانات
    setShowRentalModal(false);
    setSelectedGames([]);
    
    // إعادة تحميل البيانات بعد تأخير قصير
    setTimeout(() => {
      loadGames();
      loadActiveRentals();
      loadCompletedRentals();
    }, 1000);
    
  } catch (error) {
    console.error('🔥 خطأ عام في إنشاء التأجير:', error);
    alert('حدث خطأ في إنشاء التأجير. الرجاء المحاولة مرة أخرى');
  }
};

// إضافة دالة handleEndRental
const handleEndRental = async (rental) => {
  try {
    if (!rental || !rental.id) {
      alert('بيانات التأجير غير صالحة');
      return;
    }
    
    const isOpenTime = rental.is_open_time || rental.rental_type === 'open';
    
    if (isOpenTime) {
      // عرض نافذة تأكيد مع حساب السعر التقديري
      const startTime = new Date(rental.start_time);
      const now = new Date();
      const minutes = Math.floor((now - startTime) / 60000);
      const units = Math.ceil(minutes / 15);
      const pricePer15Min = rental.price_per_15min || 100;
      const estimatedAmount = units * pricePer15Min;
      
      if (!window.confirm(`هل تريد إنهاء التأجير المفتوح؟\n\nالمدة: ${minutes} دقيقة\nالسعر التقديري: ${formatCurrency(estimatedAmount)}`)) {
        return;
      }
    } else {
      if (!window.confirm('هل تريد إنهاء هذا التأجير؟')) {
        return;
      }
    }
    
    const response = await api.completeRental(rental.id, {
      payment_method: 'كاش'
    });
    
    if (response.success) {
      alert('✅ تم إنهاء التأجير بنجاح');
      loadActiveRentals();
      loadGames();
      loadCompletedRentals();
    } else {
      alert(`❌ فشل إنهاء التأجير: ${response.message}`);
    }
  } catch (error) {
    console.error('🔥 خطأ في إنهاء التأجير:', error);
    alert('حدث خطأ في إنهاء التأجير');
  }
};

// تحديث handleRentalAction ليبدو هكذا:
const handleRentalAction = async (action, rental) => {
  switch(action) {
    case 'details': {
      // عرض تفاصيل التأجير في نافذة منبثقة
      alert(`📋 تفاصيل التأجير:
رقم التأجير: ${rental.rental_number || 'غير متوفر'}
العميل: ${rental.customer_name}
الهاتف: ${rental.customer_phone}
اللعبة: ${rental.game_name}
وقت البدء: ${formatDateTime(rental.start_time)}
الحالة: ${rental.status}
المبلغ: ${formatCurrency(rental.total_amount || 0)}
حالة الدفع: ${rental.payment_status}`);
      break;
    }
      
    case 'end': {
      // استخدام الدالة المحسنة
      await handleEndRental(rental);
      break;
    }
      
    case 'cancel': {
      if (window.confirm('هل تريد إلغاء هذا التأجير؟ سيتم إرجاع اللعبة للمخزون.')) {
        try {
          const response = await api.cancelRental(rental.id, 0, 'تم الإلغاء من قبل المستخدم');
          if (response.success) {
            alert('✅ تم إلغاء التأجير');
            loadActiveRentals();
            loadGames();
          } else {
            alert(`❌ فشل الإلغاء: ${response.message}`);
          }
        } catch (error) {
          alert('🔥 حدث خطأ في إلغاء التأجير');
        }
      }
      break;
    }
      
    case 'extend': {
      const additionalMinutes = prompt('أددد عدد الدقائق الإضافية:', '15');
      if (additionalMinutes && !isNaN(additionalMinutes)) {
        try {
          const response = await api.extendRental(rental.id, parseInt(additionalMinutes));
          if (response.success) {
            alert(`✅ تم تمديد التأجير ${additionalMinutes} دقيقة`);
            loadActiveRentals();
          }
        } catch (error) {
          alert('❌ فشل تمديد التأجير');
        }
      }
      break;
    }
    
    default:
      console.warn(`⚠️ إجراء غير معروف: ${action}`);
  }
};

  const startShift = async () => {
    try {
      const response = await api.post('/shifts/start');
      if (response.success) {
        setCurrentShift(response.data);
        alert('تم بدء الشيفت');
        loadAllData();
      }
    } catch (error) {
      alert('فشل بدء الشيفت');
    }
  };

  const endShift = async () => {
    if (!currentShift) return;
    
    if (window.confirm('هل تريد إنهاء الشيفت الحالي؟')) {
      try {
        const response = await api.put(`/shifts/${currentShift.id}/end`);
        if (response.success) {
          setCurrentShift(null);
          setActiveRentals([]);
          alert('تم إنهاء الشيفت');
          loadAllData();
        }
      } catch (error) {
        alert('فشل إنهاء الشيفت');
      }
    }
  };

  const handleExportCompleted = (rentals) => {
    try {
      const dataStr = JSON.stringify(rentals, null, 2);
      const dataUri = 'data:application/json;charset=utf-8,'+ encodeURIComponent(dataStr);
      
      const link = document.createElement('a');
      link.setAttribute('href', dataUri);
      link.setAttribute('download', `تأجيرات_${new Date().toISOString().split('T')[0]}.json`);
      link.click();
      
      alert(`تم تصدير ${rentals.length} تأجير`);
    } catch (error) {
      alert('فشل تصدير البيانات');
    }
  };

  const handlePrintCompleted = (rentals) => {
    const printWindow = window.open('', '_blank');
    const content = `
      <html>
        <head>
          <title>تقرير التأجيرات المكتملة</title>
          <style>
            body { font-family: Arial; direction: rtl; padding: 20px; }
            h1 { text-align: center; }
            table { width: 100%; border-collapse: collapse; margin-top: 20px; }
            th, td { border: 1px solid #ddd; padding: 8px; text-align: center; }
            th { background-color: #f2f2f2; }
          </style>
        </head>
        <body>
          <h1>تقرير التأجيرات المكتملة</h1>
          <table>
            <thead>
              <tr>
                <th>رقم التأجير</th>
                <th>العميل</th>
                <th>اللعبة</th>
                <th>المبلغ</th>
                <th>وقت الانتهاء</th>
              </tr>
            </thead>
            <tbody>
              ${rentals.map(rental => `
                <tr>
                  <td>${rental.rental_number || rental.id}</td>
                  <td>${rental.customer_name}</td>
                  <td>${rental.game_name}</td>
                  <td>${formatCurrency(rental.total_amount)}</td>
                  <td>${formatDateTime(rental.end_time)}</td>
                </tr>
              `).join('')}
            </tbody>
          </table>
          <script>window.print();</script>
        </body>
      </html>
    `;
    
    printWindow.document.write(content);
    printWindow.document.close();
  };

  // ==================== التصيير ====================
  if (loading.games && loading.rentals) {
    return (
      <div className="loading-container">
        <Loader2 className="spinner" size={48} />
        <p>جاري تحميل النظام...</p>
      </div>
    );
  }

  return (
    <div className="rentals-page">
      {/* الرأس */}
      <header className="page-header">
        <div className="header-content">
          <h1>
            <Gamepad2 size={32} />
            نظام إدارة التأجيرات
          </h1>
          <div className="user-info">
            <User size={20} />
            <span>{user?.name} - {isManager ? 'مدير' : 'موظف'}</span>
            <Building size={20} />
            <span>{user?.branch_name}</span>
          </div>
        </div>
        
        <div className="header-actions">
          {selectedGames.length > 0 && (
            <button 
              onClick={() => setShowRentalModal(true)}
              className="btn btn-primary"
            >
              <ShoppingCart size={20} />
              بدء تأجير ({selectedGames.length})
            </button>
          )}
          
          <button 
            onClick={loadAllData}
            className="btn btn-secondary"
          >
            <RefreshCw size={20} />
            تحديث
          </button>
        </div>
      </header>
      
      {/* لوحة التحكم */}
      <DashboardStats 
        stats={stats}
        currentShift={currentShift}
        onStartShift={startShift}
        onEndShift={endShift}
        loading={loading.shift}
      />
      
      {/* الرسالة عند عدم وجود شيفت */}
      {!currentShift && (
        <div className="no-shift-alert">
          <AlertCircle size={24} />
          <div>
            <h3>لا يوجد شيفت نشط</h3>
            <p>يجب بدء شيفت جديد للتمكن من إدارة التأجيرات</p>
          </div>
          <button onClick={startShift} className="btn btn-success">
            بدء شيفت جديد
          </button>
        </div>
      )}
      
      {/* المحتوى الرئيسي */}
      {currentShift && (
        <div className="main-content">
          {/* الألعاب المتاحة */}
          <GamesGrid 
            games={games}
            branchId={user?.branch_id}
            onSelectGame={handleSelectGame}
            loading={loading.games}
          />
          
          {/* التأجيرات النشطة */}
          <ActiveRentalsTable 
            rentals={activeRentals}
            onAction={handleRentalAction}
            loading={loading.rentals}
          />
          
          {/* التأجيرات المكتملة (للمدير فقط) */}
          <CompletedRentalsTable 
            rentals={completedRentals}
            onExport={handleExportCompleted}
            onPrint={handlePrintCompleted}
            loading={loading.stats}
            isManager={isManager}
          />
        </div>
      )}
      
      
      {/* نافذة التأجير المتعدد */}
      <MultiRentalModal 
        show={showRentalModal}
        onClose={() => {
          setShowRentalModal(false);
          setSelectedGames([]);
        }}
        selectedGames={selectedGames}
        currentShift={currentShift}
        onConfirmRental={handleCreateRental}
      />
    </div>

    
  );
};

export default Rentals;
