import React, { useState } from 'react';
import { Link, useLocation, useNavigate } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import './Layout.css';

const Layout = ({ children }) => {
  const { user, logout } = useAuth();
  const location = useLocation();
  const navigate = useNavigate();
  const [sidebarOpen, setSidebarOpen] = useState(true);
  const [notifications] = useState([
    { id: 1, message: 'ÿ™ÿ£ÿ¨Ÿäÿ± ÿ¨ÿØŸäÿØ #1234', time: 'ŸÖŸÜÿ∞ 5 ÿØŸÇÿßÿ¶ŸÇ' },
    { id: 2, message: 'ŸÑÿπÿ®ÿ© ŸÖÿ™ÿßÿ≠ÿ© ŸÑŸÑÿßÿ≥ÿ™ÿ±ÿ¨ÿßÿπ', time: 'ŸÖŸÜÿ∞ ÿ≥ÿßÿπÿ©' },
    { id: 3, message: 'ÿ™ŸÇÿ±Ÿäÿ± ÿßŸÑŸÖÿ®Ÿäÿπÿßÿ™ ÿ¨ÿßŸáÿ≤', time: 'ŸÖŸÜÿ∞ ŸäŸàŸÖ' },
  ]);

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const isActive = (path) => {
    return location.pathname.startsWith(path) ? 'active' : '';
  };

  const getRoleText = (role) => {
    switch(role) {
      case 'admin': return 'üëë ÿßŸÑŸÖÿØŸäÿ± ÿßŸÑÿπÿßŸÖ';
      case 'branch_manager': return 'üè¢ ŸÖÿØŸäÿ± ŸÅÿ±ÿπ';
      case 'employee': return 'üë§ ŸÖŸàÿ∏ŸÅ';
      default: return 'ŸÖÿ≥ÿ™ÿÆÿØŸÖ';
    }
  };

  return (
    <div className="layout">
      {/* Sidebar */}
      <aside className={`sidebar ${sidebarOpen ? 'open' : 'closed'}`}>
        <div className="sidebar-header">
          <h2 className="logo">L3BTY STORE</h2>
          <button 
            className="toggle-sidebar"
            onClick={() => setSidebarOpen(!sidebarOpen)}
          >
            {sidebarOpen ? '‚óÄ' : '‚ñ∂'}
          </button>
        </div>
        
        {/* User Info */}
        <div className="user-info">
          <div className="user-avatar">
            {user?.name?.charAt(0) || 'U'}
          </div>
          <div className="user-details">
            <h3>{user?.name}</h3>
            <p className="user-role">{getRoleText(user?.role)}</p>
          </div>
        </div>
        
        {/* Navigation */}
        <nav className="sidebar-nav">
          {user?.role === 'admin' && (
            <div className="nav-section">
              <h4 className="section-title">üëë ÿßŸÑÿ•ÿØÿßÿ±ÿ© ÿßŸÑÿπÿßŸÖÿ©</h4>
              <ul>
                <li className={isActive('/admin') && !isActive('/admin/') ? 'active' : ''}>
                  <Link to="/admin">
                    <span className="nav-icon">üìä</span>
                    <span className="nav-text">ŸÑŸàÿ≠ÿ© ÿßŸÑÿ™ÿ≠ŸÉŸÖ</span>
                  </Link>
                </li>
                <li className={isActive('/admin/users')}>
