import React, { useState, useEffect, useCallback, useMemo, useRef } from 'react';
import { 
  Gamepad2, Clock, User, Phone, ShoppingCart, DollarSign, 
  CheckCircle, XCircle, Eye, Plus, Minus, Trash2, Calendar,
  Printer, Download, RefreshCw, AlertCircle, Check, X,
  Building, Zap, Lock, Unlock, Loader2, Filter, Search,
  ChevronRight, MapPin, Mail, Users, Package, RotateCcw, Calculator 
} from 'lucide-react';
import { useAuth } from '../../context/AuthContext';
import api from '../../services/api';
import './Rentals.css';

const formatCurrency = (amount) => {
  if (amount === null || amount === undefined || amount === '') return '0 ÿ¨.ŸÖ';
  const num = parseFloat(amount) || 0;
  return new Intl.NumberFormat('ar-EG', {
    style: 'currency',
    currency: 'EGP',
    minimumFractionDigits: 0,
    maximumFractionDigits: 2
  }).format(num);
};

const formatDateTime = (dateString) => {
  if (!dateString) return '--/--/---- --:--';
  try {
    const date = new Date(dateString);
    if (isNaN(date.getTime())) return '--/--/---- --:--';
    return date.toLocaleDateString('ar-EG', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--/--/---- --:--';
  }
};

const formatTime = (dateString) => {
  if (!dateString) return '--:--';
  try {
    const date = new Date(dateString);
    if (isNaN(date.getTime())) return '--:--';
    return date.toLocaleTimeString('ar-EG', {
      hour: '2-digit',
      minute: '2-digit'
    });
  } catch {
    return '--:--';
  }
};

const validatePhone = (phone) => {
  if (!phone || phone.trim() === '') return true;
  const cleaned = phone.replace(/\D/g, '');
  return cleaned.length === 11 && cleaned.startsWith('01');
};

const calculateDuration = (startTime, endTime) => {
  if (!startTime || !endTime) return 0;
  const start = new Date(startTime);
  const end = new Date(endTime);
  if (isNaN(start.getTime()) || isNaN(end.getTime())) return 0;
  return Math.max(0, Math.round((end - start) / (1000 * 60)));
};

// ÿ£ŸÜÿ¥ÿ¶ ŸÉÿßÿ¶ŸÜ cache
const apiCache = {
  rentals: {},
  games: {},
  shift: {},
  
  set: function(key, data, expiry = 30000) { // 30 ÿ´ÿßŸÜŸäÿ© ÿßŸÅÿ™ÿ±ÿßÿ∂ŸäÿßŸã
    this[key] = {
      data,
      expiry: Date.now() + expiry,
      timestamp: Date.now()
    };
  },
  
  get: function(key) {
    const item = this[key];
    if (!item) return null;
    
    if (Date.now() > item.expiry) {
      delete this[key];
      return null;
    }
    
    return item.data;
  },
  
  clear: function(key) {
    if (key) {
      delete this[key];
    } else {
      this.rentals = {};
      this.games = {};
      this.shift = {};
    }
  }
};

const Rentals = () => {
  const { user } = useAuth();
  const isManager = user?.role === 'admin' || user?.role === 'branch_manager';
  
  const [loading, setLoading] = useState({
    games: false,
    rentals: false,
    shift: false,
    stats: false,
    processing: false
  });
  
  const [games, setGames] = useState([]);
  const [allGames, setAllGames] = useState([]);
  const [activeRentals, setActiveRentals] = useState([]);
  const [completedRentals, setCompletedRentals] = useState([]);
  const [currentShift, setCurrentShift] = useState(null);
  const [selectedGames, setSelectedGames] = useState([]);
  const [showRentalModal, setShowRentalModal] = useState(false);
  const [showChangeGameModal, setShowChangeGameModal] = useState(false);
  const [selectedRentalForChange, setSelectedRentalForChange] = useState(null);
  const [showRerentalModal, setShowRerentalModal] = useState(false);
  const [selectedCompletedRental, setSelectedCompletedRental] = useState(null);
  const [error, setError] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [filterCategory, setFilterCategory] = useState('all');

  const [refreshInterval, setRefreshInterval] = useState(null);
const lastRefreshRef = useRef(Date.now());

  const [stats, setStats] = useState({
    availableGames: 0,
    totalGames: 0,
    activeRentals: 0,
    todayRevenue: 0,
    todayRentals: 0
  });

  const processingRef = useRef(false);
  const dataLoadTimeoutRef = useRef(null);
  const shiftCheckIntervalRef = useRef(null);

    // ‚úÖ 7. ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ® - ŸÖÿ≠ÿ≥ŸÜ
  const loadGames = useCallback(async () => {
    if (!user?.branch_id) {
      setGames([]);
      setAllGames([]);
      return;
    }
    
    try {
      setLoading(prev => ({ ...prev, games: true }));
      
      const response = await api.loadGamesSmart(user.branch_id);
        
      if (response.success && response.data) {
        setGames(response.data);
        setAllGames(response.data);
        
        const availableCount = response.data.filter(game => 
          game.status === 'ŸÖÿ™ÿßÿ≠'
        ).length;
        
        setStats(prev => ({
          ...prev,
          availableGames: availableCount,
          totalGames: response.data.length
        }));
      } else {
        setGames([]);
        setAllGames([]);
      }
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ£ŸÑÿπÿßÿ®:', error);
      setGames([]);
      setAllGames([]);
    } finally {
      setLoading(prev => ({ ...prev, games: false }));
    }
  }, [user?.branch_id]);

  // ‚úÖ 8. ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑÿ≠ÿßŸÑŸä - ŸÖÿ≠ÿ≥ŸÜ
  const loadCurrentShift = useCallback(async () => {
    try {
      setLoading(prev => ({ ...prev, shift: true }));
      
      const response = await api.getCurrentShift();
      
      if (response.success && response.data) {
        const shift = response.data;
        
        if (shift.employee_id === user?.id || shift.employee_name === user?.name) {
          setCurrentShift(shift);
          localStorage.setItem('current_shift', JSON.stringify(shift));
          return shift;
        } else {
          console.warn('‚ö†Ô∏è ÿßŸÑÿ¥ŸäŸÅÿ™ ÿßŸÑŸÜÿ¥ÿ∑ ŸÑÿß ŸäŸÜÿ™ŸÖŸä ŸÑŸÑŸÖÿ≥ÿ™ÿÆÿØŸÖ ÿßŸÑÿ≠ÿßŸÑŸä');
          setCurrentShift(null);
          localStorage.removeItem('current_shift');
          return null;
        }
      } else {
        setCurrentShift(null);
        localStorage.removeItem('current_shift');
        return null;
      }
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ¥ŸäŸÅÿ™:', error);
      setCurrentShift(null);
      localStorage.removeItem('current_shift');
      return null;
    } finally {
      setLoading(prev => ({ ...prev, shift: false }));
    }
  }, [user?.id, user?.name]);
  
  // ‚úÖ 1. ÿØÿßŸÑÿ© ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ•Ÿäÿ±ÿßÿØ ÿßŸÑÿ≠ŸÇŸäŸÇŸä - ŸÖÿ≠ÿ≥ŸÜÿ©
  const calculateRealRevenue = useCallback(async (shiftId, branchId) => {
    if (!shiftId || !branchId) return 0;
    
    try {
      const response = await api.get('/rentals', {
        params: {
          shift_id: shiftId,
          branch_id: branchId,
          limit: 1000
        }
      });
      
      if (response.success && response.data) {
        const rentals = Array.isArray(response.data) ? response.data : [];
        
        return rentals.reduce((total, rental) => {
          if (rental.status === 'ŸÖŸÉÿ™ŸÖŸÑ') {
            const rentalType = getRentalType(rental);
            if (rentalType === 'open') {
              return total + parseFloat(rental.final_amount || 0);
            } else {
              return total + parseFloat(rental.paid_amount || rental.total_amount || 0);
            }
          }
          return total;
        }, 0);
      }
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ•Ÿäÿ±ÿßÿØ ÿßŸÑÿ≠ŸÇŸäŸÇŸä:', error);
      setError('ÿÆÿ∑ÿ£ ŸÅŸä ÿ≠ÿ≥ÿßÿ® ÿßŸÑÿ•Ÿäÿ±ÿßÿØ');
    }
    
    return 0;
  }, []);

  // ‚úÖ 2. ÿ™ÿ≠ŸÖŸäŸÑ ÿ•Ÿäÿ±ÿßÿØ ÿßŸÑÿ¥ŸäŸÅÿ™ - ŸÖÿ≠ÿ≥ŸÜ
  const loadShiftRevenue = useCallback(async () => {
    if (!currentShift?.id || !user?.branch_id) return;
    
    try {
      setLoading(prev => ({ ...prev, stats: true }));
      const revenue = await calculateRealRevenue(currentShift.id, user.branch_id);
      
      setStats(prev => ({
        ...prev,
        todayRevenue: revenue
      }));
      
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿ•Ÿäÿ±ÿßÿØ ÿßŸÑÿ¥ŸäŸÅÿ™:', error);
      setError('ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ•Ÿäÿ±ÿßÿØ');
    } finally {
      setLoading(prev => ({ ...prev, stats: false }));
    }
  }, [currentShift?.id, user?.branch_id, calculateRealRevenue]);

  // ‚úÖ 3. ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•Ÿäÿ±ÿßÿØ - ŸÖÿ≠ÿ≥ŸÜ
  const updateRevenueOnRentalComplete = useCallback((rental, amount) => {
    if (!rental || !currentShift?.id) return;
    
    try {
      const rentalType = getRentalType(rental);
      
      if (rentalType === 'open') {
        setStats(prev => ({
          ...prev,
          todayRevenue: (prev.todayRevenue || 0) + amount
        }));
      }
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ•Ÿäÿ±ÿßÿØ:', error);
    }
  }, [currentShift?.id]);

  // ‚úÖ 4. ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ© - ŸÖÿ≠ÿ≥ŸÜ
  const loadCompletedRentals = useCallback(async () => {
    if (!isManager || !currentShift?.id || !user?.branch_id) return;
    
    try {
      const response = await api.get('/rentals', {
        params: {
          shift_id: currentShift.id,
          branch_id: user.branch_id,
          status: 'ŸÖŸÉÿ™ŸÖŸÑ',
          limit: 100
        }
      });
      
      if (response.success && response.data) {
        setCompletedRentals(response.data);
        
        const revenue = response.data.reduce((total, rental) => {
          if (rental.status === 'ŸÖŸÉÿ™ŸÖŸÑ') {
            const rentalType = getRentalType(rental);
            if (rentalType === 'open') {
              return total + parseFloat(rental.final_amount || 0);
            } else {
              return total + parseFloat(rental.paid_amount || rental.total_amount || 0);
            }
          }
          return total;
        }, 0);
        
        setStats(prev => ({
          ...prev,
          todayRevenue: revenue,
          todayRentals: response.data.length
        }));
      }
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿ¨ŸÑÿ® ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÉÿ™ŸÖŸÑÿ©:', error);
      setCompletedRentals([]);
    }
  }, [currentShift?.id, isManager, user?.branch_id]);

const loadActiveRentalsForCurrentShift = useCallback(async () => {
  if (!user?.branch_id || !currentShift?.id) {
    setActiveRentals([]);
    return;
  }
  
  // ŸÖŸÜÿπ ÿßŸÑÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑŸÖÿ™ŸÉÿ±ÿ±
  const cacheKey = `rentals_${currentShift.id}_${Date.now()}`;
  const cachedData = sessionStorage.getItem(cacheKey);
  
  if (cachedData && !force) {
    const parsed = JSON.parse(cachedData);
    if (Date.now() - parsed.timestamp < 10000) { // 10 ÿ´ŸàÿßŸÜŸä ŸÉÿßÿ¥
      setActiveRentals(parsed.data);
      return;
    }
  }
  
  try {
    setLoading(prev => ({ ...prev, rentals: true }));
    
    // **ÿßÿ≥ÿ™ÿÆÿØÿßŸÖ ÿßŸÑŸÜŸÇÿ∑ÿ© ÿßŸÑÿµÿ≠Ÿäÿ≠ÿ© ŸÅŸÇÿ∑**
    const response = await api.get('/rentals', {
      params: {
        branch_id: user.branch_id,
        status: 'ŸÜÿ¥ÿ∑',
        limit: 100
      }
    });
    
    if (response.success && response.data) {
      // ŸÅŸÑÿ™ÿ±ÿ© ŸÖÿ≠ŸÑŸäÿßŸã ÿ≠ÿ≥ÿ® shift_id
      const shiftRentals = response.data.filter(rental => 
        rental.shift_id == currentShift.id
      );
      
      setActiveRentals(shiftRentals);
      setStats(prev => ({
        ...prev,
        activeRentals: shiftRentals.length
      }));
      
      // ÿ≠ŸÅÿ∏ ŸÅŸä ÿßŸÑŸÉÿßÿ¥
      sessionStorage.setItem(cacheKey, JSON.stringify({
        data: shiftRentals,
        timestamp: Date.now()
      }));
    } else {
      setActiveRentals([]);
    }
    
  } catch (error) {
    console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÜÿ¥ÿ∑ÿ©:', error);
    setActiveRentals([]);
  } finally {
    setLoading(prev => ({ ...prev, rentals: false }));
  }
}, [currentShift?.id, user?.branch_id]);

  // ‚úÖ 6. ÿ™ÿ≠ÿØŸäÿ´ ÿ¥ÿßŸÖŸÑ ŸÑŸÑÿ®ŸäÿßŸÜÿßÿ™ - ŸÖÿ≠ÿ≥ŸÜ
  const refreshAllData = useCallback(async (force = false) => {
    if (processingRef.current && !force) return;
    
    processingRef.current = true;
    console.log('üîÑ ÿ™ÿ≠ÿØŸäÿ´ ÿ¥ÿßŸÖŸÑ ŸÑŸÑÿ®ŸäÿßŸÜÿßÿ™...');
    
    try {
      setLoading({ 
        games: true, 
        rentals: true, 
        shift: true, 
        stats: true,
        processing: false 
      });
      
      await loadGames();
      const shift = await loadCurrentShift();
      
      if (shift && shift.id) {
        await Promise.all([
          loadActiveRentalsForCurrentShift(),
          loadShiftRevenue(),
          isManager ? loadCompletedRentals() : Promise.resolve()
        ]);
      }
      
      setError(null);
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:', error);
      setError('ŸÅÿ¥ŸÑ ÿ™ÿ≠ÿØŸäÿ´ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™');
    } finally {
      setTimeout(() => {
        setLoading({ 
          games: false, 
          rentals: false, 
          shift: false, 
          stats: false,
          processing: false 
        });
        processingRef.current = false;
      }, 500);
    }
  }, [loadGames, loadCurrentShift, loadActiveRentalsForCurrentShift, loadShiftRevenue, loadCompletedRentals, isManager]);



  // ‚úÖ 9. ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ±ÿßÿ™ ÿßŸÑŸÖŸÜÿ™ŸáŸäÿ© - ŸÖÿ≠ÿ≥ŸÜ
  const processCompletedRentalsManual = useCallback(async () => {
    if (processingRef.current || loading.processing) return;
    
    try {
      processingRef.current = true;
      setLoading(prev => ({ ...prev, processing: true }));
      
      const now = new Date();
      const expiredRentals = activeRentals.filter(rental => {
        if (!rental.start_time) return false;
        
        const rentalType = getRentalType(rental);
        const startTime = new Date(rental.start_time);
        
        if (rentalType === 'fixed') {
          const duration = rental.duration_minutes || 15;
          const endTime = new Date(startTime.getTime() + duration * 60000);
          return now > endTime;
        }
        
        return false;
      });
      
      if (expiredRentals.length === 0) {
        return { success: true, processed: 0 };
      }
      
      console.log(`üîß ŸÖÿπÿßŸÑÿ¨ÿ© ${expiredRentals.length} ÿ™ÿ£ÿ¨Ÿäÿ± ŸÖŸÜÿ™ŸáŸä`);
      
      let processedCount = 0;
      
      for (const rental of expiredRentals) {
        try {
          const rentalType = getRentalType(rental);
          
          if (rentalType === 'open') {
            await api.completeRental(rental.id, {
              payment_method: 'ŸÉÿßÿ¥'
            });
          } else {
            await api.completeRental(rental.id, {
              payment_method: 'ŸÉÿßÿ¥',
              final_amount: rental.paid_amount || rental.total_amount
            });
          }
          
          processedCount++;
          await new Promise(resolve => setTimeout(resolve, 200));
        } catch (error) {
          console.warn(`‚ö†Ô∏è ŸÅÿ¥ŸÑ ŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± ${rental.id}:`, error.message);
        }
      }
      
      if (processedCount > 0) {
        setTimeout(() => {
          refreshAllData();
        }, 1000);
      }
      
      return { 
        success: true, 
        processed: processedCount 
      };
      
    } catch (error) {
      console.error('üî• ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑŸäÿØŸàŸäÿ©:', error);
      return {
        success: false,
        message: error.message
      };
    } finally {
      processingRef.current = false;
      setLoading(prev => ({ ...prev, processing: false }));
    }
  }, [activeRentals, refreshAllData]);

  // ‚úÖ 10. ÿ™ÿ≠ÿØŸäÿØ ŸÜŸàÿπ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± - ŸÖÿ≠ÿ≥ŸÜ
  const getRentalType = useCallback((rental) => {
    if (!rental) return 'fixed';
    
    if (rental.rental_type === 'open' || rental.is_open_time === 1) {
      return 'open';
    }
    
    if (rental.rental_type === 'fixed' || rental.is_open_time === 0) {
      return 'fixed';
    }
    
    if (rental.payment_status === 'ÿπŸÜÿØ ÿßŸÑÿ•ŸÜŸáÿßÿ°' || rental.payment_status === 'pending') {
      return 'open';
    }
    
    if (rental.payment_status === 'ŸÖÿØŸÅŸàÿπ ŸÖÿ≥ÿ®ŸÇÿßŸã' || rental.payment_status === 'paid') {
      return 'fixed';
    }
    
    return 'fixed';
  }, []);

  // ‚úÖ 11. ÿ™ÿ≠ŸÖŸäŸÑ ÿ¨ŸÖŸäÿπ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ - ŸÖÿ≠ÿ≥ŸÜ
  useEffect(() => {
    if (!user?.branch_id) return;
    
    const loadData = async () => {
      try {
        await refreshAllData(true);
      } catch (error) {
        console.error('üî• ÿÆÿ∑ÿ£ ÿ±ÿ¶Ÿäÿ≥Ÿä ŸÅŸä ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™:', error);
        setError('ŸÅÿ¥ŸÑ ÿ™ÿ≠ŸÖŸäŸÑ ÿßŸÑÿ®ŸäÿßŸÜÿßÿ™ ÿßŸÑÿ£ŸàŸÑŸäÿ©');
      }
    };
    
    loadData();
    
    return () => {
      if (dataLoadTimeoutRef.current) {
        clearTimeout(dataLoadTimeoutRef.current);
      }
      if (shiftCheckIntervalRef.current) {
        clearInterval(shiftCheckIntervalRef.current);
      }
    };
  }, [user?.branch_id, refreshAllData]);

  // ‚úÖ 12. ÿßŸÑŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿØŸàÿ±Ÿäÿ© - ŸÖÿ≠ÿ≥ŸÜ
  useEffect(() => {
    if (!currentShift?.id) return;
    
    const processAndUpdate = async () => {
      try {
        await processCompletedRentalsManual();
        await loadShiftRevenue();
      } catch (error) {
        console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑŸÖÿπÿßŸÑÿ¨ÿ© ÿßŸÑÿØŸàÿ±Ÿäÿ©:', error);
      }
    };
    
    processAndUpdate();
    
    const interval = setInterval(processAndUpdate, 60000);
    
    return () => clearInterval(interval);
  }, [currentShift, processCompletedRentalsManual, loadShiftRevenue]);



useEffect(() => {
  if (!currentShift?.id) return;
  
  // ÿ™ŸÜÿ∏ŸäŸÅ ÿ£Ÿä interval ÿ≥ÿßÿ®ŸÇ
  if (refreshInterval) {
    clearInterval(refreshInterval);
  }
  
  // ÿ•ŸÜÿ¥ÿßÿ° interval ÿ¨ÿØŸäÿØ ŸÉŸÑ 30 ÿ´ÿßŸÜŸäÿ© ŸÅŸÇÿ∑
  const interval = setInterval(() => {
    const now = Date.now();
    // ÿ™ÿ£ŸÉÿØ ŸÖŸÜ ŸÖÿ±Ÿàÿ± 30 ÿ´ÿßŸÜŸäÿ© ÿπŸÑŸâ ÿßŸÑÿ£ŸÇŸÑ ŸÖŸÜÿ∞ ÿ¢ÿÆÿ± ÿ™ÿ≠ÿØŸäÿ´
    if (now - lastRefreshRef.current > 30000 && !processingRef.current) {
      lastRefreshRef.current = now;
      refreshAllData();
    }
  }, 30000);
  
  setRefreshInterval(interval);
  
  return () => {
    if (interval) clearInterval(interval);
  };
}, [currentShift?.id]);

useEffect(() => {
  const handleVisibilityChange = () => {
    if (!document.hidden && currentShift?.id) {
      // ÿ™ÿ≠ÿØŸäÿ´ ÿπŸÜÿØ ÿßŸÑÿπŸàÿØÿ© ŸÑŸÑÿµŸÅÿ≠ÿ© ŸÅŸÇÿ∑
      setTimeout(() => refreshAllData(), 1000);
    }
  };
  
  document.addEventListener('visibilitychange', handleVisibilityChange);
  
  return () => {
    document.removeEventListener('visibilitychange', handleVisibilityChange);
  };
}, [currentShift?.id]);

  // ‚úÖ 14. ÿØÿßŸÑÿ© Debounce ŸÖÿ≠ÿ≥ŸÜÿ©
  const useDebounce = (callback, delay) => {
    const timeoutRef = useRef(null);
    
    return useCallback((...args) => {
      if (timeoutRef.current) {
        clearTimeout(timeoutRef.current);
      }
      
      timeoutRef.current = setTimeout(() => {
        callback(...args);
      }, delay);
    }, [callback, delay]);
  };

  const debouncedRefresh = useDebounce(refreshAllData, 1000);

  // ‚úÖ 15. ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑŸÖÿµÿßÿØŸÇÿ© - ŸÖÿ≠ÿ≥ŸÜ
  const checkAndRefreshAuth = useCallback(async () => {
    try {
      const token = localStorage.getItem('token');
      const userData = localStorage.getItem('user');
      
      if (!token || !userData) {
        window.location.href = '/login';
        return false;
      }
      
      const response = await api.get('/auth/profile');
      
      if (!response.success) {
        if (response.status === 403 || response.status === 401) {
          const user = JSON.parse(userData);
          const loginResult = await api.login(user.email, '123456');
          
          if (loginResult.success) {
            return true;
          } else {
            localStorage.clear();
            window.location.href = '/login';
            return false;
          }
        }
      }
      
      return true;
    } catch (error) {
      console.error('‚ùå ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑŸÖÿµÿßÿØŸÇÿ©:', error);
      return false;
    }
  }, []);

  // ‚úÖ 16. ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ¥ŸäŸÅÿ™ ŸÇÿ®ŸÑ ÿßŸÑÿ™ÿ£ÿ¨Ÿäÿ± - ŸÖÿ≠ÿ≥ŸÜ
  const validateShiftBeforeRental = useCallback(async () => {
    try {
      if (!currentShift) {
        const shiftResponse = await api.get('/shifts/simple');
        
        if (shiftResponse.success && shiftResponse.data) {
