      const confirmed = window.confirm(
        'ÿ≠ÿØÿ´ ÿÆÿ∑ÿ£ ŸÅŸä ÿßŸÑÿ™ÿ≠ŸÇŸÇ ŸÖŸÜ ÿßŸÑÿ¥ŸäŸÅÿ™. ŸáŸÑ ÿ™ÿ±ŸäÿØ ÿ®ÿØÿ° ÿ¥ŸäŸÅÿ™ ÿ¨ÿØŸäÿØÿü'
      );
      
      if (confirmed) {
        const shiftStarted = await handleStartShift();
        return shiftStarted;
      }
      
      return false;
    }
  }, [currentShift, user?.id, user?.name]);

  // ‚úÖ 17. ÿßÿÆÿ™Ÿäÿßÿ± ŸÑÿπÿ®ÿ© - ŸÖÿ≠ÿ≥ŸÜ
  const handleSelectGame = useCallback(async (game) => {
