        const paymentStatus = item.rentalType === 'open' ? 'ÿπŸÜÿØ ÿßŸÑÿ•ŸÜŸáÿßÿ°' : 'ŸÖÿØŸÅŸàÿπ ŸÖÿ≥ÿ®ŸÇÿßŸã';
        const rentalNumber = response.rental_number || response.data?.rental_number;
        const totalAmount = calculateItemPrice(item);
        
